INSERT INTO public.after VALUES ('b0gb4cKRpT', '2002-07-27', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', true, false, 0, 160, 2, 2, 2, 4, 4, '渋谷', 10, '女装、星占い、勉強', 'いつマの閉店時間が、土日に限り午前2時に延長された。しかし、10時半以降は、深夜料金で30%増し。
