INSERT INTO public.after VALUES ('Ai2EgbP1lG', '2003-11-26', '水', '水曜練', 1, 'ジャンジャン', '焼肉', false, false, 1500, 1500, 5, 3, 3, 6, 6, '渋谷', 4, '田川、ＢＧＭ', '田川は食べ放題に興奮しすぎて、壊れていた。謎のＢＧＭが流れていて、白崎がＣＤなら購入したいといっていた。');
INSERT INTO public.after VALUES ('r2tx26TN6Q', '2003-11-29', '土', '土曜練', 1, 'Shakey''s', 'ピザ', true, false, 800, 900, 4, 4, 3, 8, 8, '渋谷', 4, '起床時間、サークルの歴史', '４年生はかなり不規則な生活をしているようだ。キャプテン・副キャプテンを初代まで遡ろうとして、４代上ぐらいまで遡って、挫折。');
INSERT INTO public.after VALUES ('og7wc2ItqO', '2003-12-03', '水', '水曜練', 1, 'おひつ家', '和食', true, false, 600, 900, 3, 3, 3, 6, 6, '渋谷', 8, '一人暮らし', '金子は寮で二人暮らしの為、一人暮らしに憧れを抱いている。濱崎は同居人がいることをうらやんでいた。');
INSERT INTO public.after VALUES ('NQXFk3G0VS', '2003-12-10', '水', '水曜練', 1, 'カプリチョーザ', '洋食', true, false, 800, 900, 2, 4, 4, 8, 8, '渋谷', 5, '就職、バイト', '就職の時期・バイトの面接など、シリアスな話をした。田川がこないだのバイトの面接を無愛想に受けてみたと話すと、落合はそれは心配だと言った。結論として、この店はなかなか使えるのでまた来ようと思った。');
INSERT INTO public.after VALUES ('wcKPzDMigJ', '2003-12-13', '土', '土曜練', 1, 'ポポラマーマ', 'パスタ', true, false, 480, 930, 3, 3, 2, 4, 4, '渋谷', 3, '司法試験、講習会', '司法試験に合格するコツについて。さすがに合格者の話には説得力がある。
INSERT INTO public.after VALUES ('ZvPXRSPqb5', '2003-12-17', '水', '水曜練', 1, '卵と私', '洋食', true, false, 880, 980, 3, 4, 5, 4, 4, '渋谷', 6, '下の名前', '大黒が高宮、宮倉の名前を聞いていた。濱崎、田川は初耳だった。田川は大黒の下の名前がわからなかった。しかも田川は荷物を忘れた。');
INSERT INTO public.after VALUES ('4OLSkWLRMx', '2003-12-17', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', true, false, 100, 250, 2, 2, 2, 4, 4, '渋谷', 3, '実験、眠気', '大黒は今、駒場を実験をしたら身になりそうだといっていた。田川、大黒は眠気と戦いながら、話をしていた。');
INSERT INTO public.after VALUES ('z8zaNZDq8K', '2003-12-19', '金', '忘年会', 1, 'モーモーパラダイス', '和食（食べ放題）', true, false, 2000, 3200, 5, 3, 3, 10, 10, '渋谷', 20, '豆腐', '大畑と藤原が、陰で争いをしていた。
INSERT INTO public.after VALUES ('ZX5ELNmq3o', '2003-12-19', '金', '忘年会', 2, 'カチカチ山', '飲み', true, true, 1000, 1500, 2, 3, 2, 8, 8, '渋谷', 16, 'クリスマス', 'クリスマスに向けての必死な人が多いようだ。
INSERT INTO public.after VALUES ('mY3YGeKiVm', '2003-12-20', '土', '土曜練', 1, '勝一', '和食', false, false, 650, 980, 4, 4, 3, 4, 4, '渋谷', 5, '兄弟', '白崎には女兄弟がいるように見えるといわれるが実は男3人。森本は一人っ子であるため雰囲気からでは家族構成は全く分からないらしい。');
INSERT INTO public.after VALUES ('Gkl5lZVOfT', '2003-12-20', '土', '土曜練', 2, 'DOUTOR', 'カフェ', true, false, 300, 300, 2, 3, 2, 4, 4, '渋谷', 5, 'ドラゴンボール', 'TAの仕事は基本的にレポートの採点らしい。しかも昔は時給制だったが、今は授業ごとに給料が決まってるという。ドラゴンボールは突っ込みを入れる所が多すぎて面白い。');
INSERT INTO public.after VALUES ('p5HlAiYMzS', '2003-12-24', '水', '水曜練', 1, 'おひつ家', '和食', true, false, 650, 680, 4, 3, 2, 4, 4, '渋谷', 3, '病気', '人が集まりが悪かったので、練習は無しでアフターを行った。
INSERT INTO public.after VALUES ('RkuN4qal6W', '2006-04-29', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 100, 500, 2, 2, 2, 2, 4, '渋谷', 12, '納豆', '納豆をうどんにかけるとうまい。もちろん生卵つきで。');
INSERT INTO public.after VALUES ('1LHVAeZamK', '2003-12-27', '土', '土曜練', 1, 'おひつ家', '和食', true, false, 680, 700, 4, 4, 3, 4, 4, '渋谷', 8, '速さ', '最近この店ばかりだ。
INSERT INTO public.after VALUES ('nSVyO6oALt', '2003-12-27', '土', '土曜練', 2, 'NEW YORKER''S Cafe', 'カフェ', true, false, 360, 360, 3, 4, 3, 4, 4, '渋谷', 8, 'クイズ', '斎藤が出したクイズをみなで考えた。');
INSERT INTO public.after VALUES ('uFgg4VcilO', '2004-01-07', '水', '水曜練', 1, '庄や', '飲み', true, false, 3500, 3500, 3, 3, 3, 14, 14, '渋谷', 15, '新年会', '酔っ払って迷惑な人がいた。');
INSERT INTO public.after VALUES ('CvFRcGv2xN', '2004-01-10', '土', '土曜練', 1, 'ねぎし', '和食', true, false, 800, 1200, 5, 4, 4, 8, 8, '渋谷', 11, 'かばおくん、語学留学、陸部のメニュー', '工藤さんは全てのおかずを３等分すればごはんを３杯食べられると話し、しっかりと実行に移していた。その隣では、清さんが普通にごはんを４杯食べていた。');
INSERT INTO public.after VALUES ('h7X9dSqoCI', '2004-01-10', '土', '土曜練', 2, 'Sweden', 'アイスクリーム', true, false, 300, 600, 3, 5, 3, 6, 6, '渋谷', 7, 'ホノルルマラソンの写真、レンタルビデオショップ、耳血、携帯のライト', '鼻血は誰でも出すのに耳血を出す人はなぜかいない…と思っていたが、稲田さんの友達はよく出していたようである。
INSERT INTO public.after VALUES ('LXdS38D6gP', '2004-01-14', '水', '水曜練', 1, '勝一', '和食', false, false, 650, 900, 5, 4, 3, 10, 10, '渋谷', 9, '英語', '大畑が店員に、「お茶のお代わり下さい。」と言った。すると白崎が、「Me, too.」と続いた。なぜ英語なのかは謎である。');
INSERT INTO public.after VALUES ('KIagpdNL5a', '2004-01-14', '水', '水曜練', 2, 'ロッテリア', 'ファーストフード', true, false, 200, 200, 2, 3, 3, 4, 4, '渋谷', 5, 'ダルシム、名前', 'インド人といえばダルシムという考えは間違っている。
INSERT INTO public.after VALUES ('pd7cdbUtpn', '2004-01-17', '土', '土曜練', 1, 'タパスタパス', 'パスタ', true, false, 880, 880, 3, 3, 3, 4, 4, '渋谷', 3, 'サッカー', 'うちの大学のサッカー部の新キャプテンは青木の高校時代のキャプテンだった。');
INSERT INTO public.after VALUES ('21a6K34e0a', '2004-01-21', '水', '水曜練', 1, 'Oh Calcutta', 'インド料理', true, false, 1000, 1000, 5, 2, 3, 6, 6, '渋谷', 10, 'カレー、栄養', '宮倉はカレー好きで、あちこち食べ歩いているそうだ。また清も一日一カレーを目指している。
INSERT INTO public.after VALUES ('fZDkeVfMPt', '2004-05-26', '水', '水曜練', 1, 'ラケル', '洋食', false, false, 500, 999, 3, 3, 3, 8, 8, '渋谷', 7, 'テーブルマナー', '田川はテーブルマナーをあまりに知らな過ぎる。');
INSERT INTO public.after VALUES ('TBJUHYYkAU', '2004-01-21', '水', '水曜練', 2, 'CAF&Eacute; DANMARK', 'カフェ', true, false, 250, 280, 1, 3, 3, 4, 4, '渋谷', 3, 'エスプレッソ', '試験前なので勉強するために店に入ったが、それほど続かなかった。
INSERT INTO public.after VALUES ('lPXjS1kCkA', '2004-01-24', '土', '土曜練', 1, 'ブラックブラウン', 'パスタ', true, false, 290, 630, 3, 3, 3, 6, 6, '渋谷', 5, 'ケンタ', 'どちんさんはケンタのメニューにやけにくわしい。
INSERT INTO public.after VALUES ('XfMXCpiBBc', '2004-01-28', '水', '水曜練', 1, 'SIZZLE', '和食', true, false, 690, 860, 4, 3, 3, 8, 8, '渋谷', 7, 'コミックマーケット', '青木がコミケに行った話。一般人はほんの少ししかいなかったそうだ。');
INSERT INTO public.after VALUES ('KIOsUY901h', '2004-01-28', '水', '水曜練', 2, 'ロッテリア', 'ファーストフード', true, false, 180, 250, 2, 3, 2, 4, 4, '渋谷', 5, '保健センター、さとられ', '駒場の保健センターには、面白い人が多い。
INSERT INTO public.after VALUES ('nhVQqWm0XR', '2004-01-31', '土', '土曜練', 1, '勝一', '和食', false, false, 650, 1000, 4, 4, 3, 10, 10, '渋谷', 14, 'カオス、聖書', 'カオスに関する最初の論文は、「一足す一はカオス」のような題名だったそうだ。
INSERT INTO public.after VALUES ('9NsjqUAEVP', '2004-01-31', '土', '土曜練', 2, 'ロッテリア', 'ファーストフード', true, false, 200, 250, 2, 3, 2, 4, 4, '渋谷', 9, '今後の予定', '2月後半から3月にかけて、大黒スキー・1年合宿・山手線一周・追いコン・卒業旅行に大会3つと、予定が重ならないようにするのが大変。');
INSERT INTO public.after VALUES ('TfVqMvsjYD', '2004-02-04', '水', '水曜練', 1, 'くいしんぼ', '洋食', true, false, 780, 880, 4, 3, 3, 5, 5, '渋谷', 9, 'ご飯', 'どちんさんがご飯をおかわりするときにやや多めでって言ったらかなり多めだった。');
INSERT INTO public.after VALUES ('UhqOQ7vfBy', '2004-02-04', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', true, false, 200, 200, 2, 3, 3, 4, 4, '渋谷', 4, 'バイト、およげたいやきくん', '田川と濱崎は最近新しくバイトを始めたらしい。およげたいやきくんはじっくり聞くとおかしなところがたくさんある');
INSERT INTO public.after VALUES ('BlQd9zPwWU', '2004-02-07', '土', '土曜練', 1, 'ロコモコ', 'どんぶり', true, false, 480, 800, 3, 3, 3, 4, 4, '渋谷', 8, '山の手、追いコン', '春は大会などが多い。いつやったらいいものか。最後の方では決まっていた雰囲気だった。');
INSERT INTO public.after VALUES ('ppFNwZRRqt', '2004-02-07', '土', '土曜練', 2, 'ファーストキッチン', 'ファーストフード', true, false, 180, 500, 2, 3, 2, 4, 4, '渋谷', 3, 'バイト、人生経験', '田川はバイトで高2の女子を相手にしていることが発覚。しかし広島の子。他にも面白いバイトがあり人生経験になる。');
INSERT INTO public.after VALUES ('M52xp7kssJ', '2004-02-11', '水', '水曜練', 1, 'いんでいら', 'インド料理', false, false, 390, 390, 2, 3, 3, 4, 4, '渋谷', 4, '学割カレー', '学割ということで、カレー1皿390円だった。10分足らずで食べ終わり、最短アフターではないかと思われる。');
INSERT INTO public.after VALUES ('VKT9z3oyiC', '2004-02-11', '水', '水曜練', 2, 'はなまるうどん', '和食', true, false, 220, 470, 3, 3, 2, 4, 4, '渋谷', 4, '牛丼', '意外とゆっくり出来た。安いし、席さえ空いていれば、アフターでも使えそうだ。
INSERT INTO public.after VALUES ('v0MCpMaXnp', '2004-02-11', '水', '水曜練', 3, 'ロッテリア', 'ファーストフード', true, false, 180, 250, 2, 2, 2, 4, 4, '渋谷', 3, '1年合宿', '1年合宿は、3日間やる必要があるかどうか。田川が各種の連絡に大忙しだった。');
INSERT INTO public.after VALUES ('7HwoFVxlya', '2004-02-14', '土', '土曜練', 1, 'タパスタパス', 'パスタ', true, false, 500, 1000, 3, 3, 4, 6, 6, '渋谷', 15, '牛丼', 'このアフターも、牛丼の話題で持ち切りだった。しかし、牛丼は食べ飽きたという人も多い。');
INSERT INTO public.after VALUES ('DKgNstQ8Jr', '2004-02-14', '土', '土曜練', 2, 'cha', 'カフェ', true, false, 360, 730, 3, 4, 3, 4, 4, '渋谷', 4, 'バイオリンとピアノ', '2次会は猿田のおごりだった。
INSERT INTO public.after VALUES ('rxBvQslVAa', '2004-02-18', '水', '水曜練', 1, '大紘', 'ラーメン', false, false, 650, 720, 3, 2, 3, 6, 6, '渋谷', 12, 'コピープロテクト', '渋谷駅の向かいから、ちょっと奥へと移転した大紘に行った。座席数はちょっと減ったかな、という気がするが、やはり大人数でも座れた。
INSERT INTO public.after VALUES ('3xZHLdMUQ0', '2004-02-18', '水', '水曜練', 2, 'ファーストキッチン', 'ファーストフード', true, false, 250, 250, 2, 3, 2, 6, 6, '渋谷', 5, '携帯電話向けサイト', '大抵の携帯電話向けサイトは、i-mode、EZweb、Vodafone liveのみ対応になっている。PHSにも対応して欲しいものだ。');
INSERT INTO public.after VALUES ('wvAyG82hrp', '2004-02-21', '土', '土曜練', 1, '土間土間', '飲み', true, false, 2000, 3000, 2, 2, 2, 18, 18, '渋谷', 21, '中島、忘れた', '中島がこないとみんな嘆いていた。なわけない。特に何も印象が残っていない。セクシーはなにげにやり手。かもしれない。');
INSERT INTO public.after VALUES ('Y7efrKsvAn', '2004-02-21', '土', '土曜練', 2, 'カチカチ山', '飲み', true, true, 1000, 2000, 1, 1, 1, 8, 8, '渋谷', 13, '不愉快', '斎藤が帰ったあと、みんな不愉快になったが星野さんからの電話でなんとか場が治まった。');
INSERT INTO public.after VALUES ('rjYXvGSPTS', '2004-02-21', '土', '土曜練', 3, 'ロッテリア', 'ファーストフード', true, false, 200, 430, 2, 2, 2, 4, 4, '渋谷', 5, 'アフター録', 'でっちあげてない。今日一日を振り返り、また不愉快になった。早く星野さんに帰ってきてほしい。byなかじまっつん。');
INSERT INTO public.after VALUES ('obmDRh7bI8', '2004-02-25', '水', '水曜練', 1, 'おひつ家', '和食', true, false, 680, 700, 4, 4, 3, 4, 4, '渋谷', 7, '一人暮らし', '一人暮らしでの料理は大変だ。斎藤も家で多少は料理をする。');
INSERT INTO public.after VALUES ('8LW0AkKX8u', '2004-02-25', '水', '水曜練', 2, 'ロッテリア', 'ファーストフード', true, false, 200, 430, 2, 2, 2, 4, 4, '渋谷', 3, '進振り、大学院', '濱崎にとっての進振りと斎藤にとっての大学院は同じくらい漠然としているっぽい。');
INSERT INTO public.after VALUES ('xT3p1KKwSS', '2004-02-28', '土', '土曜練', 1, '勝一', '和食', false, false, 650, 1000, 4, 3, 3, 9, 9, '渋谷', 13, '一年合宿、進路', '昨日から今日にかけて、丹羽家の合宿所で一年合宿が行われていた。
INSERT INTO public.after VALUES ('vjzdHqBOcs', '2004-03-03', '水', '水曜練', 1, 'ふらんす亭', '洋食', true, false, 900, 1000, 3, 4, 3, 4, 4, '渋谷', 9, '大会予想', 'ご飯が無料で大盛・特盛に変更できた。大黒が特盛を注文したが、大盛が来た。
INSERT INTO public.after VALUES ('tXrLpdxpsJ', '2004-03-06', '土', '土曜練', 1, 'ねぎし', '和食', true, false, 780, 780, 4, 3, 3, 4, 4, '渋谷', 8, '合格発表、アンパンマン', '東大の合格発表は、曜日やうるう年にかかわらず、3月10日と決まっているようだ。サークルで何かをする予定は、今のところない。
INSERT INTO public.after VALUES ('sjirUkwH3z', '2004-03-06', '土', '土曜練', 2, 'ロッテリア', 'ファーストフード', true, false, 200, 250, 2, 3, 2, 4, 4, '渋谷', 4, '1年合宿', '今日は珍しく混んでいた。
INSERT INTO public.after VALUES ('Kdk5IzOf20', '2004-03-10', '水', '水曜練', 1, 'Oh Calcutta', 'インド料理', true, false, 1000, 1000, 5, 3, 3, 4, 4, '渋谷', 10, '吉野家', '吉野家のカレーは、ココイチの味に似ているそうだ。豚飯やら豚丼やらを各社出しているが、牛丼の時ほど味に差がない。最近アフターでこの手の話題が必ず出る。');
INSERT INTO public.after VALUES ('flWKoZhH4A', '2004-03-10', '水', '水曜練', 2, 'CAF&Eacute; DANMARK', 'カフェ', true, false, 200, 280, 2, 2, 2, 4, 4, '渋谷', 6, 'ビラ配り、携帯', '今日東大の合格発表があり、早速ビラ配りを行った。
INSERT INTO public.after VALUES ('fYmAmCXosM', '2004-03-13', '土', '土曜練', 1, 'DON''A', 'パスタ', true, false, 680, 880, 3, 3, 2, 10, 10, '渋谷', 10, 'こぼし、ハワイ島', '今日も大黒が水をこぼした。
INSERT INTO public.after VALUES ('g2qTZ2vCqY', '2004-08-28', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', true, false, 200, 200, 1, 2, 2, 4, 4, '渋谷', 1, 'なし', '大学院にむけて勉強した。');
INSERT INTO public.after VALUES ('DGOCR8CGrf', '2004-03-17', '水', '水曜練', 1, '甚八', '飲み', true, false, 3000, 3000, 2, 2, 3, 10, 10, '渋谷', 27, '追いコン', '卒業する4年とM2の門出を祝った。1年生は、知らない人が何人も来て驚いたことと思う。
INSERT INTO public.after VALUES ('yqwlONHgvl', '2004-03-17', '水', '水曜練', 2, '白木屋', '飲み', true, false, 1000, 2000, 2, 3, 2, 10, 10, '渋谷', 20, '撮影', '外で映画の撮影をやっていて、通行が困難だった。江口洋介がいたらしい。');
INSERT INTO public.after VALUES ('0aJgnEsj8z', '2004-03-20', '土', '土曜練', 1, '牛角食堂', '和食', false, false, 700, 900, 4, 4, 3, 3, 3, '渋谷', 5, '混雑、アフター後', '4時台に入店するも席が空くのを待つ。
INSERT INTO public.after VALUES ('IJGbYx5hF1', '2004-03-20', '土', '土曜練', 2, '歌広場', 'カラオケ', true, false, 600, 600, 2, 2, 2, 4, 4, '渋谷', 6, '女性、お土産', 'なんだか女性ボーカルの歌が多かった。神永がエジンバラのお土産を持ってきた。');
INSERT INTO public.after VALUES ('YOs6ACFjpn', '2004-03-21', '日', '荒川市民マラソン', 1, 'ふらんす亭', '洋食', false, false, 900, 1300, 3, 4, 3, 6, 6, '赤羽', 9, '食欲、帰省', 'フルの後は食欲の増える人と減る人がいるようだが、全員食欲旺盛だった。帰省はお金がかかる。');
INSERT INTO public.after VALUES ('iyyQ2mbKua', '2004-03-27', '土', '土曜練', 1, 'SIZZLE', '和食', true, false, 690, 860, 4, 3, 3, 8, 8, '渋谷', 6, '卒業式', '卒業式には面白い格好をしてくる人もいれば、壇上で寝たり、自慢話を始めたりする人もいる。');
INSERT INTO public.after VALUES ('LeKHe2AHlo', '2004-03-28', '日', '山手線一周', 1, '松屋', '牛丼', false, false, 250, 400, 3, 3, 3, 4, 4, '駒込', 9, '店員', '全員疲れていたのと、眠気の為ほとんど会話は無かった。店員が一気に9人も入店した為、苦笑いを浮かべ困っていた。');
INSERT INTO public.after VALUES ('8P9nbsjUXm', '2004-03-31', '水', '水曜練', 1, 'タパスタパス', 'パスタ', true, false, 750, 950, 4, 5, 3, 4, 4, '渋谷', 12, '寝言、大食い', '濱崎は寝言を大きな声で滑舌に言う。
INSERT INTO public.after VALUES ('9AwzB4ll2m', '2004-04-03', '土', '土曜練', 1, '卵と私', '洋食', false, false, 925, 1030, 3, 4, 3, 10, 10, '渋谷', 10, 'オムライスの食べ方', 'オムライスの食べ方には、右側から、左側から、手前からと様々なスタイルがあり、それぞれ理由がある。');
INSERT INTO public.after VALUES ('BwD7gKwcnu', '2004-04-07', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 880, 1030, 2, 4, 2, 6, 6, '渋谷', 12, '周期表', '客がお好みの辛さとスパイスを選べるようになっていた。辛さは通常・2倍・5倍・10倍から、スパイスは通常・2倍・3倍・4倍から選ぶことができ、一段階上がるたびに50円増しになる。
INSERT INTO public.after VALUES ('d4lav0CVFb', '2004-04-07', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', true, false, 200, 200, 2, 3, 3, 4, 4, '渋谷', 2, '勉強、睡眠', '大黒は勉強。濱崎は読書をする静かなアフター。大黒が勉強を終えたとき濱崎は寝ていた。');
INSERT INTO public.after VALUES ('8LV5AEUeL7', '2004-04-10', '土', '土曜練', 1, '勝一', '和食', false, false, 900, 1520, 5, 4, 4, 6, 6, '渋谷', 9, '携帯電話', '携帯電話の機種変を普通にするより白ロムとかいうのを買ってもしくは新規で買って解約して、その新しい携帯に今の電話番号をドコモショップで移し変えてもらうと安上がり！ということらしい。
INSERT INTO public.after VALUES ('j04aUwllYv', '2004-04-14', '水', '水曜練', 1, 'おひつ家', '和食', false, false, 650, 740, 5, 4, 2, 6, 6, '渋谷', 6, 'お代わり', '雨のため、練習はなしで、いきなりアフター。
INSERT INTO public.after VALUES ('M8ch9Vivg7', '2004-04-21', '水', '水曜練', 1, 'SIZZLE', '和食', false, false, 690, 900, 4, 3, 3, 8, 8, '渋谷', 14, '新歓コンパ、ゲーム', '今週の土曜は新歓コンパ。一体どれほど新入生が来るだろうか。
INSERT INTO public.after VALUES ('kDWmv2YgjD', '2004-04-21', '水', '水曜練', 2, 'CAF&Eacute; DANMARK', 'カフェ', true, false, 220, 280, 2, 3, 3, 4, 4, '渋谷', 3, '進振り', '机の足の高さがあっていなかった。
INSERT INTO public.after VALUES ('57AGvOL5sd', '2004-04-24', '土', '土曜練', 1, 'PRONTO', 'dining bar', false, false, 3000, 3500, 2, 3, 2, 8, 8, '渋谷', 38, '新歓コンパ、首都', 'タイの首都を、外国人はバンコクと呼んでいるが、タイではクルンテープ・マハーナコーン・アモーンラッタナコーシン・マヒンタラアユッタヤー・マハーディロッカポップ・ノッパラッターナラーチャタニーブリーロム・ウドンラーチャニウェットマハーサターン・アモーンラピーンアワターンサティット・サッカタットティヤウィサヌカムプラシットという。これに比べれば、スリランカの首都スリジャヤワルダナプラコッテなど短いものだ。');
INSERT INTO public.after VALUES ('02yBV0EeOD', '2004-04-28', '水', '水曜練', 1, '勝一', '和食', false, false, 650, 1000, 4, 4, 3, 10, 10, '渋谷', 6, 'みそ汁', 'くいしんぼ、オーカルカッタともにだめで、勝一に行った。
INSERT INTO public.after VALUES ('zIaPmJD9k4', '2004-04-28', '水', '水曜練', 2, 'ロッテリア', 'ファーストフード', false, false, 189, 262, 2, 2, 2, 4, 4, '渋谷', 4, 'ゾルデ', '濱崎の母校では、ゾルデという怖い話が語り継がれている。
INSERT INTO public.after VALUES ('2VPyyNGsHb', '2004-05-01', '土', '土曜練', 1, 'おひつ家', '和食', false, false, 650, 740, 5, 4, 2, 8, 8, '渋谷', 12, '個別指導、女子高', '個別指導で1対4は中途半端でやり辛いらしい。女子高では「かっこいい女子の先輩」が本当にいるらしい。');
INSERT INTO public.after VALUES ('NkXp8RbGZH', '2004-05-05', '水', '水曜練', 1, '東海苑もうもう家', '焼肉', true, false, 1980, 1980, 5, 3, 3, 8, 8, '渋谷', 8, 'バイキング、携帯電話キャリア', '昔ヨーロッパを荒らした北欧人をバイキングということを知らない者が2人いた。6/8にしか知られていないので、このことは常識ではないようだ。
INSERT INTO public.after VALUES ('QN4kB2p1ZO', '2004-05-08', '土', '土曜練', 1, 'タパスタパス', 'パスタ', true, false, 800, 850, 3, 2, 3, 4, 4, '渋谷', 14, '会員', '携帯からメールを送り、アンケートに答えると、無料で会員になることが出来る。特典は本人にドリンク1杯。');
INSERT INTO public.after VALUES ('dqAKns3cOx', '2004-05-08', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 100, 100, 2, 3, 2, 4, 4, '渋谷', 4, 'ホームページ', 'ホールページを制作している4人が集まった。更新しているのはほとんど日記のみだが、それすら書くことがなく困っている。
INSERT INTO public.after VALUES ('HwXCEQNkEw', '2004-05-12', '水', '水曜練', 1, 'Oh Calcutta', 'インド料理', true, false, 1000, 1000, 5, 3, 3, 4, 4, '渋谷', 4, '新フェス、1年生', '次の日曜に新フェスがあるそうだが、結局東大ぐらいしかいない。働きすぎて楽しくなかったと濱崎が愚痴ってた。今年の1年生はツッコミどころが多いかも。');
INSERT INTO public.after VALUES ('UD0HwkcGYs', '2004-05-15', '土', '土曜練', 1, '勝一', '和食', false, false, 650, 980, 4, 3, 3, 10, 10, '渋谷', 17, '法律用語', '法律では、知っていることが悪意であり、知らないことが善意である。またけだしは、「思うに」という意味ではなくて、「なぜなら」という意味になる。');
INSERT INTO public.after VALUES ('aGk1ihtODd', '2004-05-15', '土', '土曜練', 2, 'ロッテリア', 'ファーストフード', false, false, 210, 410, 2, 3, 2, 4, 4, '渋谷', 6, '数学、鬼ごっこ', '佐藤が持ってきた高校数学の問題を、みなで考えた。よりエレガントな解答が欲しいらしい。
INSERT INTO public.after VALUES ('TMP4olsyZ7', '2004-05-19', '水', '水曜練', 1, 'ロコモコ', 'どんぶり', true, false, 680, 940, 2, 3, 3, 4, 4, '渋谷', 4, '卵', 'すべての料理に卵が使われていて、店長には酷だった。');
INSERT INTO public.after VALUES ('b12zOw14Uz', '2004-05-22', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 880, 1280, 3, 3, 2, 12, 12, '渋谷', 12, '名簿', '名簿は紙と電子化されるのとどちらが良いか。どちらの意見にしろ、まだ書いていない人は、<a href="http://meibo.honomara.net">ホノマラ名簿</a>から書いて下さい。');
INSERT INTO public.after VALUES ('b1BxgAX2HJ', '2004-05-22', '土', '土曜練', 2, 'NEW YORKER''S Cafe', 'カフェ', false, false, 263, 462, 2, 3, 3, 4, 4, '渋谷', 7, 'プチハーレム', '明日駅伝なのだが、どうもやる気が感じられない。男女比1:2を超えると、プチハーレムというそうだ。
INSERT INTO public.after VALUES ('5ZIER6Pwsx', '2004-05-26', '水', '水曜練', 1, '駒場生協', '学食', false, false, 490, 490, 3, 3, 1, 1, 1, '駒場', 1, '有名人', '道でばったり有名人に会ったときどうしようか勝手にシミュレーションしてみる。あの方のようにナンパするのがよいか、僕のように見ているだけで幸せになっているのがよいか。結局有名度により変わるという結論になった。');
INSERT INTO public.after VALUES ('0Prf2lwhUW', '2004-05-26', '水', '水曜練', 2, 'Haagen-Dazs', 'アイスクリーム', false, false, 360, 400, 3, 4, 3, 2, 2, '渋谷', 3, '数学', '2本の線分が交わる条件や、円周率が無理数であることの証明について。');
INSERT INTO public.after VALUES ('KXG8P3MKdv', '2004-05-29', '土', '土曜練', 1, '仙台四郎', '和食', false, false, 900, 1300, 4, 3, 2, 6, 6, '渋谷', 11, '冷房、単位、食べ方', '冷房が壊れていた為、不快なぐらい暑かった。大学変われば単位取得も変わる。なんだか、牛タン丼にお酢を入れたり、わさびで涙したりしてた。');
INSERT INTO public.after VALUES ('ukurLu0aYi', '2004-05-29', '土', '土曜練', 2, 'ロッテリア', 'ファーストフード', true, false, 200, 250, 2, 3, 2, 4, 4, '渋谷', 3, '五月祭、ウイルス、酒', '田川はチケットを利用する為だけに五月祭に行くみたい。ウイルス、コンピュータウイルスは生物なのか？で議論する。キーワードは代謝。大嶋、濱崎の父親はよく飲むが、田川家は全員酒に弱い。田川は酒の練習が必要だということで一致した。');
INSERT INTO public.after VALUES ('vTMczytTWp', '2004-06-02', '水', '水曜練', 1, 'カプリチョーザ', '洋食', false, false, 870, 900, 3, 3, 3, 8, 8, '渋谷', 7, '割引', '店に向かい途中、道端でちょうどこの店の10%割引券を配っていた。ラッキー。');
INSERT INTO public.after VALUES ('RnTK9d50Xu', '2004-06-02', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 210, 262, 2, 3, 2, 4, 4, '渋谷', 3, 'アフターズ', 'アフターズに慣れない2人は、アフターズで何をすればよいか戸惑っているようだった。');
INSERT INTO public.after VALUES ('aJg4fF7REQ', '2004-06-05', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 900, 1100, 3, 3, 3, 4, 4, '渋谷', 17, '誕生日', '最近良いことがない。
INSERT INTO public.after VALUES ('vBVSXbYkW2', '2004-06-05', '土', '土曜練', 2, 'ウィステリアコート（大黒家）', '大黒', false, false, 500, 500, 5, 5, 5, 7, 7, '駒込', 7, '誕生日', '最近良いことがない。
INSERT INTO public.after VALUES ('6ZtTvG2Gfv', '2004-06-06', '日', '喜多マラソン', 1, '和み亭', '和食', true, false, 500, 1000, 3, 3, 3, 8, 8, '王子', 15, '疲れ', '疲れて眠たそうな人が多かったが、応援の濱崎と大黒はよく喋っていた。');
INSERT INTO public.after VALUES ('1dOuI7YQmB', '2004-06-09', '水', '水曜練', 1, 'SIZZLE', '和食', false, false, 690, 900, 4, 3, 3, 8, 8, '渋谷', 7, '制服、先生', '安藤は女子高生と制服という響きに弱いみたいだ。田川と安藤の母校には見た目が浮浪者のような先生がいる。落合の母校ではフックの法則をスパゲティで実験した。');
INSERT INTO public.after VALUES ('LF5Nsv8OBI', '2004-06-09', '水', '水曜練', 2, 'CAF&Eacute; DANMARK', 'カフェ', true, false, 220, 280, 2, 3, 3, 4, 4, '渋谷', 3, 'あだ名', '落合の中学時代のあだ名は「カラキン」、大嶋の高校時代のあだ名は「駅長」だった。濱崎は昔からあだ名が無い。');
INSERT INTO public.after VALUES ('n2XoIlNOgo', '2004-06-12', '土', '土曜練', 1, 'Oh Calcutta', 'インド料理', true, false, 1000, 1000, 5, 3, 3, 8, 8, '渋谷', 13, 'バラバラ、皇居練', '混んでいて３グル−プに分かれた。清は毎週日曜皇居練という案を出していた。他のテーブルでも色んな話題で盛り上がっている様子だった。');
INSERT INTO public.after VALUES ('4EFzpaLBV1', '2006-04-22', '土', '土曜練', 2, '和民', '飲み', false, false, 1000, 1000, 3, 3, 3, 4, 4, '渋谷', 25, '手狭', '狭くて机につけない人もいた。');
INSERT INTO public.after VALUES ('FqwSNmxrMR', '2004-06-16', '水', '水曜練', 1, '卵と私', '洋食', false, false, 925, 1300, 3, 4, 3, 10, 10, '渋谷', 7, '知り合い、ローカル、ずらしキャラ', '落合の知り合いが働いていた。近野は濱崎と田舎の共通点について、斎藤と山形の教習所について、とローカルな話題満載。
INSERT INTO public.after VALUES ('IVan9VnHjq', '2004-06-16', '水', '水曜練', 2, 'NEW YORKER''S Cafe', 'カフェ', false, false, 263, 462, 2, 3, 3, 6, 6, '渋谷', 6, '合宿、旅行', '合宿と旅行の話が同時進行していた。どちらも日程と予算、空き状況などに苦戦しているようだ。');
INSERT INTO public.after VALUES ('2KKJ8cGmfQ', '2004-06-19', '土', '土曜練', 1, '勝一', '和食', false, false, 650, 1000, 4, 4, 3, 10, 10, '渋谷', 12, 'ハヤシライス、大盛り、総長杯', 'ハヤシライスがメニューから消えた。お代わりの大盛りが本当に大盛りだった。清曰く総長杯ではホノマラは戦えるらしい。ただキーパーがいない。');
INSERT INTO public.after VALUES ('hcj9sGH9M0', '2004-06-19', '土', '土曜練', 2, 'エスト', 'ビリヤード・カラオケ', false, false, 680, 680, 3, 4, 3, 4, 4, '渋谷', 3, 'ミラクル、カラオケ', 'ビリヤード大会を行った。ルールは三人で順番に番号の少ないボールを狙って打っていき、９番ボールを落とした人を勝ちとするもの。勝者は次のようになった。
INSERT INTO public.after VALUES ('XXZ2vemKWC', '2004-06-20', '日', '皇居練', 1, 'ジョナサン', 'ファミレス', false, false, 800, 1200, 3, 3, 2, 4, 4, '渋谷', 6, '疲労、咳き', '疲れていて比較的静かなアフター。大会の話、研究室の話などがでた。中島が酷く咳きこんでいた。さらに大嶋にそれはうつった。');
INSERT INTO public.after VALUES ('yg20dVShbW', '2004-06-23', '水', '水曜練', 1, 'GOLDRUSH', '洋食', false, false, 900, 1280, 3, 3, 3, 6, 6, '渋谷', 6, '水、クラス', '田川と落合の水のお代わりが尋常じゃなかった。クラスの中のよさは、クラスごとかなり違う。近野のクラスは毎月コンパ開催。津釜のクラスは秋休みに合宿が企画されている。');
INSERT INTO public.after VALUES ('LQ2NqAAcFM', '2004-06-23', '水', '水曜練', 2, 'NEW YORKER''S Cafe', 'カフェ', false, false, 263, 462, 2, 3, 3, 6, 6, '渋谷', 5, '合宿、世界記録', '合宿の参加者は結構いるようだ。100mで9秒を切るのとマラソンで2時間を切るのはどちらが早く達成されるのか？大黒は後者だと主張していた。');
INSERT INTO public.after VALUES ('e512Gmr1bK', '2004-06-26', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 800, 900, 3, 3, 3, 4, 4, '渋谷', 14, '富士登山、24時間リレー', '富士登山の日程について。8月22日あたりが適当か？
INSERT INTO public.after VALUES ('zzAqh04NA6', '2004-06-26', '土', '土曜練', 2, 'ロッテリア', 'ファーストフード', true, false, 105, 294, 3, 3, 3, 4, 4, '渋谷', 9, '勉強', '清はパソコンを開いて勉強をしていた。下付き文字のショートカットキーが分からず悩んでいた。');
INSERT INTO public.after VALUES ('h7Y9gWzEXE', '2004-06-27', '日', '横田駅伝', 1, 'スカイラーク', 'ファミレス', false, false, 800, 1200, 3, 3, 2, 4, 4, '立川', 11, '邪魔、セット、ミニどんぶり', '濱崎が明らかに邪魔な席に座っていた。御飯だけ頼みたいのに、セットメニューにするしかないのはおかしい。隣のテーブルでは半分の人がミニ丼ぶりを2杯頼んでいた。');
INSERT INTO public.after VALUES ('u6U3Kvza5u', '2004-06-27', '日', '横田駅伝', 2, 'サンビリヤード', 'ビリヤード', false, false, 600, 1200, 3, 1, 3, 4, 4, '吉祥寺', 4, '店員', 'ガラガラの店内。店員が「今日はサッカーとかあるんですか？」と話しかけてくる。寂しかったそうだ。その後、客が集まり「お客さんがいっぱい来てくれました。」と笑顔で話しかけてきた。');
INSERT INTO public.after VALUES ('X7kbTnPej6', '2004-06-27', '日', '横田駅伝', 3, '松屋', '牛丼', false, false, 300, 600, 2, 2, 2, 2, 2, '久我山', 3, '久しぶり、豚丼', '大嶋の家から１分もかからないところにあるのに、大嶋は約半年ぶりの入店だった。
INSERT INTO public.after VALUES ('imf7IBAJaR', '2004-06-30', '水', '水曜練', 1, 'くいしんぼ', '洋食', true, false, 780, 880, 4, 3, 3, 5, 5, '渋谷', 6, '模様替え、酒', '5階部分が別の店となり、店内は様相をガラリと変えていて驚いた。近野は毎週何らかの形で酒を飲む。一人で飲むこともあるようだ。');
INSERT INTO public.after VALUES ('KgYyi4EyL1', '2004-06-30', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 210, 262, 2, 3, 2, 4, 4, '渋谷', 6, '必勝法、哲学', '棒しゲームの必勝法を落合が披露。見え方、聞こえ方は結局、言葉を使っても他人とは共有できないなんて哲学めいた議論が濱崎が描いた絵をきっかけに勃発。');
INSERT INTO public.after VALUES ('cDvaBXWSEM', '2004-07-03', '土', '土曜練', 1, 'SIZZLE', '和食', false, false, 690, 900, 4, 3, 3, 8, 8, '渋谷', 14, 'ＨＰ、日記', '結構みんなサークルのＨＰを見ているようだ。さらに日記も読まれているようだ。');
INSERT INTO public.after VALUES ('Ae6meTddJq', '2004-07-03', '土', '土曜練', 2, 'Sweden', 'アイスクリーム', true, false, 300, 600, 3, 5, 3, 4, 4, '渋谷', 4, '攻撃、ストライクゾーン', '田川がスプーンを折ったら、大嶋の首に破片が直撃。大嶋の痛そうな声が響いた。女性のタイプに関して、田川の場合、抽象的過ぎて言葉にできず、かなり広いストライクゾーンの持ち主だ。');
INSERT INTO public.after VALUES ('Ij64arZz9L', '2004-09-01', '水', '水曜練', 1, 'おひつ家', '和食', false, false, 680, 760, 4, 3, 2, 4, 4, '渋谷', 5, '旅行', '旅行中は訳の分からないことで盛り上がることがよくあるようだ。');
INSERT INTO public.after VALUES ('c4LNmNZFwN', '2004-07-07', '水', '水曜練', 1, 'ラケル', '洋食', false, false, 500, 999, 3, 3, 3, 8, 8, '渋谷', 6, '水、ストッパー', '田川が浴びるように水を飲んでいた。ストッパーを稲田が探していた。メンバーを先発、中継ぎ、ストッパーに分けようとしたが、無理があった。');
INSERT INTO public.after VALUES ('8JpFDeTol0', '2004-07-07', '水', '水曜練', 2, 'サンマルク・カフェ', 'カフェ', true, false, 410, 450, 3, 3, 3, 4, 4, '渋谷', 3, '冷房、虫、親族', 'タイでは冷房を効かせすぎて設定温度が5度のこともある。落合は虫に対して良く悪戯をしていたようだ。濱崎は親族が多すぎて家系図がかけない。大嶋の祖父は物凄く酒を飲むらしい。');
INSERT INTO public.after VALUES ('ybSYWqaom9', '2004-07-10', '土', '土曜練', 1, '大紘', 'ラーメン', false, false, 650, 850, 3, 3, 3, 4, 4, '渋谷', 8, 'ホノマラな人、ご飯', '合宿で行う「ホノマラな人」の質問項目を考えた。清、大嶋はランクインを望んでいたが、田川は嫌がっていた。
