INSERT INTO public.after VALUES ('gws8xUnXOe', '2004-11-03', '水', '水曜練', 2, 'ロッテリア', 'ファーストフード', false, false, 210, 280, 3, 3, 3, 6, 6, '渋谷', 6, 'こびと', '100人のこびとのうち、最低何人が怪獣から助かることができるか。100年に一度の祭りといい、こびとは頭が良い。');
