INSERT INTO public.after VALUES ('recm7OKl81', '2005-01-26', '水', '水曜練', 1, 'ぱすたかん', 'お好み焼き', false, false, 1200, 1200, 3, 4, 3, 4, 4, '渋谷', 5, '駒場祭', '来年の駒場祭でお好み焼きを売るなら、店長が作るとさまになりそうだ。');
