INSERT INTO public.after VALUES ('UIuFkyvFhg', '2005-02-02', '水', '水曜練', 1, '卵と私', '洋食', false, false, 975, 1030, 3, 3, 3, 10, 10, '渋谷', 7, '住居選択のポイント、自炊', '小野さんが住居を選ぶ上でポイントとしているのはツタヤが近いことだそうだ。
INSERT INTO public.after VALUES ('VdVfc9zFCl', '2005-02-02', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 0, 0, 2, 2, 3, 6, 6, '渋谷', 6, '体育祭、バイト', '高校の体育祭の華と言えば棒倒しだが、最近行う学校が減っている。
