INSERT INTO public.after VALUES ('GPctFNgXMk', '2005-03-06', '日', '三浦国際市民マラソン', 1, '笹みづ', '和食', false, false, 1000, 1000, 2, 4, 3, 2, 4, '三浦海岸', 2, '追いコン', '一昨年も訪れた店です。店名違うかも。
