INSERT INTO public.after VALUES ('OKV6zsb9T4', '2005-03-13', '日', '浦安マラソン', 1, 'PLANET HOLLYWOOD', '洋食', false, true, 1030, 1580, 4, 4, 4, 10, 10, '浦安', 11, '無言', '今年もイクスピアリでアフター。大会後で混んでいる店が多かったが、この店は空いていた。
