INSERT INTO public.after VALUES ('mPi0FuQ313', '2005-03-19', '土', '土曜練', 1, 'おひつ家', '和食', false, false, 720, 900, 5, 4, 3, 4, 4, '渋谷', 5, '満員電車、入試', '満員電車に乗るときは、痴漢に間違えられないよう努力しなければならない。男性専用車両も設置してほしい。
INSERT INTO public.after VALUES ('XX8ApEGRUa', '2005-03-20', '日', '荒川市民マラソン', 1, '一源', '和食', false, false, 990, 990, 4, 3, 2, 6, 6, '浮間舟渡', 7, 'パニック', 'マラソンを走った人々が店に押し寄せたため、店員がパニクっていた。料理がなかなか出てこず、用事のあった森本は食すことができなかった。');
INSERT INTO public.after VALUES ('u7Cn0POFAw', '2005-03-23', '水', '水曜練', 2, '東海苑もうもう家', '焼肉', false, false, 2079, 2079, 5, 3, 3, 4, 4, '渋谷', 2, 'ビールLv.、山手線', '大嶋は福岡に帰省していた３日間で、ビールだけでも2850ml飲んでいる。そろそろ「ビールはさらさらしてて飲みやすい」の境地に達しそうだ。');
INSERT INTO public.after VALUES ('uhkxrQeKci', '2004-06-20', '日', '皇居練', 2, 'エスト', 'ボウリング', false, false, 600, 1200, 3, 1, 3, 4, 4, '渋谷', 4, '接戦、好不調', '1ゲーム目。濱崎が「G」を4つもだす絶不調。その中、残りの三人は好調。ストライクやスペアを出しまくる。
INSERT INTO public.after VALUES ('45lwMyxCdd', '2003-07-07', '月', '本郷練', 1, 'まるみ', '中華', false, false, 500, 850, 3, 1, 3, 4, 4, '本郷', 4, 'コナン、種', '名探偵コナンを熱心に見ているお客さんがいた。七夕の話題から笹と竹の違いについて、クジラとイルカの違いについての話になった。それぞれ微妙な違いで、種としては同じものらしい。');
INSERT INTO public.after VALUES ('gXXuIcWaEx', '2004-07-14', '水', '水曜練', 2, 'エスト', 'ボウリング', false, false, 1200, 1500, 3, 5, 3, 4, 4, '渋谷', 4, '好記録', 'みんなスコアがすごく良かったので載せておきます。
INSERT INTO public.after VALUES ('CkpUaqac5A', '2004-08-07', '土', '土曜練', 3, 'エスト', 'ボウリング', false, false, 1200, 1800, 3, 3, 3, 4, 4, '渋谷', 2, '平凡', '２ゲーム目に大嶋がコントロールを乱したものの、どちらもアベレージが１００前後で何の波乱もなく終わった。');
INSERT INTO public.after VALUES ('mbtAhjChA3', '2004-10-20', '水', '水曜練', 2, 'エスト', 'ボウリング', false, false, 1200, 1800, 3, 3, 3, 4, 4, '渋谷', 4, '急上昇、仕事人', '1ゲーム目はみんな散々なスコアだったが中島が見に来てから、全員調子が急激にあがり、スコアが全員30以上上がった。田川の球威であのスコアは奇跡と呼ぶよりは仕事人だ。');
INSERT INTO public.after VALUES ('vwu6AlRMbB', '2005-03-23', '水', '水曜練', 1, 'エスト', 'ボウリング', false, false, 1300, 1300, 3, 3, 3, 4, 4, '渋谷', 2, '携帯のカメラ', '大嶋は新しい投げ方で投げ、いつもよりかなり悪いスコア。田川は６ポンドの球で投げ、いつも通りかなり悪いスコア。');
INSERT INTO public.after VALUES ('VrvTRkQN6d', '2005-03-26', '土', '土曜練', 3, 'ルノアール', 'ファミレス', false, false, 500, 500, 3, 3, 3, 4, 4, '渋谷', 6, 'もの忘れ', '山手線にチャリ参加のまっつんもルノアールに呼ぼうとしたが、結局断念。
INSERT INTO public.after VALUES ('92kGHmtpmL', '2005-03-26', '土', '土曜練', 2, 'Sweden', 'アイスクリーム', true, false, 780, 780, 3, 3, 3, 4, 4, '渋谷', 3, 'もの忘れ', 'わけをは最近もの忘れがひどいらしい。しかし、今日は前から気になっていたことをはっきりさせることができた。');
INSERT INTO public.after VALUES ('NuUFCnrwOR', '2005-02-16', '水', '水曜練', 1, 'エスト', 'ボウリング', false, false, 1000, 1500, 3, 5, 5, 4, 4, '渋谷', 9, 'チョコ', '女子の皆さんがチョコを持ってきてくれました。おいしかったです。ありがとうございました！
INSERT INTO public.after VALUES ('HMP20zgKqq', '2005-01-15', '土', '土曜練', 2, 'エスト', 'ボウリング・カラオケ', false, false, 1500, 1500, 2, 2, 2, 8, 8, '渋谷', 10, 'ボウリング、カラオケ', 'ボウリングは、1回目が店長、2回目が中島が、2人とも147点でトップだった。
INSERT INTO public.after VALUES ('5lF37Blqcw', '2004-12-22', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 800, 900, 4, 3, 3, 6, 6, '渋谷', 7, 'ひとり、几帳面', '一人でカラオケやボウリングに行くことは普通だろうか？寺田は几帳面だということが判明した。');
INSERT INTO public.after VALUES ('4nIgEPOfoE', '2004-10-30', '土', '土曜練', 1, 'にんにく屋', '洋食', true, false, 780, 980, 3, 3, 3, 6, 6, '渋谷', 6, 'サッカー、メモ帳', '今日は、総長杯のサッカーの試合があって、それから食堂で食事をして、それから代々木公園近くの広場でのスリランカフェスティバルに行って、それから渋谷でボウリングをして、それからダーツをして、そのアフター。
INSERT INTO public.after VALUES ('1We1KM1RtB', '2004-04-17', '土', '土曜練', 1, 'タパスタパス', 'パスタ', true, false, 900, 900, 3, 3, 3, 4, 4, '渋谷', 16, '地上の楽園', 'どちんが、オーストラリア留学の思い出に浸り、地上の楽園だったと言っていた。
INSERT INTO public.after VALUES ('UZ66YcKUsi', '2002-05-04', '土', '土曜練', 1, '勝一', '和食', false, false, 500, 2170, 5, 4, 4, 10, 10, '渋谷', 11, '給食、店員さん', '給食が話題に。ミルメークという牛乳に混ぜるものがあったらしい。大黒は小、中学校と、ライスだけは持参だった。店員さんがかなりかわいかったため、まっつんと大黒が腹黒く御代わりやら水やら頼んでいた模様。１年生は猿田のおごり。この後、タイピング、ボウリング、コイン消費、右脳診断を経て2次会へ。');
INSERT INTO public.after VALUES ('8ILz97wFVp', '2005-03-27', '日', '山手線一周', 1, 'めしや丼', '和食', false, false, 350, 720, 5, 4, 3, 5, 5, '神田', 11, '食べ放題', 'ご飯と漬物が食べ放題なのだが、田川が漬物を食べ尽くしてしまった。お茶の水差しも空になっていた。');
INSERT INTO public.after VALUES ('LqQWUwqygy', '2005-04-02', '土', '土曜練', 1, '卵と私', '洋食', false, false, 975, 1030, 3, 3, 2, 8, 8, '渋谷', 7, '牛タン', '店長が牛タンを求めて、まずは仙台四郎に行ったが、無くなっていた。仕方なく卵と私で牛タンのオムライスを注文。よほど牛タンが食べたかったようで、全員の食事が出揃う前に食べ終わっていた。
INSERT INTO public.after VALUES ('ona40SEU8E', '2005-04-06', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 820, 950, 5, 3, 3, 4, 4, '渋谷', 2, '新歓、授業の取り方', '運動会自転車部も新入生集めは大変らしい。3学期の授業の取り方は理系と文系では大きく違う。');
INSERT INTO public.after VALUES ('KZTeIdxPaU', '2005-04-09', '土', '土曜練', 1, '代々木公園', '野外', false, false, 500, 500, 3, 3, 3, 20, 20, '渋谷', 15, '花見、紛失', '桜が満開だったので、代々木公園で花見をした。
