INSERT INTO public.after VALUES ('xLZi8mcCVO', '2005-04-27', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 800, 960, 3, 4, 3, 4, 4, '渋谷', 15, '家事', '洗濯したり洗濯物を干して取り込んだりするのはけっこう面倒だ。
INSERT INTO public.after VALUES ('SHOawnfR9G', '2005-04-30', '土', '土曜練', 2, 'NEW YORKER''S Cafe', 'カフェ', false, false, 210, 315, 2, 3, 3, 8, 8, '渋谷', 8, '伊藤博文、誕生日', '伊藤博文の千円札について。夏目漱石のが発行されたのが、昭和59年11月だそうだ。なぜ落合は、そんなお札をたくさん持っているのだろう。
