INSERT INTO public.after VALUES ('XPWhTGNk0g', '2008-04-29', '火', '皇居練', 1, '甘太郎', '焼肉', false, false, 2929, 2929, 0, 0, 0, 10, 10, '神田', 9, '新機能', '「ボタンを押したら一昔前の助っ人外国人選手の名前を一人出してくれるような機械が欲しい」というU原さんの発言を受けて学さんが早速雑談掲示板に新機能を付けてくれた。');
