INSERT INTO public.after VALUES ('eoFNWqGJtw', '2008-06-25', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 350, 350, 0, 0, 0, 10, 10, '渋谷', 10, '高尾練', '21時ごろには残りメンバーが4人になってしまった。小谷と新春マラソンの苦い思い出を語り合った。');
INSERT INTO public.after VALUES ('SAnEPGnfG7', '2008-06-25', '水', '水曜練', 1, 'ヘルスマジック ', '和食', false, false, 800, 800, 0, 0, 0, 18, 18, '渋谷', 17, 'おかわり', '吉津がご飯のおかわりをもらうために、立ち上がってご飯茶碗を店員のほうへ持っていっていた。');
INSERT INTO public.after VALUES ('Rk2rhHY9Mk', '2008-06-26', '木', '鬼ごっこ', 1, '和館', '飲み', false, false, 1000, 1000, 0, 0, 0, 30, 30, '駒場', 33, '鬼コン', '鬼ごっこ×和館飲み＝鬼コンという初の試み。ホノマラっぽいまったりな感じで夜は更けていった。何を話したんだっけ？
INSERT INTO public.after VALUES ('o3OXKU4Ydk', '2008-06-28', '土', '土曜練', 1, 'カレー研究所', '洋食', true, false, 1000, 1000, 0, 0, 0, 4, 6, '渋谷', 13, '合宿のコース', '今日はなぜかカレ研のほうが人気だった。また2週間後に高尾練を企画するとかしないとか…');
INSERT INTO public.after VALUES ('LrN3UB0k4X', '2008-06-28', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 250, 250, 0, 0, 0, 4, 4, '渋谷', 11, '運転免許', '1年生の中にはもう仮免までとった人もいるらしい。学さんの運転テクについて。何人かのホノマラに入った動機を聞いた。それから1年会が開かれるという噂が。。');
INSERT INTO public.after VALUES ('qlxoz8t8ZB', '2008-06-28', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 8, 'スイカ', 'Suica、nanacoを始めとする日本で普及している非接触式ＩＣカードの規格をFeliCa方式という。
INSERT INTO public.after VALUES ('Uy4ps3hZzi', '2008-07-02', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 20, 20, '渋谷', 17, '1年会', '今年はどのくらいのホノマラーがホノルルマラソンに行って真のホノマラーになるのか。近々1年会なるものが開催されるらしい。日程は調整中？');
INSERT INTO public.after VALUES ('f85JED5XSw', '2008-07-02', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 9, '匿名性', '基礎演習の課題、「インターネットの匿名性を禁止し、実名にするべきだ」という意見のディベートについて真剣に考えてしまった。隣のテーブルでは数学。つまりアフターズに来ればテスト勉強もできるってことですね。');
INSERT INTO public.after VALUES ('DowYO6IiGA', '2008-07-05', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 250, 250, 0, 0, 0, 4, 4, '渋谷', 5, '夢', '夢を見るたびに、目覚めた直後に手帳に記録しているという話
INSERT INTO public.after VALUES ('755GiO47yI', '2008-07-05', '土', '土曜練', 1, 'カレー研究所', 'インド料理', false, false, 950, 950, 0, 0, 0, 12, 12, '渋谷', 11, '甘酸っぱい', 'カレ研の各テーブルに置いてあるオレンジのジャムみたいなトッピングっぽいものを食べている人がいたが結局何か分からずじまい。
INSERT INTO public.after VALUES ('bPJrlr7Fcw', '2009-09-05', '土', '土曜練', 1, 'ロコモコ', 'どんぶり', false, false, 900, 1000, 0, 0, 0, 4, 4, '渋谷', 9, 'はら', '果たして本当に彼は○〇〇腹なのだろうか？
INSERT INTO public.after VALUES ('6gqAmqBbws', '2008-07-05', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 20, 20, '渋谷', 19, 'ホクマラ', 'ごーがホクマラ旅行の段取りについて画期的なアイディアを発案したらしいのだが、結局何だったんだろう。');
INSERT INTO public.after VALUES ('ELIAsDYlrk', '2008-07-05', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 350, 350, 0, 0, 0, 22, 22, '渋谷', 22, '女性に対する気配りが足りないらしい', '男が異性に話しかけるという点で「出身が男子校か共学ではやっぱり違いますよ」というところを「出身が男子校か女子校かではやっぱり違いますよ」と以頭が言っていた。
INSERT INTO public.after VALUES ('jLw4Dkwo3t', '2008-07-07', '月', '本郷練', 1, '中央食堂', '学食', false, false, 500, 500, 0, 0, 0, 12, 12, '本郷', 12, '赤門ラーメン', '赤門ラーメンを食べてる人がいっぱいいた。ニンニク入れるとおいしいよね。
INSERT INTO public.after VALUES ('ltrsvm3yMd', '2008-07-09', '水', '水曜練', 1, 'ロコモコ', 'どんぶり', false, false, 950, 950, 0, 0, 0, 12, 12, '渋谷', 12, '初', '初ロコモコという人が多かった。ホノルルに行きましょう、といろいろ宣伝した。野郎のテーブルでは野郎でどっか行きたいねという話をしていたようだ。');
INSERT INTO public.after VALUES ('mfpj0QngLJ', '2008-07-09', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 400, 400, 0, 0, 0, 8, 8, '渋谷', 8, 'ホノマラ劇場', 'ホノマラで劇をやりたいという話が持ち上がった。演じるのは、『ロミオとジュリエット』。配役はどうでもいいけど、ジュリエットが彼だというのは譲れないらしい。');
INSERT INTO public.after VALUES ('3GWDvHc5LS', '2008-07-12', '土', '土曜練', 1, 'ヘルスマジック ', '和食', false, false, 800, 800, 0, 0, 0, 26, 26, '渋谷', 26, 'フリスビー', 'ホノマラが入る前はお客さんが1人しかいなかった。
INSERT INTO public.after VALUES ('ZsZfMH8itG', '2008-07-12', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 450, 450, 0, 0, 0, 18, 18, '渋谷', 18, 'まばたき', 'どんなに速くまばたきをやろうと思っても彼女のスピードには勝てないだろう。
INSERT INTO public.after VALUES ('WhSp6QIPKj', '2008-07-14', '月', '本郷練', 1, '中央食堂', '学食', false, false, 500, 500, 0, 0, 0, 10, 10, '本郷', 9, '同じ', '先週と同じものを食べている人がいっぱいいた。勉強の話で盛り上がった？');
INSERT INTO public.after VALUES ('zZ7zPQVNx7', '2008-07-16', '水', '水曜練', 1, 'GOLDRUSH', '洋食', false, false, 950, 950, 0, 0, 0, 12, 12, '渋谷', 11, 'weak voice', '小谷は今日、暑さの余り数年ぶりに吐いてしまったらしい。うまくつっこめなくてすいませんでした。
INSERT INTO public.after VALUES ('4N7H3css5O', '2008-07-16', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 250, 250, 0, 0, 0, 8, 8, '渋谷', 8, '○○練', '走ることに比べれば、勉強は何て楽なことなんだろうという結論に至ってしまった。バイト嫌いキャラがホノマラに一人増えた。
INSERT INTO public.after VALUES ('tWHhdZyuau', '2008-07-19', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 20, 20, '渋谷', 29, 'グラスの持ち方', '池本さんがハンズのほうのタパタパの下にあるローソンで念願のアイスを買っていた。階段の下で食べきってからの入店。
INSERT INTO public.after VALUES ('1eJxk6dfrA', '2008-07-19', '土', '土曜練', 2, 'GREEN GARDEN', '飲み', false, false, 1300, 1300, 0, 0, 0, 4, 4, '渋谷', 3, '飲み比べ', 'Bunkamuraのほうの東急の屋上のビアガーデンへ。ハイネケン、バドワイザー、ハートランドを飲み比べたり、ソーセージの食べ比べをした。
INSERT INTO public.after VALUES ('7Qosnl1CjH', '2008-07-19', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 400, 400, 0, 0, 0, 12, 12, '渋谷', 16, 'アンケート', '合宿のアンケートの項目数をどうするかちょっと話した。42という数字はやっぱりこだわる必要があるのだろうか。');
INSERT INTO public.after VALUES ('Uxgbpf5V70', '2008-07-21', '月', '皇居練', 1, '甘太郎', '焼肉', false, false, 3000, 3000, 0, 0, 0, 12, 12, '神田', 10, '占い', 'お決まりの稲荷湯→焼肉食べ放題。メニューを注文する機械についている占い機能で遊んでみた。ガツとは豚の胃のこと。gut（消化管）の複数形が語源らしい。おしぼり戦争ってなんのことなんでしょうか？');
INSERT INTO public.after VALUES ('4C4zEgLill', '2008-07-19', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 200, 200, 0, 0, 0, 6, 6, '渋谷', 6, '会話', 'スェェデンに入れなかったor入らなかった人たち
INSERT INTO public.after VALUES ('qMGmKUrYgC', '2008-07-23', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 6, 6, '渋谷', 6, 'バイク', 'ホノマラの過去の歴史を川口が現在のホノマラの状況と比較しながら話していた。連れ去り事件の顛末に皆衝撃を受けていた。そのうちホノマラそのものを考えるマジメな展開に。昔の少人数アフターズみたいな感じだった（ようだ）。
INSERT INTO public.after VALUES ('DEIjxiZkim', '2008-07-23', '水', '水曜練', 1, '卵と私', '洋食', false, false, 1030, 1030, 0, 0, 0, 12, 12, '渋谷', 11, 'アフター録', 'アフター録の書き方・語法の統一について話した。アフター録はあくまでアフターで何が話されていたかを客観的に記述するもので、書き手が自分の感情を書きたいときも、「吉江は小谷がすごいと思っていた」と書くべきではないかといったことを落合が話していた。敬語や尊称も省いたほうが分かりやすい、という指摘もあった。');
INSERT INTO public.after VALUES ('cikIiVy12J', '2008-07-26', '土', '土曜練', 2, 'モスバーガー', 'ファーストフード', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 14, '３割規定', 'そのままねぎしの上のモスへ。季節柄テストの話に。優３割規定というのはテストで３割とれば優がくるということらしい。
INSERT INTO public.after VALUES ('I6qjgV728V', '2008-07-26', '土', '土曜練', 1, 'ねぎし', '和食', false, false, 880, 880, 0, 0, 0, 20, 20, '渋谷', 24, '根岸', '田川さんが昔ウルトラ対策のためにやっていた２日間起きて２日間寝るという練習の話をしていた。
INSERT INTO public.after VALUES ('2iWQkYBokA', '2008-07-28', '月', '本郷練', 1, '銀杏・メトロ', '学食', false, false, 500, 500, 0, 0, 0, 4, 4, '本郷', 3, 'おまけ', '閉店間際の生協食堂に行くと、いろいろな物がおまけで多くなるらしい。今日は、冷やし坦々のトウガラシがやけに多かった。');
INSERT INTO public.after VALUES ('W4U5OKYYwN', '2008-07-30', '水', '水曜練', 1, 'ヘルスマジック ', '和食', false, false, 800, 800, 0, 0, 0, 20, 20, '渋谷', 20, '痛恨の閉店', 'いつものようにガラガラの店内。どのホノマラーのテーブルも話がはずんでいた。松田、小谷、以頭、吉江の中では小谷が一番平均点が高いことが判明した。
INSERT INTO public.after VALUES ('MayvUEKkUc', '2008-07-30', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 400, 400, 0, 0, 0, 14, 14, '渋谷', 14, '夏休み', '荒木がテスト明け記念にロッキーマウンテンを一人で食べきろうと挑んでいたが、失敗し、残りを吉岡が嬉しそうに食べていた。
INSERT INTO public.after VALUES ('OZoBOEJWEb', '2008-08-02', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 22, 22, '渋谷', 26, '1年会', 'シェーキーズで4時間くらい粘ってその後代々木組とカラオケ組に分かれたらしい。みんなココロオドル楽しい時間だったらしい。');
INSERT INTO public.after VALUES ('9u5PwcUVtx', '2008-08-02', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 400, 400, 0, 0, 0, 18, 18, '渋谷', 19, 'アンケート', 'ロッキーマウンテンを一人で食べきろう企画に挑戦している人がまたいた。合宿でやるアンケートの項目の候補を100個出してもらってそこから結局42個に絞った。乞うご期待。');
INSERT INTO public.after VALUES ('yv4W0pg9XX', '2008-08-02', '土', '一年会', 1, 'シェーキーズ', 'パスタ', false, false, 1180, 1180, 0, 0, 0, 6, 8, '渋谷', 14, '1年会', '練習前に初の1年会！みんなでわいわいと楽しいひと時を過ごした。1年生の中でバイトをしている人の数はまだ少ないことがわかった。');
INSERT INTO public.after VALUES ('fnOuGTNB9B', '2008-08-06', '水', '水曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 9, '失礼', 'スイパラに行こうとしたが待ち20分が待てなかったので卵と私へ。過去の食べ放題企画を振り返ったり数学科のイメージについて語りあったり。藤原正彦の顔は一見すると電車で痴漢をする人に見えてしまうらしい。');
INSERT INTO public.after VALUES ('Q9I75c0bQu', '2008-08-06', '水', '水曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 300, 300, 0, 0, 0, 6, 6, '渋谷', 7, '合宿', 'アンケートの最終チェックをして、スケジュールの確認をもう一回した。どんな項目を作ればあの人が入るだろうか、といろいろ考えたがなかなかいい案が思いつかなかった。「いい人」じゃどう？そんなにいい人だっけ？みたいな若干残念な感じだった。');
INSERT INTO public.after VALUES ('misetYRNfk', '2008-08-11', '月', '本郷練', 1, '美味しい屋', '中華', false, false, 1000, 1000, 0, 0, 0, 8, 8, '本郷', 8, 'オリンピック', 'オリンピックには遠泳という競技があるが、どっかの国の代表に魚が怖いと言っている選手がいる。');
INSERT INTO public.after VALUES ('sDMJQ4QjGB', '2008-08-13', '水', '水曜練', 1, 'ふらんす亭', '洋食', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 10, '大月まで走る', 'アフター録を見るとこの店はホノマラとしては４年ぶりに来たみたいだ。そう考えるとなつかしい。初めて来たのに。
INSERT INTO public.after VALUES ('eO86PFOrXf', '2008-08-13', '水', '水曜練', 2, 'M-SPO', 'バー', false, false, 1000, 1000, 0, 0, 0, 5, 5, '渋谷', 10, '日本２−４キューバ', 'めずらしい感じのアフターズ。飲みながら野球の日本対キューバを観戦した。他の話をしていると歓声をあげるべきタイミングを逸してしまう。');
INSERT INTO public.after VALUES ('wNVqfwd9s1', '2008-08-16', '土', '土曜練', 1, '屋台（NHK WONDER STREET）', '野外', false, false, 800, 800, 0, 0, 0, 7, 7, '渋谷', 7, 'コント', 'クールポコら芸人が近くの特設ステージでコントをしていた。が、ホノマラーはあんまり注目していなかったようだ。趣向を変えたアフターを楽しんだ。');
INSERT INTO public.after VALUES ('XkxKiA4NlE', '2008-08-16', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 7, '合宿のコンパ', '荒木は合宿のコンパの一部を覚えていない。9月に試験明けコンパを和館でやりたいという話になった。');
INSERT INTO public.after VALUES ('ofvLMTAtNu', '2008-08-20', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 4, 4, '渋谷', 8, 'iPhone', '例えばメールが来るとして、最初に来るのは「メールが来た」ということ以外はわからないただの通知のため、圏外の場所でその通知に気づくと不便らしい。');
INSERT INTO public.after VALUES ('xDmm4jfQvO', '2008-08-20', '水', '水曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 9, '修学旅行', '物理の先生はディズニーランドで生徒の後ろをずっとついてきた。
INSERT INTO public.after VALUES ('5xwXJq2EcA', '2008-08-23', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 20, 20, '渋谷', 20, '限定メニュー', '期間限定のメニューを頼んだ。隣のテーブルも同じものを頼もうとしたら、品切れで終わってしまっていた。タパス風スパゲッティが復活していた。
INSERT INTO public.after VALUES ('sU4k16iHtw', '2008-08-23', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 450, 450, 0, 0, 0, 19, 19, '渋谷', 20, '食べ方', 'ティラミスをどう食べればいいか、どう食べると綺麗かについてSとーさんが助言していた。
INSERT INTO public.after VALUES ('mhsPrcPBZt', '2008-08-25', '月', '本郷練', 1, 'もり川', '和食', false, false, 800, 800, 0, 0, 0, 4, 4, '本郷', 4, 'まぐろカツ、冷豚シャブポン酢定食', '雨だったのでアフターのみ。皆が示し合わせたかのように日替わりメニューのまぐろカツ、冷豚シャブポン酢定食を頼んでいた。他のお客さんにも好評のようだった。');
INSERT INTO public.after VALUES ('N7H039KELo', '2008-08-27', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 880, 880, 0, 0, 0, 20, 20, '渋谷', 19, 'スーツ', 'インターン帰りのスーツが似合っている人がいた。成人式の同窓会で昔好きだった人のアドレスを聞きそびれたとかどうとか。タイムリーな進振りの話も。');
INSERT INTO public.after VALUES ('KC0OnDzv8s', '2008-08-27', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 18, '企業リスト', '社会人&内定ホノマラー35人くらいの就職先の企業一覧表を作ってしまった。これで学部ガイダンスのようにサークルオリで卒業生の主な就職先が紹介できるようになった。
INSERT INTO public.after VALUES ('zVLCIfCUjD', '2008-08-30', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 15, 'ダメダメですね', '入店前カレ研には誰一人として客がいなかった。自分がバイトで働いている店に知り合いがやってくると緊張するらしい。グリーンカレーは辛いですよ。アフター終了後、バイト上がりの門屋が来て、みんなにお土産を配っていた。');
INSERT INTO public.after VALUES ('HXdxwvOorz', '2008-08-30', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 9, 9, '渋谷', 9, 'シンクロ', '小谷と門屋が同じタイミングで同じようにストローを動かしていた。メーリスを使うタイミングは難しい。ウインドブレーカーにサポートギアをつけたいとか背番号をつけたいと言っている人がいた。そして最後には荒木がへこんでいた。');
INSERT INTO public.after VALUES ('187lxNz9Nk', '2008-09-01', '月', '本郷練', 1, 'バンビ', '洋食', false, false, 700, 700, 0, 0, 0, 4, 4, '本郷', 7, 'toto', '浦佐totoについて話した。荒木が終始、飲みたいと繰り返していた。');
INSERT INTO public.after VALUES ('1VuSLv59av', '2008-09-03', '水', '水曜練', 1, 'GOLDRUSH', '洋食', false, false, 1000, 1000, 0, 0, 0, 16, 16, '渋谷', 15, '被害妄想', 'びるさんは偉大な先輩に習って近々O森に引っ越すらしい。インターン帰りの裕士さんが来た。自分がフライングをしなくなってからフラインガーが増えたと嘆いていた。');
INSERT INTO public.after VALUES ('F50n9eL0YW', '2008-09-03', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 12, 12, '渋谷', 14, '話の筋道', 'まっつんさんと荒木はサンマルクの場所がわからず道に迷ってしまったらしい。男型、女型の思考回路とか集団の動き方とかの話になってしまった。');
INSERT INTO public.after VALUES ('Z6y3en3Cvv', '2008-09-06', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 20, 20, '渋谷', 15, '料理', '料理を作ってもらえるって、いいよね。');
INSERT INTO public.after VALUES ('wbGjGrc3Iu', '2008-09-06', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 12, '客引き', '駒祭が近付いてきている。客引きを頑張らなくては。');
INSERT INTO public.after VALUES ('cO66zPwU91', '2008-09-08', '月', '本郷練', 1, '中央食堂', '学食', false, false, 500, 500, 0, 0, 0, 8, 8, '本郷', 8, 'ぽにょ', 'ぽにょを見たいという話がまた持ち上がったようなそうでもないような。疲れ気味なのか誰かが「覇気がない」と言われていた。');
INSERT INTO public.after VALUES ('0m41vr1NbV', '2008-09-15', '月', '皇居練', 1, 'ジョナサン', 'ファミレス', false, false, 1300, 1300, 0, 0, 0, 6, 6, '神田', 11, '食事のマナー', '例えば、欧米の食文化の中で皿を持ち上げて食べることは日本でいえばどれくらい悪いことなのか、手づかみで食べることくらい悪いことなのだろうか、という話になった。皿を持ち上げるくらいなら顔を皿にうずめて食べるほうがましなのだろうか。');
INSERT INTO public.after VALUES ('HNWj4hR0th', '2008-09-13', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 12, 12, '渋谷', 20, '4日も経ったので忘れてしまった。', '1年は平成生まれがデフォルトだみたいな他愛もない話をしてたような気がする。
INSERT INTO public.after VALUES ('xjBguhVWWJ', '2008-09-13', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 300, 300, 0, 0, 0, 12, 12, '渋谷', 18, '動物界？', '動物病院で診察してもらえる動物種の範囲について熱く語った。');
INSERT INTO public.after VALUES ('oAVG5AZAz0', '2008-09-17', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 750, 750, 0, 0, 0, 16, 16, '渋谷', 16, 'お酒', '店内には懐かしい90年代的な曲が流れていた。居酒屋でのコンパをホノマラで久しくしてないという話に。そうえいえば新勧コンパの2次会以来行っていない。やっぱり飲みたいですよねー的な雰囲気が一部で流れ出した。');
INSERT INTO public.after VALUES ('tiRNTfzFRu', '2008-09-17', '水', '水曜練', 2, '葡萄屋', '飲み', false, false, 1300, 1300, 0, 0, 0, 12, 12, '渋谷', 11, 'ひどい言われよう', 'ということで飲みに行くことにしたらたくさんついてきた。ホノマラらしいまったりした飲みに。「めんどくさい」とか「存在してはいけない」とかひどい言われようの人がいた。一方ではデート云々の話をしていた。');
INSERT INTO public.after VALUES ('wa3khQIviB', '2008-09-10', '水', '水曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 17, '遅れてきたあの人', '遅れてきたあの人は相変わらずお忙しいらしい。お疲れ様です。ビールをギャルズにお酌させていた。');
INSERT INTO public.after VALUES ('iHt9U76J52', '2008-09-10', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 450, 450, 0, 0, 0, 19, 19, '渋谷', 12, '遂に', 'あいつを完食に成功する人が現れた。これからは完食するか否かではなく、そのスピードが問題になるらしい。大部分な一部はその後カラオケに流れた。');
INSERT INTO public.after VALUES ('7mkA0dhQep', '2008-09-20', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 16, 'タイムラグ', '土曜日はかなりの高確率でタパタパに足を運んでいるような気がする。あるテーブルがすべて食べ終わったときに隣のテーブルにはまだ何も来てなかったりした。花火をしようってなった時に、一体一人何本分くらい買うのがちょうどよいのだろうか。');
INSERT INTO public.after VALUES ('0ppxneDQj3', '2008-09-20', '土', '土曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 250, 250, 0, 0, 0, 4, 4, '渋谷', 11, '自己アピール', 'サンマルクに行こうとしたが、席が埋まってて入れず。太田さんと小谷さんがそれぞれの方法で周囲の気を引こうとしていた。紙コップから芸術品ができあがっていた。最後のほうはホノマラのこれからを考える真面目な話になっていた。');
INSERT INTO public.after VALUES ('PAYhK7qrma', '2008-09-23', '火', '皇居練', 1, 'ジョナサン', 'ファミレス', false, false, 1000, 1000, 0, 0, 0, 10, 10, '神田', 8, '男らしくない', '荒木がパフェのことでまたもや男らしくないと言われていた。一方、小谷は笹尾の隣だから照れていたらしい。');
INSERT INTO public.after VALUES ('GC1C5ragbi', '2008-09-23', '火', '皇居練', 2, 'VELOVE', 'カフェ', false, false, 260, 260, 0, 0, 0, 10, 10, '神田', 6, '名前', 'はじめは前回のアフターズ同様のまじめな話からスタート。気づけば、色々な人の呼びかたについて。年老いてもあだ名で呼び続けることはありなのだろうか。');
INSERT INTO public.after VALUES ('okdsn6Z6U3', '2008-09-24', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 11, 11, '渋谷', 13, '電波', '携帯を壁にあてると電波状況がよくなるとかならないとか。電波の悪いところで自分の携帯が一本立てばそれは他の人から一本とったことになるらしい。');
INSERT INTO public.after VALUES ('Mv9fqK4dDA', '2008-09-24', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 350, 350, 0, 0, 0, 13, 13, '渋谷', 13, 'おり紙コップ', '太田さんが紙コップで芸術品を作っていた。またまた次期キャプテンをどうするか、というめんどくさい話題に。氷と解け残ったアイスコーヒーは、ビーフジャーキーの匂いがした。');
INSERT INTO public.after VALUES ('DRx23pUHv6', '2008-09-27', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 24, 24, '渋谷', 27, '首都？', 'ParisやDILI、Addis Ababaなど世界のいろんな首都が英語でプリントされている鞄を見ていたら、Panmunjomってのがあって、どこだろうという話になった。頑張って調べてみたら北朝鮮と韓国の国境辺りにある板門店という村だった。世界の首都とこの村を並べたのは何か平和のメッセージなのだろうか。');
INSERT INTO public.after VALUES ('fItwRW9MzC', '2008-09-27', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 400, 400, 0, 0, 0, 22, 22, '渋谷', 21, '器', 'どんなメールが送られてきたらへこむか、どんな言葉が一番ぐさっと来るのかなど結構きつい話をしていた気がする。「器が小さい」は確かに言われたらへこむ、という結論に。');
INSERT INTO public.after VALUES ('2hP2vnkd16', '2008-09-29', '月', '本郷練', 1, 'カフェテラス本郷', '洋食', false, false, 1100, 1100, 0, 0, 0, 4, 4, '本郷', 5, '大盛り', '荒木、田儀、秋田帰りの店長と中山(愛)が大盛りメニューに挑戦していた。田儀はさすがの早さで洗面器カツカレーを完食し、中山(愛)はさらにジョッキパフェを追加していた。末恐ろしい人材である。
INSERT INTO public.after VALUES ('fDwU4uARF8', '2008-11-05', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 16, 16, '渋谷', 14, '日本シリーズ', '巨人対西武の構図はホノマラーの中でも亀裂を生みつつあるらしい。
INSERT INTO public.after VALUES ('QAe6rm1Q4b', '2008-10-01', '水', '水曜練', 1, 'カレー研究所', 'インド料理', false, false, 950, 950, 0, 0, 0, 12, 12, '渋谷', 14, 'そんなに食べない', 'ホノマラー以外にも珍しく大盛況の店内。店員さんが大変そうだった。「ビールとキーマカレー」とオーダーしたら「ビーフカレーとキーマカレーでよろしいですか？」と聞き返された。2倍、5倍、10倍ホットなどみんながそれぞれ挑戦していた。');
INSERT INTO public.after VALUES ('3i0lh67Mr7', '2008-10-01', '水', '水曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 8, '?', '?');
INSERT INTO public.after VALUES ('cyHCjksQ1u', '2008-10-01', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 450, 450, 0, 0, 0, 14, 14, '渋谷', 15, '惜しい', '1学期の成績や2学期の時間割・逆評定などの話でもりあがっていた。彼は可もなく不可もなかったが、優は情報の1つのみで後はすべて良だったらしい。それでいて平均点がごく標準的なあたりが彼らしいところなのか。');
INSERT INTO public.after VALUES ('bA4yxwRagu', '2008-10-04', '土', '土曜練', 1, 'GOLDRUSH', '洋食', false, false, 1000, 1000, 0, 0, 0, 12, 12, '渋谷', 11, 'これはアフター録じゃない', 'アフターへの道中、道を聞かれた。「ExactlyこのBuilding」という言い方はどうなんだろう。');
INSERT INTO public.after VALUES ('BZfK2dd9ll', '2008-10-04', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 100, 100, 0, 0, 0, 10, 10, '渋谷', 8, '家族とのメール', '妹間のメール≒恋人間のメール??今回はサンプルが少なくて結論が出なかった。');
INSERT INTO public.after VALUES ('Yjvok6HTh3', '2008-10-04', '土', '平塚ゆめリレー', 1, 'なか卯', '和食', false, false, 600, 600, 0, 0, 0, 12, 12, '平塚', 12, 'ボール', 'ゆめリレー後恒例らしいなか卯でのアフター。野球のストライク・ボール・アウトのボールとは何だ？という話になった。ストライクでもそれはボールだろ！という不思議。
INSERT INTO public.after VALUES ('fvHUQkRnia', '2008-10-06', '月', '本郷練', 1, '清水', '和食', false, false, 700, 700, 0, 0, 0, 4, 4, '本郷', 4, '年齢', '平塚のリレーのゲストの中に50歳を過ぎてからサブスリー達成した人がいたらしい。ホノマラーもあきらめずに走り続ければいつかサブスリー出来るかも。');
INSERT INTO public.after VALUES ('oc1ExOQZyo', '2008-10-08', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 12, 12, '渋谷', 15, '苗字', '東森田、水玉、などの苗字も珍しいが、どこかのマックの店員さんには外国人で「ナンダナ」さんという人がいたらしい。お前、店員なんだな。ナンダナさんなんだな…
INSERT INTO public.after VALUES ('tS1pmxv6bo', '2008-10-08', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 100, 100, 0, 0, 0, 12, 12, '渋谷', 11, '結論', '途中で合流する彼女を迎えにいった組が遅いなぁと思っていたらウィンブレ関係でナイキショップに行っていたらしい。
INSERT INTO public.after VALUES ('k2TuaAYE5U', '2008-10-11', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 20, 20, '渋谷', 19, '北京', '北京でどこに観光に行くかという話になった。スペースシャトルから万里の長城も見えるらしいが、海に浮かぶイカ漁船のライトも見えるらしい。');
INSERT INTO public.after VALUES ('jahCvNveFk', '2008-10-11', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 450, 450, 0, 0, 0, 16, 16, '渋谷', 17, 'サークル記録', '一人ロッキーマウンテンのTTが行われ、大嶋が14:55というサークル記録を叩き出した。(これまでのトップは中山(愛)の20分だった)
INSERT INTO public.after VALUES ('Q28fyy51hW', '2008-10-13', '月', '本郷練', 1, '美味しい屋', '中華', false, false, 800, 800, 0, 0, 0, 4, 4, '本郷', 9, '北京', '北京に行く前に中華料理の予習をした。北京の物価はどのくらいだろうか。美味しい屋は量が多く、貧乏学生の味方だ。皆、苦戦していた。');
INSERT INTO public.after VALUES ('I1uHrxORMz', '2008-10-15', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 6, 6, '渋谷', 8, 'スポーツ', '・スポーツ観戦の意義とは？
INSERT INTO public.after VALUES ('Lb8oBxcX0K', '2008-10-15', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 3, 'あの緩やかな坂を上った後がポイント', 'なぜか２階の店の方へ人が流れてゆく中、あえて１階にとどまった人と２階に行こうとしたがなんとなく行きそびれた人の３人で、練習コースについての考察や今後の予定や抱負や野望について意見交換した。');
INSERT INTO public.after VALUES ('nkgtoLdRdp', '2008-10-15', '水', '水曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 8, '由来', 'デミグラスというのは何語由来なのだろう。某クイズ番組ならデ・ミグラス/デミ・グラス/デミグ・ラス/デミグラ・スのどれかという問題が出題されるはず。
INSERT INTO public.after VALUES ('slQTwgAejO', '2008-10-18', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 26, 'ホノマラ記録', '公園通りのタパタパは人がいっぱいだったのでハンズ前のタパタパへ。
INSERT INTO public.after VALUES ('FC8Hiuz7GN', '2008-10-18', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 500, 500, 0, 0, 0, 6, 6, '渋谷', 6, '人間の育て方', 'どこもかしこも人がいっぱいという事態にアフターズ分割という苦渋の選択をした。と思ったら20時ごろにはスウェーデンはがらがらになった。
INSERT INTO public.after VALUES ('Hm2vdzpMob', '2008-10-18', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 8, 8, '渋谷', 7, 'アウトロー', '来週の伊豆・戸田マラソンに小谷と以頭は思いを巡らせていた。それぞれにノルマを課すらしい。
INSERT INTO public.after VALUES ('WlIhdEaeQh', '2008-10-22', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 16, 16, '渋谷', 16, '駒祭準備', 'ただいま、材料の値段をリサーチ中。駒祭係指導のもと、着実に準備は進んでいます。');
INSERT INTO public.after VALUES ('9pXeF8eT4Q', '2008-10-22', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 400, 400, 0, 0, 0, 13, 13, '渋谷', 13, 'ベストというよりベター', 'という話題を横目に今週の日曜日は戸田マラソンということで小川と小谷が一位を争っていた。濱崎は小谷に賭けるらしい。');
INSERT INTO public.after VALUES ('wb72p8Oufn', '2008-10-29', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 750, 750, 0, 0, 0, 10, 10, '渋谷', 15, '過去', 'もうすぐ代替わり、という時期。自然と「あの頃のホノマラは…」的な話題に。元キャプと現キャプが次キャプに切々と思うところを語った。');
INSERT INTO public.after VALUES ('7mwhVhNw6h', '2008-10-29', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 13, 13, '渋谷', 13, '羨ましい', '今日の昼に駒場のコミプラで「本日、G省内定者の方招いて説明会ありまーす」と宣伝していたがそれはK口のことだった。熱い視線を浴びるなかいろいろ話したらしい。
INSERT INTO public.after VALUES ('YBZDQiYuiN', '2008-10-27', '月', '本郷練', 1, '長寿庵', '和食', false, false, 800, 800, 0, 0, 0, 4, 4, '本郷', 4, '先週末', '昨日、一昨日とイベント続きだったのでそれについて。戸田に出た方はお疲れ様でした。');
INSERT INTO public.after VALUES ('QRDbUdUi5X', '2008-11-01', '土', '1年会', 1, '香港厨房　渋谷店', '飲み', false, false, 2500, 2500, 0, 0, 0, 6, 6, '渋谷', 20, '役職決め', '2度目の1年会は代替わりに向けての役職決め。無事決まりました！！頑張っていきましょう！
INSERT INTO public.after VALUES ('9KijliZmg7', '2008-11-01', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 8, '?', '?');
INSERT INTO public.after VALUES ('dfhyZcsKbZ', '2008-11-01', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 14, 14, '渋谷', 14, '日程', '合宿の日程はやはり土日を絡めるものなのか。後から予定を聞いてあわせるより日程をだいぶ前から決めておく方が便利なのかも。
INSERT INTO public.after VALUES ('j5LRB2O6SZ', '2008-11-01', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 400, 400, 0, 0, 0, 16, 16, '渋谷', 27, 'かっこいい', 'バスレーンに乗用車が止まっていたので注意しに行こうと思ったら、当て逃げ車をはっている刑事の覆面パトカーだったという話。
INSERT INTO public.after VALUES ('MExyvwYrVQ', '2008-12-10', '水', '水曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 5, '年越し', '年末の予定は人それぞれ。今年は年越し企画があるのだろうか。');
INSERT INTO public.after VALUES ('acq2MlHmTX', '2008-11-05', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 350, 350, 0, 0, 0, 10, 10, '渋谷', 11, '年末のコンパ', '新しい人が来たので恒例のロッキーマウンテン。
INSERT INTO public.after VALUES ('dUsLxBckT3', '2008-11-03', '月', '皇居練', 1, 'ジョナサン', 'ファミレス', false, false, 1000, 1000, 0, 0, 0, 6, 6, '本郷', 6, '来年の合宿', '来年の合宿の展望を考えてみた。１００キロコースはあるのだろうか。');
INSERT INTO public.after VALUES ('PeFmTKHMlk', '2008-11-08', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 350, 350, 0, 0, 0, 12, 12, '渋谷', 13, '東京マラソン', '東京マラソンの抽選結果メールが昨日届いた。店内でノートPCを広げつつ「メールが届いていない。エントリーしていなかったのか？そういえばエントリー完了メールも見当たらないぞ」と嘆いている人がいた。しばらくしてから「いまだに（エントリーできてなかったことが）信じられない」と嘆きなおしていた。');
INSERT INTO public.after VALUES ('XgrfYMhpOs', '2008-11-08', '土', '土曜練', 1, 'ロコモコ', 'どんぶり', false, false, 900, 900, 0, 0, 0, 14, 14, '渋谷', 14, '海', 'これまでホノルルで行って楽しかったところ、今年どこにいけばいいか、という話をした。シュノーケリングや海底ウォークも楽しそうだが「海は見るのが楽しい」という人もいるようだ。');
INSERT INTO public.after VALUES ('a6DcJca79k', '2008-10-25', '土', '土曜練', 1, 'わたみん家', '和食（飲み）', false, false, 2400, 2400, 0, 0, 0, 28, 28, '渋谷', 28, '駒祭やら何やら', '各テーブルで盛り上がったようだ。盛り上がりすぎたところも…');
INSERT INTO public.after VALUES ('Y8mTrJfU9X', '2008-11-10', '月', '本郷練', 1, '美味しい屋', '中華', false, false, 800, 800, 0, 0, 0, 4, 4, '本郷', 4, 'お馴染み', '本郷練ではお馴染みの美味しい屋。昨日、一昨日の徽音祭にはかなりのホノマラーが足を運んだようだ。細越がときわじるこを絶賛していた。');
INSERT INTO public.after VALUES ('hbre8i7BmR', '2008-11-12', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 12, 12, '渋谷', 11, 'お互いに…', 'メニューを決めあった。自分では頼まないようなものを食べるとは、なかなか新鮮。');
INSERT INTO public.after VALUES ('qwG1fkQ6tO', '2008-11-12', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 8, '仕事とクリパ', 'まずは動くことが大事。あと途中合流が1.5人。');
INSERT INTO public.after VALUES ('3Vjjt99NBc', '2008-11-15', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 20, 20, '渋谷', 19, '食いそびれ', '注文し終わったあとに、影山は歯医者の予約をしていたのを思い出し、食べる前に店をそそくさと出て行った。
INSERT INTO public.after VALUES ('T3FrJzTpbU', '2008-11-15', '土', '土曜練', 2, 'ピンクスイートベリー', 'アイス', false, false, 460, 460, 0, 0, 0, 12, 12, '渋谷', 13, 'QUIZ', 'スウェーデン→ドトール→サンマルクとさまよってみた結果、客がいなかったこの店に。
INSERT INTO public.after VALUES ('Rw5N0Qazum', '2008-11-16', '日', '大会', 1, 'サイゼリア', 'ファミレス', true, false, 600, 600, 0, 0, 0, 8, 8, '赤羽', 9, 'インド', '初ハーフということもあり金沢は疲れていた。それをよそにインドの話をするＭつん。
INSERT INTO public.after VALUES ('LXquH4ol01', '2008-11-19', '水', '水曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 300, 300, 0, 0, 0, 8, 8, '渋谷', 10, 'チャンス', '土曜日に引き続きまたサンマルクに嫌われた。
INSERT INTO public.after VALUES ('MyVvy9Zm3G', '2008-11-22', '土', '土曜練', 1, 'スウィーツパラダイス', 'デザート', false, false, 1480, 1480, 0, 0, 0, 10, 10, '渋谷', 11, 'ウィンタースポーツ', 'おいしいものと言われて、スイパラと言うキャプテン。
INSERT INTO public.after VALUES ('BJ4IPLWNZz', '2008-11-22', '土', '土曜練', 2, 'ピンクスイートベリー', 'アイス', false, false, 460, 460, 0, 0, 0, 10, 10, '渋谷', 11, '反省', '水曜にこの店に来た時「ここもいつ潰れるんだろうね」という話をしていたが、今日来てみると「12月25日を以て閉店いたします」との張り紙が。
INSERT INTO public.after VALUES ('9C2SMi8d0O', '2009-03-04', '水', '水曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 270, 270, 0, 0, 0, 10, 10, '渋谷', 7, 'コスプレ', 'かつて一男は合宿で女装していた。２女が今度のEKIDENカーニバルで制服を着ればそれが新たな慣例になるのかもしれない。');
INSERT INTO public.after VALUES ('cT5CoG13xc', '2008-11-24', '月', '駒場祭打ち上げ＆代替わりコンパ', 1, '和民', '飲み', false, false, 3000, 3000, 0, 0, 0, 46, 46, '渋谷', 46, '中継地点', '今日をもって世代交代。2年生から1年生に役職のたすきが引き継がれました。
INSERT INTO public.after VALUES ('lFw6rjD5xR', '2008-11-24', '月', '駒場祭打ち上げ＆代替わりコンパ', 3, 'ビッグワン', 'カラオケ', false, false, 900, 900, 0, 0, 0, 13, 13, '渋谷', 13, 'オールの後', 'フローリングの大きな部屋に13人で入ってカラオケオール。翌日の授業にまじめに出た人もさぼった人もいるなかで、松田はこの日のために火曜日は有給を取っていた。');
INSERT INTO public.after VALUES ('AuwqEk7EQt', '2008-11-24', '月', '駒場祭打ち上げ＆代替わりコンパ', 2, 'すずめのおやど', '飲み', false, false, 2100, 2100, 0, 0, 0, 33, 33, '渋谷', 35, 'いきなり', '斎藤(秀)が新キャプテンの不信任決議案をしようといってきたが、結局うやむやになった。というかかなり酔っていた僕は断片的にしかこの時間帯のことは覚えていない。');
INSERT INTO public.after VALUES ('UP1znb6j62', '2008-11-26', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 750, 750, 0, 0, 0, 4, 4, '渋谷', 13, '本気', '駒場の1号館とコミプラは地下通路でつながれているらしい。
INSERT INTO public.after VALUES ('ARLojD5XXb', '2008-11-26', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 5, 5, '渋谷', 5, '数年後', '最近嫌われ続けたサンマルク。今日も人が多そうだったが少人数のおかげで何とか入れた。
INSERT INTO public.after VALUES ('jyeEhDED9q', '2008-11-29', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 12, 12, '渋谷', 12, 'スケート', '練習前のスケートは相当楽しかったらしい。今日スケートに行ったから今度女の子とスケートに行ける、といった声が聞こえてきた。');
INSERT INTO public.after VALUES ('khcgFKNgZe', '2008-11-29', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 7, 7, '渋谷', 7, 'つくば', 'つくばに応援に行くためには電車の他にシャトルバスが必要になるらしく、なかなか面倒であるようだ。店員が新人らしかった。');
INSERT INTO public.after VALUES ('R241L6ZsT7', '2008-11-29', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 350, 350, 0, 0, 0, 6, 6, '渋谷', 6, 'ＪＲ', '自分が高校の時使っていた鉄道がＪＲと知って細越が驚いていた。クリパ、忘年会、toto、ウルマラなど様々な話題が飛び交っていた。');
INSERT INTO public.after VALUES ('IKClZKaDmd', '2008-12-01', '月', '本郷練', 1, 'バンビ', '洋食', false, false, 880, 880, 0, 0, 0, 2, 2, '本郷', 2, '伊豆', 'となりのテーブルでオムライスの注文が殺到していたのを尻目に鉄板カルビとスタミナを食べた。伊豆大島行きのフェリーは東京からも出るらしい。');
INSERT INTO public.after VALUES ('r2VYZ7u68b', '2008-12-03', '水', '水曜練', 1, 'ドナ', 'パスタ', false, false, 850, 850, 0, 0, 0, 4, 4, '渋谷', 14, 'テニサー', 'ねぎしに行こうとしたら人がいっぱいで向かいのパスタ屋へ。
INSERT INTO public.after VALUES ('4HeOTux9X6', '2008-12-03', '水', '水曜練', 2, 'モスバーガー', 'ファーストフード', false, false, 300, 300, 0, 0, 0, 6, 6, '渋谷', 7, '家族', '性格から何人兄弟かを当てることはできるのか。田儀は女の兄弟がいると思われがちなのだが、実際はそうではない。
INSERT INTO public.after VALUES ('rsPmopXGwZ', '2008-12-06', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 200, 200, 0, 0, 0, 6, 6, '渋谷', 8, '略して…', 'さっきご飯を食べたばかりなのにバリューセットを頼んでいる人がいた。4人で取り分けると量が少ないと感じてしまうのか。
INSERT INTO public.after VALUES ('TMRwhB6RKD', '2008-12-10', '水', '水曜練', 1, 'カレー研究所', 'インド料理', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 9, '一人暮らし', '相変わらずがらがらの店内。今日はやたらとエビカレーが人気だった。
INSERT INTO public.after VALUES ('aA0DJa3M2X', '2008-12-10', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 400, 400, 0, 0, 0, 4, 4, '渋谷', 11, 'ブラック・クリスマス', 'クリスマスパフェを食べている人やクリスマスブレンドコーヒー(？)を飲んでいる人がいた。
INSERT INTO public.after VALUES ('CkYSa3fOeW', '2008-12-13', '土', '土曜練', 1, 'むら', 'お好み焼き', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 8, 'ふらふらと', 'せっかく人数が少ないのだからということでちょっとぶらついて普段はいかない店に入ってみた。
INSERT INTO public.after VALUES ('amTHT3SVAe', '2008-12-13', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 300, 300, 0, 0, 0, 7, 7, '渋谷', 7, 'チーズバーガーn個', 'お好み焼きやもんじゃ焼はなかなかおなかいっぱいになりにくいのか、みんなハンバーガーやチーズバーガーを頼んていた。
INSERT INTO public.after VALUES ('ALq1wLmX9q', '2008-12-15', '月', '本郷練', 1, '美味しい屋', '中華', false, false, 880, 880, 0, 0, 0, 3, 3, '本郷', 3, '食べ物', 'やはり美味しい味噌汁をつくるにはダシをしっかりとるのが望ましい。ココイチのカレーの５辛はカレ研の１０辛を軽く凌駕するらしいし、一人のときに挑戦するものではないということになった。');
INSERT INTO public.after VALUES ('1EjIfiZbpT', '2008-12-17', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 3, 3, '渋谷', 3, 'グランドピアノ', '生演奏のピアノを聴きながら、そんな雰囲気は全く似合わない飲み会の話をした。過去のコンパを振り返ったり、これからのコンパに思いを巡らせてみたり。
INSERT INTO public.after VALUES ('cBKkhgTXYA', '2008-12-17', '水', '水曜練', 1, '大戸屋', '和食', false, false, 850, 850, 0, 0, 0, 5, 5, '渋谷', 5, '525', '今日は雨がやまないということで練習なしで即アフターという姿勢を打ち出した。彼女は一人練への情熱とアフターにはもとからいく予定がなかったから練習したようだ。
INSERT INTO public.after VALUES ('tT0osOQQwQ', '2008-12-20', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1100, 1100, 0, 0, 0, 4, 4, '渋谷', 12, '電車', '鉄道にロマンを見出す男たちがたまたま同じテーブルに集って熱い議論がかわされた。首都圏のすべての駅に何らかの思い出を残したいとか、下北沢や西船橋での乗り継ぎ方によっては運賃が安くなるとか。しまいには車なんて必要ないではないかなんて話にもなった。');
INSERT INTO public.after VALUES ('E6Mos7YH1S', '2008-12-20', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 350, 350, 0, 0, 0, 7, 7, '渋谷', 7, '思い出', 'ホノルルでの出来事を語る吉江とそれを聞いて昔言ったときのことを思い出す先輩たち。とりあえずマリンスポーツはものすごく楽しかったらしい。あとパールハーバーの展示に行く日本人は少ないとか。
INSERT INTO public.after VALUES ('OJ8OXbIfqK', '2008-12-21', '日', 'クリスマスパーティー', 1, 'Koki''s', '飲み', false, false, 3000, 3000, 0, 0, 0, 45, 45, '吉祥寺', 43, 'Happy Christmas!', 'ホノマラ3回目となるクリパは忘年会との一括開催となった。田中（裕）の司会のもと歌やジャグリング、漫才といった出し物や今年1年を振り返るスライドショーが進められ、たくさんの手作りケーキやプレゼント交換などなど盛りだくさんの内容であっという間に2時間半が過ぎていった。');
INSERT INTO public.after VALUES ('7YTAL91bUJ', '2009-03-07', '土', '土曜練', 1, 'うまいもの市場  TAPA', '飲み', false, false, 3500, 3500, 0, 0, 0, 50, 50, '渋谷', 49, '追いコン', '５０人もの参加者のもとで追いコンが行われた。主役の人たちに１年生＋αからホノバムが贈られた');
INSERT INTO public.after VALUES ('ZMcuNRSsnv', '2008-12-21', '日', '忘年会', 1, 'つぼ八', '飲み', false, false, 2500, 2500, 0, 0, 0, 50, 50, '吉祥寺', 47, 'あと10日', '数時間前とはガラッと雰囲気を変えて年忘れの宴へ。新コンパ係ががんばっていた。
INSERT INTO public.after VALUES ('ljCMGm4YC7', '2008-12-21', '日', '忘年会', 2, '甘太郎', '飲み', false, false, 1300, 1300, 0, 0, 0, 15, 15, '新宿', 15, 'アウトオブガンチュウ', 'ウルマラには才能ではなく努力、練習量がものを言うようだ。クリパ、忘年会の疲れもあってか多少のお疲れモードが感じられた。');
INSERT INTO public.after VALUES ('ci9zJasmEq', '2008-12-24', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 900, 900, 0, 0, 0, 4, 4, '渋谷', 4, '箱根駅伝', 'アフターにいかない人の強い推薦で店が決まった。
INSERT INTO public.after VALUES ('ddZkHpCsUh', '2008-12-24', '水', '水曜練', 2, '東京ミッドタウン', 'その他', false, false, 0, 0, 0, 0, 0, 4, 4, '六本木', 4, '正攻法', 'せっかくクリスマスイブで人数も少ないのだからといつもとは大きく趣向の異なるアフターズに。東京ミッドタウンにイルミネーションを見に行ってきました。スターライトガーデンというのがすごくきれいでした！！
INSERT INTO public.after VALUES ('b5AebPiQOK', '2008-12-27', '土', '土曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 250, 250, 0, 0, 0, 4, 4, '渋谷', 11, '笑い納め', 'こんなに寒いのにアイスクリーム。サンマルクがあいてなかったから仕方がない。
INSERT INTO public.after VALUES ('8b2VZIdCb4', '2008-12-27', '土', '土曜練', 1, 'ねぎし', '和食', false, false, 900, 900, 0, 0, 0, 4, 4, '渋谷', 14, 'ランチ', '渋谷を歩いていけばいろんな店が見つかる。開拓精神をもっていろいろ探してみるといいようだ。');
INSERT INTO public.after VALUES ('294Xh5KhP1', '2009-01-07', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 13, 'もしマラソンをやめれば', 'その時間をいくらでも有効利用できる。免許を取りに行くこともできるし、もっと勉強することだってできる、そう力説する2人のサークル記録保持者。でもやめるには何らかのきっかけが必要らしい。
INSERT INTO public.after VALUES ('QYJZvlm1GK', '2009-01-07', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 450, 450, 0, 0, 0, 10, 10, '渋谷', 14, 'Mr.Afters', '当分は練習に来れないからと自分のことをそう称している人がいた。
INSERT INTO public.after VALUES ('AYc6dHqND8', '2009-01-01', '木', '年越しラン', 1, 'マクドナルド', 'ファーストフード', false, false, 440, 440, 0, 0, 0, 10, 10, '有明', 8, 'チキンナゲット', 'マラソン後には食欲がないという人からナゲットをもらった人は、さらに同じものを買っていた。');
INSERT INTO public.after VALUES ('uy9Fxu5d6M', '2009-01-10', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1100, 1100, 0, 0, 0, 5, 5, '渋谷', 18, 'メニュー票の裏には', '公園通りのタパタパはいっぱいで、ハンズ前のタパタパはガラガラ。タパタパにも格差が生じているのだろうか。
INSERT INTO public.after VALUES ('wmbNpeW6ma', '2009-01-11', '日', '大会', 1, 'バーミヤン', '中華', false, false, 1000, 1000, 0, 0, 0, 4, 4, '立川', 7, 'ふたりのおうじ', '一方のおうじさまは筆者を弟扱いしたがっていた。でも弟になったことのないものが簡単に弟の気持ちを理解できるはずがないので却下した。
INSERT INTO public.after VALUES ('reY17GkZEm', '2009-01-10', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 200, 200, 0, 0, 0, 9, 9, '渋谷', 10, '勢いのある人は違う', '勢いづいている人が褒められて照れまくっていた。ケガとやけどはしないようにとその人が近野に諭されていた。
INSERT INTO public.after VALUES ('8fQveImEro', '2009-01-03', '土', '土曜練', 1, '博多天神', 'ラーメン', false, false, 500, 500, 0, 0, 0, 5, 5, '御茶ノ水', 1, '一人アフター', '念願の一人アフター。にんにく、辛子高菜、紅ショウガ、ゴマが食べ放題。何をどのタイミングで入れたら最もおいしいのか考えたり考えなかったり。');
INSERT INTO public.after VALUES ('xed60WGp8o', '2009-01-14', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 900, 900, 0, 0, 0, 2, 2, '渋谷', 12, 'フード', '湯豆腐には何をかけて食べるのかという話になった。しょうゆとかポン酢とかみそとかいろいろ出てきたが、地域性でもあるのだろうか。
INSERT INTO public.after VALUES ('I7BBoIXeQN', '2009-01-14', '水', '水曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 11, 'あだ名', 'これまで自分のことをどう呼ばれていたかという話になった。小学校のときは下の名前で呼ばれていたが、それからはそうでもなくなるというのが一般的なのか。ユニークなあだ名も飛び出した。');
INSERT INTO public.after VALUES ('mdiKBtzTdo', '2009-01-17', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 23, '自己分析', '就活の話になった。濱崎が自分の経験を交えながらアドバイスをしていた。
INSERT INTO public.after VALUES ('nabZWhGWNA', '2009-01-17', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 400, 400, 0, 0, 0, 20, 20, '渋谷', 20, '旅行のお誘い', 'ギリシャ旅行に行こうと誘っている人がいた。この誘いに乗ると、243km走る大会とその大会の参加資格を得るための100kmの大会にもれなく出場できるそうだ。
INSERT INTO public.after VALUES ('h65OtL0Ejq', '2009-01-18', '日', '大会', 1, 'グランビア', '洋食', false, false, 1000, 1000, 0, 0, 0, 2, 6, '海浜幕張', 12, '社内価格', '「就職したらザバスを社内価格で買ってほしい」と田川に言うと、「いや、ドラッグストアで買った方が普通に安い」と返答した。そんなものなのか。
INSERT INTO public.after VALUES ('Xl0PGiMxqf', '2009-01-21', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 9, '僕がカキフライを食べられない理由', '食べ物の好き嫌いという話になった。とりあえずないと答えてみるものの、考えてみるといろいろと出てくるものだ。
INSERT INTO public.after VALUES ('rG3XxukeHb', '2009-01-21', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 500, 500, 0, 0, 0, 6, 6, '渋谷', 6, '仮面ライダー', 'マクドに行く前にゲーセンによって対戦型クイズゲームをやった。濱崎が1年男子相手に大人げなく3連勝した。白熱した戦いでなかなか面白かった。
INSERT INTO public.after VALUES ('AryermUiXe', '2009-01-24', '土', '土曜練', 1, 'サイゼリヤ', 'ファミレス', false, false, 600, 600, 0, 0, 0, 8, 8, '渋谷', 8, '自炊', '壮行会の前にちゃんとご飯を食べておきたいという人がアフターでは約1年ぶりのサイゼに行った。
INSERT INTO public.after VALUES ('aCMaeXaXKx', '2009-01-24', '土', '土曜練', 2, 'うまいっしょ匠', '飲み', false, false, 2500, 2500, 0, 0, 0, 29, 29, '渋谷', 29, 'まっつんさんを送る会', 'アメリカに行くのかよく分からない人の壮行会をやった。色紙を渡したり、何人かが感謝の言葉を述べたり。
INSERT INTO public.after VALUES ('YoWfBEENzg', '2009-01-24', '土', '土曜練', 3, '葡萄屋', '飲み', false, false, 1500, 1500, 0, 0, 0, 20, 20, '渋谷', 20, '初めて二人で出かける時', '「ここに残った人が本気で追い出したい人たちだと思うので、そのことはNYに行ってもきっちり覚えときます」というありがたいお言葉をもらった。
INSERT INTO public.after VALUES ('rc7RdxECiI', '2009-01-28', '水', '水曜練', 1, 'エスト', 'ボウリング', false, false, 800, 800, 0, 0, 0, 9, 9, '渋谷', 9, '雨だから', '雨が降ってきたので練習はさっさとあきらめてボウリングに行った。4チームの対抗戦で第1ゲームは中山(愛)・田儀組が、第2ゲームは濱崎・以頭組が勝利した。');
INSERT INTO public.after VALUES ('nUst8u7ZmO', '2009-01-28', '水', '水曜練', 2, '大戸屋', '和食', false, false, 900, 900, 0, 0, 0, 4, 4, '渋谷', 8, '新幹線', '静岡県には新幹線の駅がたくさんあるという話に。6個も新幹線の駅があるから日本で一番新幹線の駅がある県ではないかということになったが、実際には山形県に10個駅が存在した。それでも6個は第2位である。');
INSERT INTO public.after VALUES ('wDSzHchxMI', '2009-01-28', '水', '水曜練', 3, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 8, 8, '渋谷', 8, '2・14', '久々にサンマルクの大テーブルが空いてたので、ホノマラで占拠した。
INSERT INTO public.after VALUES ('mgt9jBvl6H', '2009-01-31', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 900, 900, 0, 0, 0, 8, 8, '渋谷', 8, '持久係数', 'フル(ハーフ)の記録をハーフ(10km)の記録で割るとその人の持久力を測る一つの数値が出る。それを持久係数という。大嶋はハーフのベストを10Kmのベストで割るとだいたい2.0くらいだった。勝手に人の記録で持久係数を測って遊んでいた。');
INSERT INTO public.after VALUES ('TJKzgbcDUR', '2009-01-31', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 350, 350, 0, 0, 0, 8, 8, '渋谷', 10, '検索エンジン', 'グーグルなどの検索エンジンで上位に来るのはウェブページのリンクの強さと関係しているらしい。
INSERT INTO public.after VALUES ('ej1gAOLm8m', '2009-02-04', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 13, 'メガネ', '久々にタパタパに行きたかったけど、パーティーをやっていたのでカレ研に。
INSERT INTO public.after VALUES ('5x3ncQHTZg', '2009-02-04', '水', '水曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 250, 250, 0, 0, 0, 8, 8, '渋谷', 8, '孤独', '一人で外食に行けるか行けないかで意見が分かれた。松屋は一人でいくものであると主張する人もいれば、一人で外食するくらいなら自分で作って食べるという人もいた。では3人分くらい作ってだれかの家で食べるというのがあってもいいのではないか。');
INSERT INTO public.after VALUES ('UjVeX4aFwc', '2009-02-07', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 6, '渋谷', 19, '駆け込み乗車', '駆け込み乗車にはたくさんのドラマがあることがわかった。ホームに設置されているドアと電車の扉の間の空間に閉じ込められる場面を目撃した人がいた。
INSERT INTO public.after VALUES ('PaAX9pvm2z', '2009-02-07', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 400, 400, 0, 0, 0, 14, 14, '渋谷', 14, 'ワイン', '最高級のブドウを使ったワインを飲むくらいならそのブドウを使ったフレッシュジュースを飲みたいものだ。高いワインは普通のワインと何が違うのだろう。
INSERT INTO public.after VALUES ('scsvunQIGi', '2009-02-11', '水', '水曜練', 2, 'エスト', 'ビリヤード', false, false, 680, 680, 0, 0, 0, 6, 6, '渋谷', 6, '美声', 'ビリヤードでは、3人3人で分かれてやった
INSERT INTO public.after VALUES ('YRyOEfmeai', '2009-02-11', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 8, '未履修問題', '一時期世間を大いににぎわせた未履修問題。ほのまらーの中にもその影響を被った人が何人かいて、ある人は自分の親に家庭科を教わることになったらしい。');
INSERT INTO public.after VALUES ('UDGNQJynbr', '2009-02-14', '土', '土曜練', 2, '甘太郎', '飲み', false, false, 2000, 2000, 0, 0, 0, 10, 10, '渋谷', 9, 'いろいろ ', 'みんなそれぞれいろいろあるようだ。飲み会での無茶振りには気をつけましょう。');
INSERT INTO public.after VALUES ('m3Fn8qqOXH', '2009-02-16', '月', '本郷練', 1, 'もり川 ', '和食', false, false, 1000, 1000, 0, 0, 0, 8, 8, '本郷', 7, '運転', '免許とって１回も運転しなくてもゴールドになるのは変な気がする。沖縄で米軍の関係者と事故を起こすと面倒くさいらしい。');
INSERT INTO public.after VALUES ('Fdt5bA51l7', '2009-02-14', '土', '土曜練', 1, 'モーモーパラダイス渋谷牧場', '和食（食べ放題）', false, false, 2500, 2500, 0, 0, 0, 15, 15, '渋谷', 13, '食べ放題', 'しゃぶしゃぶ食べ放題なのに春雨ばっか食べてる人達や肉しか食べない男達がいた。面倒くさいアクの正体は何なんだろうか。');
INSERT INTO public.after VALUES ('4nJWyHeXO0', '2009-02-18', '水', '水曜練', 1, 'ロコモコ', 'どんぶり', false, false, 900, 900, 0, 0, 0, 10, 10, '渋谷', 16, '作戦会議', '久しぶりのロコモコ。ステーキチャーハンなんてあったっけ？
INSERT INTO public.after VALUES ('F3WlmaqSTj', '2009-02-18', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 400, 400, 0, 0, 0, 4, 4, '渋谷', 11, '沖縄・箱根', '沖縄totoをどうするか決めていた。飛行機の時間ってそんなに違うものなのだろうか。
INSERT INTO public.after VALUES ('odxZ4waJMf', '2009-02-21', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 11, '水がほしい', '罰ゲームを受けることになった田儀は一緒に座った3人とじゃんけんをして、負けた人数に応じて辛さが2倍、5倍、10倍のカレーを頼むことになったが3連勝して何とか難を逃れた。
INSERT INTO public.after VALUES ('sQ0H46m61Z', '2009-02-21', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 200, 200, 0, 0, 0, 10, 10, '渋谷', 10, 'ダーツ', 'アップルパイが50円だった。
INSERT INTO public.after VALUES ('bx7kvHSa7m', '2009-02-28', '土', '土曜練', 1, 'おひつ屋', '和食', true, false, 750, 750, 0, 0, 0, 12, 12, '渋谷', 12, '今から', 'どうしても嘘をつけなかった人は、つい携帯を手渡してしまった。電話によるとアフターから箱根に行くこともできたらしい。');
INSERT INTO public.after VALUES ('TkqLHpdGLz', '2009-02-28', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 100, 100, 0, 0, 0, 11, 11, '渋谷', 9, '感染列島', '男の子たち、特に一年生はみんな感染に気をつけるべきである。一方、話題に夢中になっていた金沢は、ずっとタイミングをはずしていた。');
INSERT INTO public.after VALUES ('rukIDlUSvj', '2009-02-25', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 800, 800, 0, 0, 0, 4, 6, '渋谷', 10, '20%OFF', '20パーセントの割引券を使って、いつもはなかなか頼まないサラダとかビールとかを頼んでる人がいた。
INSERT INTO public.after VALUES ('Dl7Mpdymqa', '2009-02-25', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 300, 300, 0, 0, 0, 5, 5, '渋谷', 6, '新歓', '田中(裕)と吉江がそれぞれ自分たちの代の新歓について語り、1年生にアドバイスしていた。自分はなぜホノマラにいるか、その答えをはっきりさせておく必要があるようだ。');
INSERT INTO public.after VALUES ('NfUXNZPraa', '2009-03-04', '水', '水曜練', 1, 'サイゼリヤ', 'ファミレス', false, false, 500, 500, 0, 0, 0, 10, 10, '渋谷', 9, 'パソコン', '清が二台のパソコンを開いて一年生にホームページについて教えていた。解決の兆しが見えたかもしれない。');
INSERT INTO public.after VALUES ('Fxddern1x4', '2009-03-11', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 5, 5, '渋谷', 18, '略語選手権', '注文するときに、メニューをどれだけ略して言えるかということをやってみた。海Pとは何のことでしょう？
INSERT INTO public.after VALUES ('VH0EbiEdYs', '2009-03-11', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 200, 200, 0, 0, 0, 6, 6, '渋谷', 10, '賞金レース', '今度開催される東京マラソンの優勝者には賞金800万円が送られるらしい。では自分たちが賞金を獲得するのに最も現実的な方法は何かという話になって、クイズ番組で賞金を獲得するのが一番良いということになった。');
INSERT INTO public.after VALUES ('gxEJj15GwA', '2009-03-14', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 7, 'こっちは別なんで', '自分の名前の漢字をどう説明するかという話になった。吉江は「真」の字を真実の真ということに抵抗感があるようだ。
INSERT INTO public.after VALUES ('T0qBjB2Oc9', '2009-03-14', '土', '土曜練', 2, '甘太郎', '和食（飲み）', false, false, 2500, 2500, 0, 0, 0, 17, 17, '渋谷', 17, '追いコン・その2', '免許合宿から帰ってきた川口を追いだす飲みを開催した。相思相愛っていいですね。
INSERT INTO public.after VALUES ('BKEzCZB8qS', '2009-03-14', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 14, '婚活', '就活はもう始まっているらしい。それと並行して婚活もして成果が上がるといい。バツイチ子持ちも狙い目ではないだろうか。ストライクゾーンが広い。');
INSERT INTO public.after VALUES ('4Gv4cXpVVh', '2009-03-14', '土', '土曜練', 3, 'sweden', 'アイス', false, false, 400, 2100, 0, 0, 0, 10, 10, '渋谷', 12, 'チャンピョンは偉大', '川口が一人ロッキーのサークル記録(14分50秒)に挑戦するも、アフターに加えて飲みの後だったこともあり、半分を過ぎたあたりで無念のリタイヤ。大嶋のサークル記録が破られる日は来るのだろうか。');
INSERT INTO public.after VALUES ('AK6XHWZZAA', '2009-03-18', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 900, 900, 0, 0, 0, 9, 9, '渋谷', 9, '学校給食', '小中学校の給食でどんなものを食べていたかという話になった。各地域によって微妙に出されているものが違ってたりして面白かった。モロヘイヤうどんって食べた事ありますか？');
INSERT INTO public.after VALUES ('bxogomlgJA', '2009-03-18', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 8, 8, '渋谷', 8, '消せない記憶', '人間誰しも忘却の彼方に置き去りにしたい記憶ってあるものですね。
INSERT INTO public.after VALUES ('SCjS8McApb', '2009-03-21', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 200, 200, 0, 0, 0, 4, 4, '渋谷', 11, 'コアラのマーチ', 'コアラのマーチは食べるときにいちいちコアラがどんな表情をしているかチェックしながら食べるのが当たり前らしい。そんなバカな。
INSERT INTO public.after VALUES ('Qo6XoLF7Sb', '2009-03-21', '土', '土曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 13, 'ブラッキー', '牛カルビセットが牛カルビブラッキーセットと名前を変えていたので、何か変わっているのかと思ったら、特に変わった感じはしなかった。
INSERT INTO public.after VALUES ('KKuKV4vYP6', '2009-03-25', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 13, 'Left or Right?', 'カレーが出てきたときにご飯を左側に置くか右側に置くかという話になった。人それぞれであるといえばそれまでなのだが、同じように靴をどっちの足からはいているとかパスタを巻く向きとか、無意識のうちにずっと同じ方向にしていることって結構あるものだ。');
INSERT INTO public.after VALUES ('l3GA6XoOIQ', '2009-03-25', '水', '水曜練', 2, '甘太郎', '飲み', false, false, 2000, 2000, 0, 0, 0, 20, 20, '渋谷', 19, '快気祝い＆謝罪会見', 'Sとーさんの快気祝い兼追いコンとアメリカに行くと言いながら結局いかなくなったまっつんさんの謝罪会見を兼ねたコンパが開催された。
INSERT INTO public.after VALUES ('k5Jk3qQ5TH', '2009-03-25', '水', '水曜練', 3, 'マクドナルド', 'ファーストフード', false, false, 200, 200, 0, 0, 0, 4, 4, '渋谷', 9, '最寄りの○○まで徒歩何分', '長崎市の海岸線は日本一長いらしい。
INSERT INTO public.after VALUES ('CMEepIhngm', '2009-03-28', '土', '土曜練', 1, '代々木公園', 'その他', false, false, 500, 500, 0, 0, 0, 30, 30, '渋谷', 28, 'お花見', 'ここ数日の寒さのせいでまだ6分から7分咲きといったところであったが、今年もお花見が開催された。
INSERT INTO public.after VALUES ('FzP9D4wX5M', '2009-03-28', '土', '土曜練', 2, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 5, 5, '渋谷', 14, '完走', '話題は自然と花見中に速報が入った伊豆大島100kmに。全員無事に完走したことに皆口々にすごいと言っていた。
INSERT INTO public.after VALUES ('zXxViGYdkj', '2009-03-28', '土', '土曜練', 3, 'マクドナルド', 'ファーストフード', false, false, 100, 100, 0, 0, 0, 4, 4, '渋谷', 8, 'サッカーが見れない', '最近ご用達の文化通りマクドナルドの地下。電波がよくないのもおなじみで、日本代表代表対バーレーン戦をワンセグで見ようと悪戦苦闘している人がいた。
INSERT INTO public.after VALUES ('CaFsVJ3iax', '2009-04-01', '水', '水曜練', 2, 'エスト', 'ビリヤード', false, false, 700, 700, 0, 0, 0, 4, 4, '渋谷', 4, 'ビリヤード', 'ビリヤードに行った。１対３のチーム戦をしたりして盛り上がった。決める時はしっかり決めたい。');
INSERT INTO public.after VALUES ('joP6ef9EWE', '2009-04-08', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 400, 400, 0, 0, 0, 4, 4, '渋谷', 15, 'サバイバルゲーム', '今年の合宿の話をちょっとした。ほぼ場所が決まったかな。
INSERT INTO public.after VALUES ('bey6op8ri5', '2009-04-01', '水', '水曜練', 1, 'GOLDRUSH', '洋食', false, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 6, '普通はどっち', '山手線一周の話をした。山手一周と聞けば電車でする事を思い浮かべるのか、走ってする事を思い浮かべるのだろうか。もちろん・・・ですよね。');
INSERT INTO public.after VALUES ('N7MU4NxPki', '2009-04-04', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 9, 'ロッキーマウンテン ', '中山（愛）がロッキーサークル記録に挑むも・・・・。あとは本人に聞いてください。');
INSERT INTO public.after VALUES ('X5CWlOH9kh', '2009-04-04', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 15, 15, '渋谷', 18, '新歓', '新しく来た２人にホノマラの紹介をした。オリ合宿でホノマラを宣伝してたおかげでスムーズに紹介できた。いよいよ新入生が次回からくるはず。');
INSERT INTO public.after VALUES ('OhXTJefJTd', '2009-04-08', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 13, 13, '渋谷', 13, '渾身の・・・', '今年はアフターから登場する新入生も現れた。一方で二年生は授業の話題に。体育では人が多すぎて抽選になる中でホノマラーが多数選択した陸上はまさかの全員当選を果たしたらしい。');
INSERT INTO public.after VALUES ('wAzZGmRpVa', '2009-04-08', '水', '水曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 12, 12, '渋谷', 12, '早くも', '新入生の中にすでに50kmを走ったことがある人がいた。そこで早速100kmのお誘いがかかっていた。');
INSERT INTO public.after VALUES ('N1LJeU0fqI', '2009-04-11', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 15, 15, '渋谷', 15, '週末', '忙しいイメージがある社会人一年目、とはいえ週末をどう過ごすかは悩みの種ではあるようだ。バイトなりオフなりホノマラなり・・・相変わらずカラオケに行っていた人もいたようである。');
INSERT INTO public.after VALUES ('3tEPFX7jTM', '2009-04-11', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 5, 'またしても', '少なくとも6人分の席が空いていたが、なぜか店員に「5人までなら」と言われた。
INSERT INTO public.after VALUES ('Lk8U3pyffI', '2009-04-11', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 900, 900, 0, 0, 0, 10, 10, '渋谷', 16, '遅い', '1か所だけ注文してたら相当待たされた。あとから来た別のお客さんのほうが早く来た。ホノマラーは常連だから待たされたのだろうか。');
INSERT INTO public.after VALUES ('lNcxcOKua9', '2009-04-11', '土', '土曜練', 1, 'らんぷ亭', '和食', false, false, 650, 650, 0, 0, 0, 9, 9, '渋谷', 9, 'ホテルバイト', 'ホテルでバイトをしている人がたまたま二人いて、ホテルの裏側がどうなっているかという話になっていた。やはり高いホテルのほうがきっちりやっているようだ。');
INSERT INTO public.after VALUES ('o6xnhAfZ24', '2009-04-11', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 350, 350, 0, 0, 0, 6, 6, '渋谷', 32, 'スウェーデン占領', 'おそらく通常のアフターズでは最高の人数である。新入生がロッキーに驚く光景はもはや定番か。
INSERT INTO public.after VALUES ('3pYHcVMkme', '2009-04-15', '水', '水曜練', 1, 'すうぷ屋', '洋食', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 10, '約７年ぶり', '今回はお店を前日に何軒か予約してからアフターは分散した。すうぷ屋が使われたのは二回目。２００２年８月ぶり。値段は許容範囲。量など女子が使うのにはいいお店かもしれない。スープパスタなら量が多そうだった。
INSERT INTO public.after VALUES ('CZtV4MRleD', '2009-04-15', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 12, 'Taste Like …', '牛肉を食べると牛乳の味を感じるという人がいた。その論理が正しいとするならば、鶏肉を食べると鶏卵の味がするはず。じゃあ親子丼は一体何なんだという話になった。');
INSERT INTO public.after VALUES ('zs1CsK1eMg', '2009-04-15', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 350, 350, 0, 0, 0, 8, 8, '渋谷', 17, '妄想族', '「ねぇ、パパ〜」
INSERT INTO public.after VALUES ('WYPfpSvpZ6', '2009-04-18', '土', '土曜練', 2, '黄金の蔵', '飲み', false, false, 2000, 2000, 0, 0, 0, 4, 4, '渋谷', 4, 'くんくん', '猿田の結婚式の3次会（？）と合流。
INSERT INTO public.after VALUES ('VzFZknD8dD', '2009-04-18', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 11, '適当', '大学生はいかにてきとーでも生きていけるかということを先輩たちが力説していた。
INSERT INTO public.after VALUES ('gJWcuixFfy', '2009-04-18', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 6, 6, '渋谷', 6, '何顔？？', 'スウェーデンに入りきれなかったのでドトールへ。アイスティーを氷抜きで頼んだら、グラスの半分までしか液体が入っていなくてなえた。しかも生温かかった。
INSERT INTO public.after VALUES ('ZzZlpasQrB', '2009-04-18', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 11, 11, '渋谷', 11, 'マッサージ', '日本人は世界でも有数の肩こりの持ち主らしい。針やタイ式など様々な体験談が語られていくなか、整骨院ならまだしもマッサージ屋に入るのは気が引ける、という結論に落ち着いていた。');
INSERT INTO public.after VALUES ('F7eCPvxrqJ', '2009-04-18', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 650, 650, 0, 0, 0, 21, 21, '渋谷', 21, 'チェック＆ライン', '今日もロッキーマウンテンを感嘆しながらほおばる新入生の姿が見られた。東大寺学園＝チェックの方程式にはどうやら不備があることがわかった。');
INSERT INTO public.after VALUES ('cXrcwtBVRL', '2009-12-02', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 13, 13, '渋谷', 13, '棒磁石', '棒磁石を縦に切るとどういう現象がおきるかということについて議論した。文系と理系の基礎知識の溝を感じた。');
INSERT INTO public.after VALUES ('o0tEUjAmGq', '2009-04-18', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 8, '学食研究会', '早稲田には学食研究会というサークルがあって、全国の学食を食べてまわる。
INSERT INTO public.after VALUES ('BKwsLBWjv3', '2009-04-18', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 850, 850, 0, 0, 0, 11, 11, '渋谷', 11, 'かんぴょう巻き', '11人が横並びで1列に座り、それぞれの会話をした。
INSERT INTO public.after VALUES ('X4zZoG59LE', '2009-04-22', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 16, '鬼ごっこ', '１年生に鬼コンパの話をしたら好評だったので今年もやるつもり。駒場生は知人に鬼ごっこ中に遭遇すると気まずいと思う。見られたら恥ずかしい。');
INSERT INTO public.after VALUES ('mzLZam8TDS', '2009-04-22', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 12, 12, '渋谷', 11, 'そっくり', '丸山はサッカーの内田選手に横顔が似ていると盛り上がった。');
INSERT INTO public.after VALUES ('r5k2402GDk', '2009-04-22', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 10, '一年前と', '今日は一年生がいなかった。一年前のアフターズみたいと誰かが言っていた。
INSERT INTO public.after VALUES ('IJsbEqSMeP', '2009-04-25', '土', '土曜練', 2, '黄金の蔵', '飲み', false, false, 2000, 2000, 0, 0, 0, 40, 40, '渋谷', 38, '告白', '男性か女性のどちらから告白した方がカップルは長く続くのかという話になった。ある人が友達の話を聞いている限り女性から告白した方が長く続いていると言い張っていた。');
INSERT INTO public.after VALUES ('hIOjRTd8Fc', '2009-04-25', '土', '土曜練', 3, 'カチカチ山', '飲み', false, false, 2000, 2000, 0, 0, 0, 8, 8, '渋谷', 8, '腕相撲', '三嶋がデービッドに腕相撲で勝負をしていた。敗者はテキーラショットを一気という罰ゲームを作ったら三嶋が立て続けに何杯も飲んでいた。デービッド強い。ついでにお酒もすごく強かった。');
INSERT INTO public.after VALUES ('KKhTyEyxQv', '2009-04-22', '水', '水曜練', 1, 'おはち', '和食', false, false, 800, 800, 0, 0, 0, 4, 4, '渋谷', 4, 'アルバイト', '気がついたら4人になっていた。
INSERT INTO public.after VALUES ('E2b09iEF7H', '2009-04-25', '土', '土曜練', 1, 'Field''s', '飲み', false, false, 2800, 2800, 0, 0, 0, 85, 85, '渋谷', 82, '新歓コンパ', '練習が雨で中止になる中、今年の新歓コンパもたくさんの人が来てくれた。店のキャパは完全に超えてしまっていた。');
INSERT INTO public.after VALUES ('kN5Fqidjvf', '2009-04-27', '月', '本郷練', 1, '美味しい屋', '中華', false, false, 850, 850, 0, 0, 0, 4, 4, '本郷', 4, 'ピーマンの仲間', '正門でいろんな人に会った。小川、尾形はピーマンの仲間に苦しめられていた。');
INSERT INTO public.after VALUES ('54ZIVCU82N', '2009-04-29', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 900, 900, 0, 0, 0, 4, 4, '渋谷', 9, '牛丼', '吉野家・松屋・すき家・なか卯の牛丼の中でどれが一番好きかという話題になった。好みは人それぞれだが、結局近くにある牛丼チェーンに行くよねというところで話は落ち着いた。');
INSERT INTO public.after VALUES ('XfTgOmO4e9', '2009-04-29', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 350, 350, 0, 0, 0, 10, 10, '渋谷', 10, 'クラスメイト', 'ある人が自分のクラスメイトにいかに面白い人がたくさんいるかを力説していた。1年間付き合っているといろいろあるものだ。');
INSERT INTO public.after VALUES ('9P68QbomzV', '2009-04-29', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 15, 15, '渋谷', 15, 'まずは１０倍ホットから', '斎藤が１０倍ホットを頼むと一年生は２倍ホットを頼んでいた。石河が新フェスのチケットを先輩に売ろうとする一方で、河田のクラスは出店しないらしい。どちらがいいのだろう。五月祭のチケットは買ってあげましょう。');
INSERT INTO public.after VALUES ('a0UgLHyosv', '2009-05-05', '火', '皇居練', 1, 'ジョナサン', 'ファミレス', false, false, 1000, 1000, 0, 0, 0, 8, 8, '神田', 7, 'どっちが得か', '小谷が他のメンバーに確率の問題を出していた。すぐに答えが分かった人や問題の意味を理解出来ない人もいた。');
INSERT INTO public.after VALUES ('QHtAWAG8Sr', '2009-05-05', '火', '皇居練', 2, 'VELOCE', 'カフェ', false, false, 300, 300, 0, 0, 0, 5, 5, '神田', 4, 'アポロ計画', 'HP係が１年生にアポロ計画の宣伝をしていた。上級生の皆さんもどんどんアポロ計画に参加してください。');
INSERT INTO public.after VALUES ('T9qGqBW1wq', '2009-05-02', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 500, 500, 0, 0, 0, 10, 10, '渋谷', 16, 'ロッキーマウンテン', '今日は田川が１人ロッキーに挑戦して見事に成功。１年生や周囲の視線を独占していた。次の成功者は誰だろう。');
INSERT INTO public.after VALUES ('xzcwEkkriH', '2009-05-02', '土', '土曜練', 1, 'すうぷ屋', '洋食', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 31, 'メニュー', 'すうぷ屋のメニューはやたら長い。注文する時や受け取るときになんだったか忘れてしまうことが多い。もっと言いやすくしてほしい。');
INSERT INTO public.after VALUES ('1178Ck4eBX', '2009-05-06', '水', '水曜練', 1, 'GOLDRUSH', '洋食', false, false, 1200, 1200, 0, 0, 0, 4, 4, '渋谷', 7, 'レディース', 'レディースセットなるものが注文している女の子をみてどうしてメンズセットはないのかという話になった。映画のレディースデーだってずるいとかなんとか。');
INSERT INTO public.after VALUES ('0WmYGIMMlb', '2009-05-06', '水', '水曜練', 2, '歌広場', 'カラオケ', false, false, 600, 600, 0, 0, 0, 5, 5, '渋谷', 5, '雨だから', '雨が降っていたので練習はやめて、カラオケ。一時間なんてあっという間にすぎていった。
INSERT INTO public.after VALUES ('UQ5PIj1Vmz', '2009-05-09', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 23, '理想と現実', '１年生は大学生はもっと楽だと思ってたらしい。現実はレポートやら課題やらで大変らしい。楽をしようと思えばいくらでも出来るのはＴ大生の特権かも。');
INSERT INTO public.after VALUES ('6qQv85e85p', '2009-05-13', '水', '水曜練', 1, 'カレー研究所', 'インド料理', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 10, '英語で話そう', '留学したい。言葉のグローバル化だ。ということで、英会話に挑戦している人がいた。日本語でいいじゃん。ところで、某T大学は学内関係者の渡航に目を光らせている。二年前麻疹の時のトラウマから、ナーバスになっているらしい。');
INSERT INTO public.after VALUES ('60r2B7ThSI', '2009-05-09', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 6, 'カンデラおばさんは何処へ', '最近新しくなったメニュー表にある「ツナとトマトの田舎風スパゲッティ」は昔は「カンデラおばさんの田舎風スパゲッティ」であった。中身は全く変わっていなかったのにどうして名前だけ変わったのか店員に問い合わせてみたが、はっきりとした回答は得られなかった。');
INSERT INTO public.after VALUES ('yymu1qM3F0', '2009-05-09', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 350, 350, 0, 0, 0, 4, 4, '渋谷', 15, '忘れた', '翌日にせまった駅伝カーニバルの代走が急遽必要になって、その場にいた吉江が代走をつとめることになった。
INSERT INTO public.after VALUES ('kd65mF6ECF', '2009-05-13', '水', '水曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 16, '変化', '卵と私もメニューが変わっていた。少し種類が少なくなったような気がする。そしてサラダがつかなくなった。
INSERT INTO public.after VALUES ('0MgRBAOu10', '2009-05-16', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 24, '10000', '清はこの日が生まれてからちょうど10000日らしい。ということでみんなにおめでとうと言われていた。ちなみに7777日は21歳と112日で迎えることになる。');
INSERT INTO public.after VALUES ('UMNlFfSgyB', '2009-05-16', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 350, 350, 0, 0, 0, 4, 4, '渋谷', 18, '流行', '建築にも流行りがあると吉江が話していた。最近は一面ガラス張りの建物が流行りらしい。
INSERT INTO public.after VALUES ('nnqzOOQDN7', '2009-05-13', '水', '水曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 350, 350, 0, 0, 0, 6, 6, '渋谷', 6, '奇遇', '少人数になったので久しぶりにスウェーデン以外でアフターズ。そしたら、ブルーシールであの人がバイトをしていた。');
INSERT INTO public.after VALUES ('ceJtebb7Fv', '2009-05-18', '月', '本郷練', 1, 'もり川 ', '和食', false, false, 1000, 1000, 0, 0, 0, 5, 5, '本郷', 4, 'ホノマラは・・・', 'ホノマラは社会主義経済ではないかという話をした。資本主義にすれば変わるかもしれないという話だった。アフター後にあるホノマラーの家の前に行った。プレゼントをおいてこうとしたけど断念した。');
INSERT INTO public.after VALUES ('0KbLzKBPHj', '2009-05-20', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 350, 350, 0, 0, 0, 5, 5, '渋谷', 5, 'ポニョ', '「ポニョ、そうすけ好き〜」は間違いである。「ポニョ、映画見に来てくれたひと好き〜」が正しい。
INSERT INTO public.after VALUES ('vBKIVNZJR6', '2009-05-20', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1100, 1100, 0, 0, 0, 4, 4, '渋谷', 21, '○○マラソン', 'ウルトラマラソンがあってスーパーマラソンがないのはおかしいという話に。というかスーパーマラソンの方が響きがかっこいいのではないか。ほかにもハイパーマラソンはどうかとか適当なことを言っていた。');
INSERT INTO public.after VALUES ('l5fy9dNLa4', '2009-05-23', '土', '土曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 7, '教養課程', '大学生って意外と勉強する、というのが一年生の感想らしい。 
INSERT INTO public.after VALUES ('Ym61E0FcKP', '2009-05-22', '金', '山手線一周', 1, 'ガスト', 'ファミレス', false, false, 600, 600, 0, 0, 0, 4, 4, '田端', 14, '魔力', '食べる前は去年と比べたらそんなに眠くないかもねなんて話をしていたが、食べ終わった後は皆猛烈な勢いで眠気に襲われていた。
INSERT INTO public.after VALUES ('m2JD3jHM3k', '2009-05-27', '水', '水曜練', 1, 'スウィーツパラダイス', 'デザート', false, false, 1480, 1480, 0, 0, 0, 22, 22, '渋谷', 21, '男女差', '1年ぶりのスイパラ。食べ放題企画。
INSERT INTO public.after VALUES ('oJjyVcueS5', '2009-05-27', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 8, '一緒に', '門屋と小川が昨日、一緒に４５かいいったらしい。');
INSERT INTO public.after VALUES ('5vi8khjRDl', '2009-05-27', '水', '水曜練', 2, '歌広場', 'カラオケ', false, false, 550, 550, 0, 0, 0, 4, 4, '渋谷', 4, 'いきものがかり', '曲のPVでやってる振付をまねしたりして楽しんで、一時間くらいで解散した。');
INSERT INTO public.after VALUES ('K2g7MjGVHF', '2009-05-30', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 850, 850, 0, 0, 0, 4, 4, '渋谷', 17, 'ペット', '家(実家)でペットを飼っているかという話に。もし飼っていなくても、近所の人がペットを飼っていてよく触れ合っているならば自分の家で飼っているのとそんなに変わらないのかもしれない。');
INSERT INTO public.after VALUES ('rmp86islYw', '2009-05-30', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 350, 350, 0, 0, 0, 5, 5, '渋谷', 14, 'ランニングシューズ', 'ランニングシューズのどの部分がすり減っているかというのをそれぞれのシューズを実際に見ながら検証してみた。わりかしかかとの部分のすり減り方が目立つ人が多かったように思われる。');
INSERT INTO public.after VALUES ('w43WJQRyVp', '2009-06-01', '月', '本郷練', 1, 'いなか家', 'その他', false, false, 1000, 1000, 0, 0, 0, 6, 6, '本郷', 6, '韓国料理', '韓国料理の店に行ってみた。ビビンバでなくビビンパが正式名称らしい。韓国料理のお店らしくヨン様のポスターが張ってあった。');
INSERT INTO public.after VALUES ('0hPx4LhytD', '2009-06-03', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 4, 6, '渋谷', 16, '関連サイト', '一年生に褒められて照れている人がいた。');
INSERT INTO public.after VALUES ('kvs6UQPX7c', '2009-06-03', '水', '水曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 8, 'ランキング', '合宿のランキングの話題に。
INSERT INTO public.after VALUES ('mTQ7BX2B2e', '2009-06-06', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1100, 1100, 0, 0, 0, 4, 4, '渋谷', 16, '7分割', 'ピザがなぜか7等分されてしまい、割りを食ってしまった人がいた。');
INSERT INTO public.after VALUES ('rmB7u9f2yE', '2009-06-06', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 400, 400, 0, 0, 0, 8, 8, '渋谷', 8, '進振り', '進振りの登録時期に差し掛かり今年も2年生がどの学科に進むのか聞かれたりした。
INSERT INTO public.after VALUES ('aEvlhwUfry', '2009-06-10', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 2, 6, '渋谷', 20, '謝辞', '論文を書くときには最後に謝辞を書くらしい。それは好き勝手に書いていいみたいなので楽しそうだ。');
