INSERT INTO public.after VALUES ('FMqlFZtm7I', '2009-06-13', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 12, '条件', '小谷の弟子になるための条件について今年も語り継がれているようだ。2つの条件のうち片方だけではダメらしい。');
INSERT INTO public.after VALUES ('a7Q4EBHaAh', '2009-06-13', '土', '土曜練', 2, '葡萄屋', '飲み', false, false, 1500, 1500, 0, 0, 0, 7, 7, '渋谷', 7, 'ゲーム理論', 'カップルが別れる別れないということをゲーム理論で考えるとどうなるかという話に。結局男は女に弱いみたいなところで落ち着いていたような気がする。
INSERT INTO public.after VALUES ('fYjStoTjxP', '2009-06-14', '日', '24時間リレー', 1, '吉野家', '牛丼', false, false, 500, 500, 0, 0, 0, 7, 7, '新木場', 7, '権力争い', '駅前の吉野家が空いてたのでそこに入ることに。
INSERT INTO public.after VALUES ('mJIDh5BXYD', '2009-06-14', '日', '24時間リレー', 2, '歌広場', 'カラオケ', false, false, 500, 500, 0, 0, 0, 6, 6, '池袋', 6, 'タフですね', '流れで池袋まで移動してカラオケ。
INSERT INTO public.after VALUES ('Zn6ynILsWk', '2009-06-17', '水', '水曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 9, 9, '渋谷', 9, '2024年', 'アポロ計画は今のペースならば2024年頃に月に到着しているという。そのころ自分たちはどうしているのだろうか、と想像してみた。いいお父さんになりそうですねと言われている人がいた。');
INSERT INTO public.after VALUES ('rrfnhThGtd', '2009-06-17', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 8, 8, '渋谷', 9, 'おかわり', 'おかわりがおひつでなく茶碗でくるようになっていた。分量を聞かれて小盛と１年が答えた時の店員さんの対応が面白かった。');
INSERT INTO public.after VALUES ('ZQ7chrS316', '2009-06-24', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 16, '先取り', '今ちまたの小学生の間では鉛筆形のシャーペンを使うのが流行りらしい。しかし、今の流行りをだいぶ先取りしていた人がいた。');
INSERT INTO public.after VALUES ('zOOUTok0Gu', '2009-06-24', '水', '水曜練', 2, '白一', 'アイス', true, false, 380, 380, 0, 0, 0, 9, 9, '渋谷', 8, '悲劇', '公園通りタパタパの真下にある牛乳生アイス専門店にみんなで行くことに。みんなおいしそうに食べている中で残念なことになっている人がいた。
INSERT INTO public.after VALUES ('8FKY8bfVpq', '2009-06-20', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 16, '牛丼+α', '牛丼チェーンでの頼み方にそれぞれこだわりが見られた。絶対につゆ抜きで頼むとか、豚汁をつけるとか。松屋の豚汁はおいしい。');
INSERT INTO public.after VALUES ('WmVPl0fB6Y', '2009-06-20', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 350, 350, 0, 0, 0, 6, 6, '渋谷', 10, '本郷⇒駒場', '安田講堂から駒場の1号館の前まで行くのは電車の方が速いのか走った方が速いのかということで議論になった。バスも巻き込んで話をしていたが結局実際にやってみればいいではないかということに。');
INSERT INTO public.after VALUES ('EaA30v2ov8', '2009-06-20', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 8, 'おさらい', '鬼ごっこコンパのクイズのおさらいをかるくして、夏合宿のクイズのヒントをつぶやいてみた。');
INSERT INTO public.after VALUES ('JfQ8o5qxD3', '2009-06-27', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 350, 350, 0, 0, 0, 3, 3, '渋谷', 3, 'アフターズ', '最近アフターズに来る人が少ないという話になって、過去のアフター録を確認したりしてみた。みんな忙しいのだろうか。');
INSERT INTO public.after VALUES ('BBmpSCQBCb', '2009-06-27', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 20, '寿司', '好きな寿司ネタは何かという話題に。デービッドはサーモン、ダビちゃんはイカが好きだと言っていた。
INSERT INTO public.after VALUES ('XDv5tbvlWy', '2009-07-01', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 900, 900, 0, 0, 0, 4, 7, '渋谷', 15, '忙しいから', '人から誘われて断るときによくつかわれるこの言葉。行きたくないならそんな遠回しに言うんじゃなくてもっとはっきり言ってほしいといった話になった。');
INSERT INTO public.after VALUES ('KdZkgfo89C', '2010-01-30', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 6, 'ご近所さん', '引っ越してみたら実は徒歩一分のところにほのまらーが住んでいることが発覚してびっくりしていた。');
INSERT INTO public.after VALUES ('Twj3SOs1A6', '2009-07-01', '水', '水曜練', 2, 'モスバーガー', 'ファーストフード', false, false, 300, 300, 0, 0, 0, 5, 5, '渋谷', 5, 'プロ野球', 'たまにwikipediaで野球選手の名前を調べたくなる。そうしたら、リンク先にあるほかの選手の記事までついつい見てしまうという話になった。');
INSERT INTO public.after VALUES ('GNvXJTWtug', '2009-07-04', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 900, 900, 0, 0, 0, 8, 8, '渋谷', 10, '備え付け', '備え付けの物は前の客がイタズラをしているかもしれない。なので持参するのがいいという話をした。');
INSERT INTO public.after VALUES ('bnnBjjPDC4', '2009-07-04', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 8, '通学', 'つくばエクスプレスで毎日、通学している、という1年生がいた。大学から徒歩2分のところに住んでるという1年生もいた。');
INSERT INTO public.after VALUES ('tGcrUoC2R0', '2009-07-06', '月', '本郷練', 1, 'もり川 ', '和食', false, false, 850, 850, 0, 0, 0, 4, 4, '本郷', 4, '小川＝ウサギ', '隣の人の話は結構面白い。');
INSERT INTO public.after VALUES ('yoWK7nmTN8', '2009-07-08', '水', '水曜練', 1, 'ロコモコ', 'どんぶり', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 12, '成人式', '今年度初ロコモコ。一年生にとっては当然初めてだったが、ステーキチャーハンを頼んでいる人の方が多かったり。
INSERT INTO public.after VALUES ('h47t0im22Q', '2009-07-08', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 350, 350, 0, 0, 0, 3, 3, '渋谷', 3, 'まったり', 'そろそろテストが近づいていたからだろうか、少人数のアフターズになった。
INSERT INTO public.after VALUES ('IVIi5kxciy', '2009-07-04', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 4, 6, '渋谷', 10, '決意', '長期的スパンに立って物事を考えることが大事だという話になった。これから10年後20年後自分はどうしているのかを話さなければならない時が来るらしい。');
INSERT INTO public.after VALUES ('Z67yhFJMPW', '2009-07-04', '土', '土曜練', 2, '花のれん', '飲み', false, false, 1200, 1200, 0, 0, 0, 13, 13, '渋谷', 13, '誕生日飲み', '田儀の誕生日だったので祝い飲みをした。ホノマラのある日に誕生日とはラッキーだと思った。');
INSERT INTO public.after VALUES ('rx38j2kqye', '2009-07-11', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 7, 7, '渋谷', 7, '辛い・・・', 'やはりカレーと聞くと１０辛に挑戦したくなるらしい。各々が美味しく食べる中走っているとき並みに汗をかきながら食べるホノマラーの姿が見受けられた。');
INSERT INTO public.after VALUES ('dDFMKj2ieI', '2009-07-11', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 900, 900, 0, 0, 0, 4, 4, '渋谷', 11, '和菓子', 'おひつやのメニューに載っている和のスイーツを見ていたら無性に食べたくなった。アフターズに使っている店でそんなものが食べられるところがあったか思いめぐらせてみたが意外とない。和菓子はやはり敷居が高いのだろうか。');
INSERT INTO public.after VALUES ('pouRmOkodq', '2009-07-11', '土', '土曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 350, 350, 0, 0, 0, 8, 8, '渋谷', 9, 'ホノマラシアター', '最近ブルーシールでも大きなパフェが発売されたようだ。今度試してみよう。
INSERT INTO public.after VALUES ('j30LDQ65GA', '2009-07-15', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 10, '結婚願望', '何歳ぐらいに結婚したいと思っているか、という話題になった。ある人は24には結婚したいと思っていたが、そのためにはもうフィアンセに出会っていなければならない計算らしく、その夢は叶えられそうにないようだ。');
INSERT INTO public.after VALUES ('KKNAb5Zfr0', '2009-07-15', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 7, 7, '渋谷', 7, 'なぐさめあう二人', '小学校の頃はモテていたということで意気投合している二人がいた。淡い思い出がたくさん語られていた。
INSERT INTO public.after VALUES ('LpLfbR4v1x', '2009-09-05', '土', '土曜練', 2, '細越家', 'その他', false, false, 0, 0, 0, 0, 0, 3, 3, '赤羽', 3, 'スポーツ', 'サッカーオランダ戦の観戦をしながら談笑した。パソコンを設定しようとしたら細越がパスワードをなくしてできなくなっていた。皆さん管理はしっかりしましょう');
INSERT INTO public.after VALUES ('OmajZTKRnV', '2009-07-18', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 450, 450, 0, 0, 0, 5, 5, '渋谷', 15, 'ムツゴロウ', '長崎にたくさん生息している(!?)らしいムツゴロウの絵を清が描いていたが、それがムツゴロウだとわかった人は少なかった。ムツゴロウといったらあの人しか思い浮かばない。
INSERT INTO public.after VALUES ('YRW9hpTOjh', '2009-07-18', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 23, 'テスト', 'テストがせまってきたからか、話題も自ずとテストのことになってきた。以前と違って理系の方が少なくて話題についていけないことが増えてきているような気がする。');
INSERT INTO public.after VALUES ('z48qQjxVQ2', '2009-07-20', '月', '皇居練', 1, '甘太郎', '焼肉', false, false, 2000, 2000, 0, 0, 0, 7, 7, '神田', 7, '猫舌理論', '長男や長女(一番上の兄弟)は猫舌になる可能性が高いのではないかという仮説を唱えている人がいた。なんでも、一番上は親にちゃんと食べ物を冷ましてもらってから食べさせてもらっているからだそうだ。
INSERT INTO public.after VALUES ('fz960xQdAN', '2009-07-22', '水', '水曜練', 1, 'GOLDRUSH', '洋食', false, false, 1200, 1200, 0, 0, 0, 5, 5, '渋谷', 10, 'ジャガイモ', '付け合わせのジャガイモを皮ごと食べるのか皮は残すのかでちょっとした論争になった。確かに皮ごと食べると考えれば楽に食べられるのだが。');
INSERT INTO public.after VALUES ('FAOVDMimzv', '2009-07-22', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 350, 350, 0, 0, 0, 7, 7, '渋谷', 7, '寝坊ってこわいですね', 'アンパンマンを実写化するという話になった。実写化しても着ぐるみを着てたらということは考えずに自由に想像を膨らませたりした。アンパンマンは佐藤隆太らしい。');
INSERT INTO public.after VALUES ('79Tp8nTrm8', '2009-07-25', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 5, 5, '渋谷', 20, 'ビールデー', '暑かったからか、やたらとビールを頼む人が多かった。280円で飲めるのは確かにお得な感じがする。
INSERT INTO public.after VALUES ('3JxLKhOU37', '2009-07-25', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 4, 7, 0, 0, 0, 400, 400, '渋谷', 16, 'TOKYO WALKER', '珍しく店内に置いてあるTOKYO WALKERを手に取りながら話をしたりした。
INSERT INTO public.after VALUES ('PyYbHPuS2D', '2009-07-29', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 900, 900, 0, 0, 0, 4, 4, '渋谷', 13, 'スーツ', 'サラリーマンが二人並ぶのは嫌だとか言って座席を微妙に入れ替えている人がいた。
INSERT INTO public.after VALUES ('JfJT8y3g3C', '2009-07-29', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 350, 350, 0, 0, 0, 7, 7, '渋谷', 8, 'ランキング', '合宿で行うランキングの項目の絞り込みをやった。今年はどのようになるのか予想しながら決めていくのは楽しかった。');
INSERT INTO public.after VALUES ('2AnyM088Hn', '2009-08-01', '土', '土曜練', 1, 'ロコモコ', 'どんぶり', false, false, 900, 900, 0, 0, 0, 4, 4, '渋谷', 13, '一年会', '昨日某所で一年会が開催されていたらしい。でもすごく楽しかったと言っていたのは一年生ではなかった。');
INSERT INTO public.after VALUES ('YcrPWSlKjI', '2009-08-01', '土', '土曜練', 1, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 8, 8, '渋谷', 8, 'コース', '合宿で設定されているコースよりもっと走りたい人がどうするのかという話になった。あの人は合宿中に150ｋｍ走りたいらしい。
INSERT INTO public.after VALUES ('j37vyGlQKh', '2009-08-05', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 17, '記憶', '過去の合宿がどうだったかとかという話を交えながら今年の合宿がどうなるのか思いをはせた。年を追うごとに10人ずつ増えてるから来年は80人になるのかとか、過去の合宿の写真を見れば先輩のちょっと今のイメージと違う姿が見れるといった話もした。');
INSERT INTO public.after VALUES ('TLgwJmd4nL', '2009-08-05', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 400, 400, 0, 0, 0, 15, 15, '渋谷', 16, '遅刻', '合宿に大幅な遅刻をした場合、レンタカーに収容されることになっているが、運転手をはじめ車に乗るメンバーはもう何人か車に乗ってくることを想定しているようだ。
INSERT INTO public.after VALUES ('rNyDtKIKlM', '2009-08-12', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 900, 900, 0, 0, 0, 4, 4, '渋谷', 11, 'すき焼き', '定番の関東風と関西風の味の違いについて。
INSERT INTO public.after VALUES ('Gq0E3k7szM', '2009-08-12', '水', '水曜練', 2, 'エスト', 'ボウリング', false, false, 1300, 1300, 0, 0, 0, 6, 6, '渋谷', 6, '大人げない', '人数も少なかったのでボーリングに行くことに。
INSERT INTO public.after VALUES ('7GARzBEAoP', '2009-08-19', '水', '水曜練', 1, 'サイゼリヤ', 'ファミレス', false, false, 500, 500, 0, 0, 0, 10, 10, '渋谷', 13, '飲み放題', 'みんなで飲み放題をしながらワイワイした。やすくすんでよかった。分かってると思いますがアルコールの飲み放題ではないですよ。');
INSERT INTO public.after VALUES ('Gqf8yauSld', '2009-08-19', '水', '水曜練', 2, '歌広場', 'カラオケ', false, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 4, 'のど自慢', 'のど自慢に出た人に本番で歌った曲を歌ってももらった。１人４役やってる人もいた。翌日院試なのに歌ってる人もいた。切ない系の曲ばっかり歌ってる人もいた。誰のことかは想像しましょう。');
INSERT INTO public.after VALUES ('fRoMzIyySz', '2009-08-15', '土', '土曜練', 2, 'カチカチ山', '飲み', false, false, 2000, 2000, 0, 0, 0, 6, 6, '渋谷', 6, '共有', '飲みながら合宿の反省や情報共有したり某先輩の武勇伝を聞いた。こういうアフターズもいいですね。');
INSERT INTO public.after VALUES ('kvi8CGHQnl', '2009-08-22', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 10, '行けなかった人に', '合宿のお話をした。でもなぜかいろいろ知っていた。本人は実は行ってたとか言ってたけど会った記憶がないｗ');
INSERT INTO public.after VALUES ('DWmnRPni5r', '2009-08-22', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 11, 'オクトーバラン', '携帯を水没させてしまった人がオークトーバランのメーリスを回してた。みんなの返信が早くて驚いた。でもメーリスをちゃんと読まずに返信してる人がいた。たとえつまらなくても最後までちゃんと読みましょう。');
INSERT INTO public.after VALUES ('x2xeUuHoX9', '2009-08-15', '土', '土曜練', 1, 'カレー研究所', '洋食', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 12, '新星あらわる ', '河田が10倍ホットを平気そうな顔で食べていた。');
INSERT INTO public.after VALUES ('E4KV598LqD', '2009-08-26', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 10, 10, '渋谷', 7, '鬼ごっこ', 'ディズニーランドで鬼ごっこしたら楽しいかもという話をした。鬼はミッキーの耳をつけたら分かりやすいということに。でもお金を払って鬼ごっこする価値はあるかということだった。');
INSERT INTO public.after VALUES ('Er66iApSuK', '2009-08-26', '水', '水曜練', 2, 'カチカチ山', '飲み', false, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 4, '真面目な話', '飲みキャラが集まったわけではないが飲みに行った。４年生がどんな物を作りたいかを語りそれについて議論したり、今後の展望についてそれぞれ話した。もちろんずっと真面目な話をしていたわけではない。');
INSERT INTO public.after VALUES ('PHXtBLNTPL', '2009-08-29', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 20, '２０世紀少年', '今日公開の２０世紀少年について話した。テレビでは映画と微妙に違ったらしい。友達の正体は誰なのだろうか。');
INSERT INTO public.after VALUES ('knfj0ec3NV', '2009-08-29', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 24, '副キャプテン', '歴代の副キャプテン同士でホノマラの過去・現在・未来について語った。');
INSERT INTO public.after VALUES ('xpWV5EF22R', '2009-09-02', '水', '水曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 14, '停学', '高校のテストでカンニングがばれたら停学になるという話をしたら意外だと言われた。そこから、高校時代にどんなことをしたやつが停学になっていたかという話になった。');
INSERT INTO public.after VALUES ('2uKNWtRLYe', '2009-09-02', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 350, 350, 0, 0, 0, 11, 11, '渋谷', 11, '○○系男子', 'ちまたではすっかり草食系男子であったり肉食系男子といった言葉がすっかり浸透しているが、自分のことをドＭ系男子と言っている人がここホノマラにいた。しかしながら、今日この場で最もいじられていたのはその彼ではなかった。');
INSERT INTO public.after VALUES ('nNFyzcnHaR', '2009-09-09', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 20, 'カレールー', '家でカレーを作るときにどのルーを使うかという話になった。こくまろ＋αが人気だった。二種類のルーを混ぜて作るのは当たり前のようだ。');
INSERT INTO public.after VALUES ('VUSWBQp4SZ', '2009-09-09', '水', '水曜練', 3, 'エスト', 'ボウリング', false, false, 1300, 1300, 0, 0, 0, 4, 4, '渋谷', 4, 'サンダルで', 'ずいぶんと軽いノリでボーリングに行った。皆さん、ボーリングをするときはボーリングシューズを忘れないようにしましょう!!');
INSERT INTO public.after VALUES ('6PaCmVIvLG', '2009-09-09', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 450, 450, 0, 0, 0, 10, 10, '渋谷', 16, '駒祭', '駒祭係を中心にしてちょっと打ち合わせをした。今年もはしまきで行く模様。
INSERT INTO public.after VALUES ('PyaoKmgIZM', '2009-09-12', '土', '土曜練', 1, 'ジョナサン', 'ファミレス', false, false, 1200, 1200, 0, 0, 0, 5, 5, '神田', 11, 'グランドメニュー', 'メニューを見ているだけで食べなくても幸せになれるよねという話をした。特にファミレスのメニューは全品写真付きで掲載されているので魅力的だ。');
INSERT INTO public.after VALUES ('DMojJ30uIm', '2009-09-12', '土', '土曜練', 2, 'VELOCE', 'カフェ', false, false, 300, 300, 0, 0, 0, 4, 4, '神田', 12, '縁起', '大嶋が帰省中にボーリングで自己2番目のスコアを自己ベストを達成したボーリング場で出したらしい。
INSERT INTO public.after VALUES ('ql7sPbvikq', '2009-09-18', '金', '皇居練', 1, 'カラオケ館', 'カラオケ', false, false, 500, 500, 0, 0, 0, 5, 5, '神田', 5, 'いい人', 'カラオケでその人の性格がわかると豪語する一年生がいた。');
INSERT INTO public.after VALUES ('qJQagAZFIv', '2009-09-18', '金', '皇居練', 2, 'ジョナサン', 'ファミレス', false, false, 1000, 1000, 0, 0, 0, 4, 4, '神田', 4, 'マラソン', 'アフター後に夕食というシュールな展開。伊豆・戸田マラソン興味がある東大関係者はぜひ出てみよう！！');
INSERT INTO public.after VALUES ('l1XV0jPPQ3', '2009-09-19', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 7, '来週は・・・', '来週はいよいよ秋田１００キロということで意気込みをきいたり、アドバイスが飛び交ったりしていた。');
INSERT INTO public.after VALUES ('YnhGFYX85y', '2009-09-19', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 15, '計画', 'SWにどこに行くか計画を練っている人たちがいた。車と宿以外は何も決まってなかったらしい。');
INSERT INTO public.after VALUES ('0NNfyEd1b4', '2009-09-23', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 450, 450, 0, 0, 0, 4, 6, '渋谷', 14, 'レパートリー', 'やっぱりレパートリーが豊富なのはいいことだ。細越は40種類持っているらしい。');
INSERT INTO public.after VALUES ('Xi4no3nf0w', '2009-09-26', '土', '土曜練', 1, 'Legato', '洋食', false, false, 2000, 2000, 0, 0, 0, 7, 7, '渋谷', 7, '少人数', '少人数アフターなので、行ったことのない店へ。渋谷の１５階、東京の夜景をみながら高そうなお店。
INSERT INTO public.after VALUES ('mTTqKxKO5C', '2009-09-26', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 100, 100, 0, 0, 0, 7, 7, '渋谷', 8, 'おすそ分け', 'なんとアフター二時間に対し、アフターズ一時間強という逆転ぶり。
INSERT INTO public.after VALUES ('kaf9CTMQo4', '2009-09-27', '日', '24時間リレー', 1, 'なか卯', '牛丼', true, false, 500, 500, 0, 0, 0, 4, 4, '平塚', 9, '時計', 'やはり平塚後のなか卯は定番。
INSERT INTO public.after VALUES ('USDTj9C2dN', '2009-12-02', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 200, 200, 0, 0, 0, 6, 6, '渋谷', 6, '子供の名前', '自分にもし子供ができたら、どんな名前にするか。ちなみに私は、年老いて病院で呼ばれても恥ずかしくない名前にします。（○一郎、×亜十夢）');
INSERT INTO public.after VALUES ('xOYDozrOpV', '2009-09-30', '水', '水曜練', 2, 'エスト', 'ボウリング', false, false, 1300, 1300, 0, 0, 0, 6, 6, '渋谷', 6, 'またもや', '6人で来て4人でボーリングをするというところから、いつの間にか第一ゲームの最下位は見学していた細井と第二ゲームは交代するというよく分からない展開になっていた。
INSERT INTO public.after VALUES ('h7wwsFuFTR', '2009-09-23', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 22, '自己流の東京マラソン', '自分で東京マラソンのコースを作ってみたら面白そうだねという話をした。42kmでどれだけ東京の名所を回れるか競ってみたら面白そうとも。');
INSERT INTO public.after VALUES ('1A62esaFeP', '2009-09-30', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 13, 'オクトーバーラン', '今年のTシャツのデザインは一新され昨年までとは大きく違うものとなっていた。
INSERT INTO public.after VALUES ('stOystphS3', '2009-10-03', '土', '土曜練', 1, 'うまいもの市場  TAPA', '飲み', false, false, 2700, 2700, 0, 0, 0, 33, 33, '渋谷', 32, '渇望', '久しぶりの居酒屋コンパ。
INSERT INTO public.after VALUES ('JQBiyLl0rs', '2009-10-03', '土', '土曜練', 2, '葡萄屋', '飲み', false, false, 1200, 1200, 0, 0, 0, 14, 14, '渋谷', 13, '五輪', '先日のIOC総会で2016年夏季オリンピックはリオデジャネイロで開催されることが決定されたが、外務省では東京が開催都市に選ばれたことを想定して、祝賀会の準備を行っていたらしい。何んともむなしい話である。');
INSERT INTO public.after VALUES ('PkEpnW1Gte', '2009-10-07', '水', '水曜練', 1, 'かにチャーハンの店', '中華', false, false, 700, 700, 0, 0, 0, 6, 6, '渋谷', 6, '本場の味', 'カレーはもともとインド料理だけど、洋食ぽいよねという話をした。本場のインドカレーは案外日本人の口に合わなかったりするらしい。');
INSERT INTO public.after VALUES ('dG3PdTdxOi', '2009-10-07', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 5, 5, '渋谷', 5, 'ホラー', 'あぁ、ミッキーマウスを思い出すと今日も眠れないだろう。');
INSERT INTO public.after VALUES ('yje3ePe1zR', '2009-10-10', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 500, 500, 0, 0, 0, 10, 10, '渋谷', 22, '文字サイズ', 'F君の携帯の画面に表示される文字サイズが小さいと周りから責められて？いた。たぶん慣れればどんな大きさでもどうっていうこともないのだろうとは思うのだが、それでも常識的な範囲というのがやはりあるということだろう。');
INSERT INTO public.after VALUES ('dnhWLMwPoj', '2009-10-10', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 6, '渋谷', 14, '理工学部', '同じトピックについて議論したとしても、理学部の人のアプローチと工学部の人のアプロ-チは全然異なるものである。「なぜそうなるのか」を追う理学部と「どうすれば解決できるのか」を探す工学部との違いが話している中で鮮明に表れていた。');
INSERT INTO public.after VALUES ('n7f5LU2Giu', '2009-10-14', '水', '水曜練', 1, 'カレー研究所', 'インド料理', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 12, 'ゲリラ雨', 'ちょうど出ようとしたらものすごい雨に見舞われた。誰のせいだろうか。');
INSERT INTO public.after VALUES ('ZMKNjXID0b', '2009-10-14', '水', '水曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 7, 7, '渋谷', 7, 'クリパ', '気が早いような気もするがクリパの話題に。某人が昨年緊張のしすぎで自分の出番が来るまでのことを全く覚えていなかったとか、今年もあの人が司会をするのかといった話をした。');
INSERT INTO public.after VALUES ('rI0MFHDzcc', '2009-10-14', '水', '水曜練', 1, 'sweden', 'アイス', false, false, 400, 400, 0, 0, 0, 4, 4, '渋谷', 11, '俺様的な彼女', '雷雨が降っていてもここは雨宿りの場所としてはあまり使われていなかったようだ。
INSERT INTO public.after VALUES ('WPVpIZSgSL', '2009-10-14', '水', '水曜練', 3, 'マクドナルド', 'ファーストフード', false, false, 120, 120, 0, 0, 0, 4, 4, '渋谷', 4, '今後の目標', 'もう学校は始まっているのになぜかさらに1時間半ほどしゃべった。内容はもっぱらランニングについてだったような気がする。');
INSERT INTO public.after VALUES ('R9ilvmkQhE', '2009-10-24', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 10, 10, '渋谷', 14, '新メニュー', 'メニューが変わっていた。カレー研に行かなくてもカレーが食べられてしまうとは。誰かがおひつ屋に行きまくるしかないと言っていた。');
INSERT INTO public.after VALUES ('xTTERmKarI', '2009-10-24', '土', '土曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 17, '8人と思いきや', '８人でカウンターでまったりアフターズをしていると、別件で飲んでたらしい人たちが合流した。夜遅くまで話したらしい。');
INSERT INTO public.after VALUES ('0fe4Ty5ZDm', '2009-10-28', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 10, 10, '渋谷', 18, '前売り券のさばき方', '５枚の前売り券をどうやってさばくかを考えた。自宅生なら家族、東大生ならクラスメートに売れば良さそうだが、自宅じゃないお茶大生は売るあてが難しいようだ。自分で食べるのがよいのではないか。');
INSERT INTO public.after VALUES ('RFytRnYSff', '2009-10-28', '水', '水曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 10, '大学駅伝等', '駅伝好きの人達で大学駅伝の展望を雑誌をよみながら熱く語り、もう一方では密度の高い議論が行われていたようだ。');
INSERT INTO public.after VALUES ('36WWdwR5kT', '2009-11-04', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 11, 'ホームページ', 'ホノマラのサイトを保護者が見ていることがよくあるようだ。大会とかをチェックされているらしい。アフター録にも余計なことは書けないですね。');
INSERT INTO public.after VALUES ('AREVWnDnEm', '2009-11-04', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 300, 300, 0, 0, 0, 6, 6, '渋谷', 6, 'クリパ', '去年のクリパの話をしたりした。今年も楽しみですね。');
INSERT INTO public.after VALUES ('DG4AjRBtHj', '2009-11-14', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 500, 500, 0, 0, 0, 10, 10, '渋谷', 14, '映画', '面白そうな映画は何かについて話した。風が強く吹いているはホノマラーにとっておすすめ。映画より原作の方が良いかも。');
INSERT INTO public.after VALUES ('LOgiPbYHxF', '2009-11-14', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 17, 'ホームページ', 'ホームページの修正についてパソコンに詳しい２人とホームページ係とパソコンが理解できない人で議論した。もっと見やすくなるらしい。');
INSERT INTO public.after VALUES ('HveyICrvx9', '2009-11-11', '水', '水曜練', 1, '？', 'その他', true, false, 800, 800, 0, 0, 0, 6, 6, '渋谷', 6, 'スポーツ', 'うどんの店に行った。一番ルールが単純なスポーツは何かという話をした。野球でないことは確かだと思う。
INSERT INTO public.after VALUES ('ZFCFnj9c1i', '2009-11-11', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 300, 300, 0, 0, 0, 5, 5, '渋谷', 5, '推理', 'モスバーガー満席のためマックへ。答えがわからない問題を推理するのは楽しいが、結局結論が出なければ心残りが倍増するということがわかった。');
INSERT INTO public.after VALUES ('YPCazCgFyd', '2009-11-18', '水', '水曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 8, '実行委員', '学祭の実行委員をやるとサークルをやれなくなるほど忙しい人もいるらしい。でもやりがいは凄くあるということだった。');
INSERT INTO public.after VALUES ('MEP5aLFCYN', '2009-11-21', '土', '土曜練', 2, 'CAF&Eacute; DANMARK', 'カフェ', false, false, 400, 400, 0, 0, 0, 3, 3, '渋谷', 3, '追憶の先', '二階席から渋谷の路上を観察すると黒タイツの集団が進行していた。一年間の思い出も意外と多いものである。');
INSERT INTO public.after VALUES ('hmmTGzpMlH', '2009-11-21', '土', '土曜練', 1, 'GOLDRUSH', '洋食', false, false, 1200, 1200, 0, 0, 0, 5, 5, '渋谷', 5, '要するに', '少人数だけあって久しぶりに入ったゴールドラッシュは煙がすごかった。店員のセンスある切り返しに全員が爆笑した。');
INSERT INTO public.after VALUES ('lxINcJ7Wtx', '2009-11-25', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 14, 14, '渋谷', 14, '新メニュー', 'ここでも代替わりを印象づける出来事があった。一年生が多かったことは何より、メニューがかわっていた。某氏の定番、海の幸ピザは残留していた。');
INSERT INTO public.after VALUES ('cOI7QnmFVJ', '2009-11-25', '水', '水曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 300, 500, 0, 0, 0, 4, 4, '渋谷', 4, '代替わり総括', '代替わりコンパを総括、反省した。この反省をクリパに生かすことを誓い、クリパへの期待を膨らませた。');
INSERT INTO public.after VALUES ('Mi3475YSL9', '2009-11-28', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 900, 900, 0, 0, 0, 11, 11, '渋谷', 11, 'ipod', '「ipod,iphone」と音楽や携帯業界へ次々に新製品が開発されるが、今度はどの業界に進出するのだろうか？？　');
INSERT INTO public.after VALUES ('gH8x54BATJ', '2009-11-28', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 300, 700, 0, 0, 0, 7, 7, '渋谷', 7, '筋トレ', '筋トレし過ぎて走りが遅くなったという人がいた。
INSERT INTO public.after VALUES ('5PXrwq16ND', '2009-12-06', '日', '駅伝大会', 1, 'はーべすと', '和食（食べ放題）', false, false, 2000, 2000, 0, 0, 0, 14, 14, '立川', 14, '細かすぎて伝わらないものまね', 'ホノマラのメンバーを題材に「細かすぎて伝わらないものまね」大会をやるとどうなるか考えた。とりあえずボーリングねたはレパートリーが豊富です。');
INSERT INTO public.after VALUES ('oDLkiaAjQ4', '2009-12-09', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 800, 1000, 0, 0, 0, 9, 9, '渋谷', 9, 'ディズニーランド', 'ディズニーランドの周辺を走っているホノマラーがいた。内側から外側が見えないように外側からも花火しか見えないらしい。いつか走りに行こうと思う。');
INSERT INTO public.after VALUES ('jhaZ361ew8', '2009-12-09', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 6, 6, '渋谷', 6, 'ドレスコード', 'クリパにどんな服を着ていけばいいんだろう。普段内面ばかりを磨き、外見には無頓着なホノマラーたちが真剣に悩んでいた。');
INSERT INTO public.after VALUES ('xG1nBNyk6i', '2009-12-12', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 7, 'クリパ＆忘年会', '去年の話をした。クリパがメインみたいな感じだが忘年会だって大切だと思った。');
INSERT INTO public.after VALUES ('x0ZyWRmh20', '2009-12-12', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 900, 900, 0, 0, 0, 8, 8, '渋谷', 8, '激辛', '多くのホノマラーがカレーを通常よりも辛くして食べていた。ところで辛すぎると鼻水が出るっていうのは普通なのだろうか。');
INSERT INTO public.after VALUES ('tfc2VhV2BM', '2009-12-12', '土', '土曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 200, 200, 0, 0, 0, 8, 8, '渋谷', 8, 'ホノルルマラソン', 'ホノルルマラソンでホノマラ内のトップは誰になるか予想した。');
INSERT INTO public.after VALUES ('4VNd6C3yvP', '2009-12-16', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 1000, 1000, 0, 0, 0, 12, 12, '渋谷', 12, '高校生のときの僕、私', '高校生のときの写真を見せあった。あまり変わってない人が多かったが、鈴木さんは焦げた中村俊輔でした。');
INSERT INTO public.after VALUES ('DbvCwojUTJ', '2009-12-16', '水', '水曜練', 2, '東方見聞録', '飲み', false, false, 500, 500, 0, 0, 0, 4, 4, '渋谷', 4, 'ファンタジスタ', 'ファンタジスタ、それはコンパで率先して潰れ、自己を犠牲にしてまでもコンパを実のあるものにする男の姿。歴代のファンタジスタの伝説を伺っていると、一年生にもぜひ、そのような人材が必要だと思った。');
INSERT INTO public.after VALUES ('NFNTdhLpZZ', '2009-12-19', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 17, 17, '渋谷', 17, 'お得意様', 'ホノマラはタパタパにとって最高のお得意様だと思う。店員とは直接話したことはないが、きっと数多くのメンバーが覚えられているはず。タパタパの店員はホノマラのキャプテンが変わったことに気づいているのだろうか？');
INSERT INTO public.after VALUES ('7QBRtjBqWy', '2009-12-19', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 300, 300, 0, 0, 0, 13, 13, '渋谷', 13, '地球温暖化', '田儀さんと地球温暖化について語った。今より３度気温が上がると、世界レベルでの経済成長はマイナスになるらしい。');
INSERT INTO public.after VALUES ('33oARCGub0', '2009-12-23', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 900, 900, 0, 0, 0, 14, 14, '渋谷', 14, '中国の正月', 'ブンちゃんの母国、中国では今年はバレンタインの日が旧正月となるらしい。旧正月は家族で集まって飯を食いテレビをみて、花火をして過ごすということだ。');
INSERT INTO public.after VALUES ('0OkX8lNBoe', '2009-12-23', '水', '水曜練', 2, 'sweden', 'アイス', false, false, 300, 300, 0, 0, 0, 8, 8, '渋谷', 8, '正確な日本地図−１グランプリ', 'どんな文脈か知らないが小谷さんが日本地図を描いたところ、愛知県だけが大きく詳しく描かれていた。細越さんは青森県だけは自信を持っていた。（いずれも各人の出身県。）');
INSERT INTO public.after VALUES ('FuTcOfOrXS', '2009-12-26', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 13, 13, '渋谷', 13, 'おきなわマラソン', '去年よりも人数が倍増した、レンタカーはどうしよう、などとおきなわマラソンについて語り合った。');
INSERT INTO public.after VALUES ('PPuADg7v8T', '2009-12-26', '土', '土曜練', 2, 'sweden', 'アイス', false, false, 250, 250, 0, 0, 0, 10, 10, '渋谷', 10, '年賀状', '年賀状をどうするか話した。多くのホノマラ—は年賀状を手書きで仕上げるというマメな一面を持ち合わせているようだった。');
INSERT INTO public.after VALUES ('5Ps3c3UL1S', '2010-01-06', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 1000, 1000, 0, 0, 0, 14, 14, '渋谷', 14, '正月の過ごし方', '正月の過ごし方はそれぞれだ。伝統的に過ごしたり、友達の家でパーティーをしたりする者もいれば、ハワイに行った人もいたようだ。');
INSERT INTO public.after VALUES ('xEWeiujgRc', '2010-01-06', '水', '水曜練', 2, '東方見聞録', '飲み', false, true, 1000, 1000, 0, 0, 0, 9, 9, '渋谷', 9, '新年会', 'せっかくなので新年会をやってみた。元日に放送されたSASUKEという番組の話で盛り上がった。史上三人目の完全制覇者が出てよかったと思う。');
INSERT INTO public.after VALUES ('Pn6HNYQC6P', '2010-01-09', '土', '土曜練', 2, 'カチカチ山', '飲み', false, false, 1000, 1000, 0, 0, 0, 11, 11, '渋谷', 11, '新年会PART2', '水曜日とは毛色の違う人々がせっかく集まられたので新年会を再度行った。もっともっと一年生と仲良くなりたいよ〜、という願望を持った人々が多いようです。');
INSERT INTO public.after VALUES ('y4FtoqR4c6', '2010-01-09', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 17, 17, '渋谷', 17, 'おれんちの雑煮', '地域や家庭によって正月のお雑煮は違うもの。ホノマラーの家庭でもいろんな雑煮がふるまわれるようだが、ぶりの照り焼きを入れる家庭があることに驚いた。');
INSERT INTO public.after VALUES ('XIHzwixGH8', '2010-01-13', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 13, 13, '渋谷', 13, '泣ける映画', '今までで泣いた映画について話した。人が死んだり、動物が死んだりすることによって泣かせるのは気に入らない。やはりハッピーエンドでなければ。「I am Sam」。何回も宣伝してますがキャプテン一押しです。');
INSERT INTO public.after VALUES ('DT5jqmjGNn', '2010-01-13', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 7, 7, '渋谷', 7, '珍客', '野ノ内さんがきました。');
INSERT INTO public.after VALUES ('mFNj4b5Z6F', '2010-01-16', '土', '土曜練', 1, 'カレー研究所', 'インド料理', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 10, '箱根企画', '今年の箱根企画は若年層が多くなりそうな感じらしい。やはり平日にやる影響なのだろうか？暇な人は23・24日にやるので参加しよう！');
INSERT INTO public.after VALUES ('Ck8bbCiJFG', '2010-01-16', '土', '土曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 250, 250, 0, 0, 0, 10, 10, '渋谷', 16, 'リレー', 'また13日にリレーをやると小谷さんが言ったらどこからかその日に１年会をやりたいとか言う声も…　その後キャプテン飲みしてきた人々が嵐のように来て嵐のように去って行きました。');
INSERT INTO public.after VALUES ('Yk4d7PzSdV', '2010-01-16', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 7, 'トークショー', '学祭に芸能人を呼ぶ際の仲介業者があるらしい。ひいきにしてもらえるとよびやすくなるらしい。');
INSERT INTO public.after VALUES ('iG2taQk9ZY', '2010-01-20', '水', '水曜練', 1, 'ロコモコ', 'どんぶり', false, false, 1000, 1000, 0, 0, 0, 9, 9, '渋谷', 9, '遺伝子', 'なぜか遺伝子の話になった。ゲノムの解析はできるようになったが、それを有効活用できるようになるまでにはまだまだ時間がかかりそうだ。');
INSERT INTO public.after VALUES ('7i9421HkAR', '2010-01-20', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 300, 300, 0, 0, 0, 6, 6, '渋谷', 6, '男だけの夜', '今日は男子だけのアフターズとなった。男気みなぎる話が展開した。');
INSERT INTO public.after VALUES ('5KCSyjyN7q', '2010-01-23', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 15, '鬼ごっこ', '鬼ごっこを壮大にしたいと言う人が…　場所とルール決めが大変そうだという話になった。');
INSERT INTO public.after VALUES ('lyV5JMn3wJ', '2010-01-23', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 11, 11, '渋谷', 11, '新歓', '徐々に近づいてきました。東大の方では新歓代表者会議が日曜にあるとか…　');
INSERT INTO public.after VALUES ('ggBBAkaJGE', '2010-01-27', '水', '水曜練', 1, 'GOLDRUSH', '洋食', false, false, 1200, 1200, 0, 0, 0, 10, 10, '渋谷', 10, '新歓活動', '先週の日曜日には早くも東大の新歓会議があったわけだが、去年の新歓の様子を田儀さんからうかがった。マラソンブームの影響を考えると、今年も大変そうですね。');
INSERT INTO public.after VALUES ('fsYH4CA3Iw', '2010-01-27', '水', '水曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 300, 300, 0, 0, 0, 8, 8, '渋谷', 8, 'ホノマラで映画を撮ろう', '「新歓活動用のプロモーションビデオを作るなら」という話題から「ホノマラでアニメの実写版を撮るなら」という話題に発展。サザエさんの実写版、『オダニさん』など、数多くの企画案が発表された。');
INSERT INTO public.after VALUES ('I0ZKlHfDZe', '2010-01-30', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 11, 11, '渋谷', 11, '俺の姉ちゃん', '最近お姉ちゃんが急にきれいになって、変な男がよりついてこないかと心配している弟がいた。');
INSERT INTO public.after VALUES ('Wx5GMu3jfc', '2010-01-30', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 9, 9, '渋谷', 9, 'ゲームがしたい', '春休みに去年のメトロノームのようなゲームがしたいという人がいた。いろいろおもしろそうな案が浮上した。
INSERT INTO public.after VALUES ('Ea4XIxTmOR', '2010-02-03', '水', '水曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 300, 300, 0, 0, 0, 6, 6, '渋谷', 6, 'ゼミ', '細越さんが持ち込んだゼミの資料をネタに談笑。その後裕士さんが登場し、盛り上がった。');
INSERT INTO public.after VALUES ('rfNmPQfcwX', '2010-02-06', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 9, 9, '渋谷', 9, 'ヨーグルト', '暖かく温度を保った状態で、牛乳をふりまくるとヨーグルトになり、さらにふりつづけるとバターになるらしい。
INSERT INTO public.after VALUES ('2K9Mra5AXq', '2010-02-06', '土', '土曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 4, '少人数アフターズ', '鉄板の二年３人衆のうち、荒木氏、細越氏がアフターズを欠席。少人数によるアフターズとなった。しかし、「以頭さん」、「弟子の作り方」、「ここを修正すればこの人はもっと良くなる」、「残りそうなホノマラー」など多岐にわたるジャンルで活発な議論が交わされた。');
INSERT INTO public.after VALUES ('ONLnD7HvSy', '2010-02-06', '土', '土曜練', 1, 'ホァングン', 'ベトナム料理', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 4, '命名権', '味の素スタジアムのように命名権をビジネスに展開している施設が多い。では人の名前にも応用できるのではという声も。　例：原光平→原ヤフーみたいな…　どうやら有名人じゃないと意味がないようだ。');
INSERT INTO public.after VALUES ('itlVQsyS3h', '2010-02-10', '水', '水曜練', 1, 'ロコモコ', 'どんぶり', false, false, 1000, 1000, 0, 0, 0, 11, 11, '渋谷', 11, '青森県民', '門屋さんが青森県民は早く死ぬという情報を披露し、その理由を考えた。血統的に酒が好きな人々が集まっており、つまみとして塩分が多く含まれる食物を過剰に摂取することによる、塩分の摂りすぎであるという結論に至った。');
INSERT INTO public.after VALUES ('sa8vEHls9o', '2010-02-10', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 200, 200, 0, 0, 0, 6, 6, '渋谷', 6, '人気者', 'ホノマラにおける人気者とは誰なのか？いやそれ以前に「人気がある」とはどのような現象を指しているのか、ということについて議論。議論を進めていくうちに、この世には受動的な人気者と能動的な人気者が存在していることが明らかになった。
INSERT INTO public.after VALUES ('lNbpqsX5VD', '2010-02-13', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 12, 12, '渋谷', 12, 'サークル記録を作りたい', '小谷氏は一週間後に迫った赤羽ハーフでサークル記録をはじき出そうと練習にいそしんでいる。そこで門屋氏も何かの種目でサークル記録を残したいと意気込んでいた。');
INSERT INTO public.after VALUES ('g25Rje25zI', '2010-02-13', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 300, 300, 0, 0, 0, 12, 12, '渋谷', 12, '指数関数的勉強法', '経済学部に進学予定のとある２年生は月曜からテストにも関わらず、アフターズに顔を出した。聞けば、今日から二徹してテストに臨むという。指数関数的に日々の勉強量がアップしているらしい。
INSERT INTO public.after VALUES ('rUGV7rfFgD', '2010-02-17', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 10, 10, '渋谷', 10, 'ポニョのここがおかしい', '先日金曜ロードショーにて地上波初登場を果たしたポニョ。ロードショーとしては異例の視聴率29％を記録。その人気は衰えを知らないが、ポニョ対して多々の疑問点を感じているホノマラーがいた。おとぎ話であることを理解せず、容赦のないつっこみを大人げなく行っていた。');
INSERT INTO public.after VALUES ('kYNuinfec5', '2010-02-17', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 5, 5, '渋谷', 5, 'こんな一年生がほしい', 'まだ2月ではあるが、暇を持て余すアフターズのホノマラーたちにとって気分はもう4月、新歓活動の季節である。
INSERT INTO public.after VALUES ('1FP4z1VsWv', '2010-02-20', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 6, 'まぎらわしい日本語', '「ミルクでたてたコーヒー」
INSERT INTO public.after VALUES ('4TuQ1yAP77', '2010-03-31', '水', '水曜練', 2, 'モスバーガー', 'ファーストフード', false, false, 300, 300, 0, 0, 0, 7, 7, '渋谷', 7, '小説スレッド', 'ホノマラの雑談掲示板に突如出現した小説スレッド。
INSERT INTO public.after VALUES ('xKRoprfm9t', '2010-02-20', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 200, 200, 0, 0, 0, 13, 13, '渋谷', 13, 'ホノマラ学', '新しく入ってくる一年生にもっとホノマラのことを知ってもらいたい・・・。
INSERT INTO public.after VALUES ('aGNNHdjV6p', '2010-02-24', '水', '水曜練', 1, 'サイゼリヤ', 'ファミレス', false, false, 300, 800, 0, 0, 0, 6, 6, '渋谷', 6, 'サイゼリア', '人数が少なかったため珍しくサイゼへ行った。比較的安価なため、ほとんどが一人二品頼んでいた。');
INSERT INTO public.after VALUES ('5xxfA1bKna', '2010-02-24', '水', '水曜練', 2, 'エスト', 'ビリヤード', false, false, 700, 700, 0, 0, 0, 5, 5, '渋谷', 5, 'ビリアード', '今日はビリアードがしたかったから練習に行ったといっても過言がないくらいビリアードがしたかった。');
INSERT INTO public.after VALUES ('F2iygSF4Qi', '2010-02-20', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 8, 'ウルトラマラソン', 'ウルトラには凄い人がいっぱいいる。そして自分もいつか２４時間走などで世界大会に出たいと小谷氏は熱く語っていた。');
INSERT INTO public.after VALUES ('2ijZSXBsEs', '2010-02-20', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 8, '給食とお弁当', '学校にお弁当を持ってくと昼前に食べる早弁をしてしまうのが普通。毎朝お弁当を作ってくれてた親に感謝した。');
INSERT INTO public.after VALUES ('RC7WyvV5Ga', '2010-02-27', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 7, 7, '渋谷', 7, '実はチャラいアルゴリズム', '最近、割引券があるのでついついカレ研でのアフターが増えてしまっている。
INSERT INTO public.after VALUES ('eZqeQuehEH', '2010-02-27', '土', '土曜練', 2, 'ブルーシールアイスクリーム', 'アイス', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 10, 'さらばブルーシール', '今月いっぱいで閉店予定のブルーシールアイスクリーム。ホノマラのアフターズ会場の提供を担う数少ない店として君臨したが、渋谷のど真ん中でホノマラを収容できる＝繁盛してない、であるから無理はない。
INSERT INTO public.after VALUES ('CvLmTPxCoh', '2010-02-27', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 8, 8, '渋谷', 8, '沖縄マラソン', '全員沖縄に行く人達だったので来週の展望を話した。運良くお会計で店員さんにおまけしてもらった。');
INSERT INTO public.after VALUES ('DTQ7JyZ6HY', '2010-03-03', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 900, 900, 0, 0, 0, 13, 13, '渋谷', 13, '沖縄観光', '沖縄での３日目は完全に一日中観光となる。
INSERT INTO public.after VALUES ('66Sb8tlQF5', '2010-03-03', '水', '水曜練', 2, 'モスバーガー', 'ファーストフード', false, false, 200, 200, 0, 0, 0, 7, 7, '渋谷', 7, 'じゃんけん選手権など', 'じゃんけん選手権をやったらどういうことになるのかを真剣に議論した。
INSERT INTO public.after VALUES ('TgcMjCI7lO', '2010-03-06', '土', '土曜練', 1, '街の洋食屋　COPAN', '洋食', false, false, 1000, 1000, 0, 0, 0, 3, 3, '渋谷', 3, '開拓！', '少人数だったのでアフターの店を開拓してみた。時間帯の影響かもしれないが、土曜なのに店内はがらがらだった。新アフター候補として申し分なかった。');
INSERT INTO public.after VALUES ('3w6vCoEBCk', '2010-04-17', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 300, 300, 0, 0, 0, 19, 19, '渋谷', 20, '鈴木氏の口癖', '鈴木氏は命令形の後に接尾語として「し」が付く。これが彼の口癖である。
INSERT INTO public.after VALUES ('IyQaZ9O78G', '2010-03-10', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 5, 5, '渋谷', 5, '競争心', '「最近のホノマラには競争心がない！」
INSERT INTO public.after VALUES ('tXHVC9ocH1', '2010-03-10', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 11, 11, '渋谷', 11, 'アイドル誕生', '新歓期を前にしながら、ホノマラのメンズはアイドルを発掘してしまった。
INSERT INTO public.after VALUES ('DWz3dHfv5c', '2010-03-17', '水', '水曜練', 1, '卵と私', '洋食', false, false, 900, 900, 0, 0, 0, 5, 5, '渋谷', 5, '恐るべしオーラ', 'オーラは日本語を専攻しているが、脳科学？にも興味を持っているらしく、いくつかの心理テストを十八番としていた。もののみごとにひっかかった。');
INSERT INTO public.after VALUES ('DBT47aHaFQ', '2010-03-17', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 4, '追いコン、花見', 'スウェーデン復活！！かと思いきやスウェーデンはホノマラを受け付けないような上品な喫茶店に変貌していた。今後もしばらくアフターズの場所探しに苦しむことになりそうだ。
INSERT INTO public.after VALUES ('HKGkbQHxX0', '2010-03-17', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 11, 11, '渋谷', 11, '辛いカレー', 'カレーの辛さを通常の倍以上にして苦しんでいる人がいた。店員さんいわく普通のカレーよりもスープカレーは余計に辛いそうだ');
INSERT INTO public.after VALUES ('4d1Kj97llF', '2010-03-17', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 100, 100, 0, 0, 0, 5, 5, '渋谷', 5, 'ある人', 'アフターズにいなかったある人についての話題で盛り上がった。ある人のいびきはすごいらしい。');
INSERT INTO public.after VALUES ('FSr0CwYakg', '2010-03-13', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 15, 15, '渋谷', 17, '合宿', '合宿係の二人を中心にどこいくか話した。有力候補としては福島県などがあがったけれど、どこ行くかは宿舎がとれるかどうかみたいだ…');
INSERT INTO public.after VALUES ('A1UtYcIzPE', '2010-03-13', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 200, 200, 0, 0, 0, 6, 6, '渋谷', 7, 'アフター録・練習録', '今年度荒木さんが練習100回に到達する見込みである。しかし、まっつんさんの2002年度練習111回には及ばない。そしてまっつんさんのアフター参加回数が1000回になったら盛大に祝おうという話になった。');
INSERT INTO public.after VALUES ('LtVLlPqjcm', '2010-03-24', '水', '水曜練', 1, 'サイゼリヤ', 'ファミレス', false, false, 500, 500, 0, 0, 0, 6, 6, '渋谷', 4, '新歓合宿', '雨で走れないので即アフターへ。新歓合宿について話したり、成績について講評した。');
INSERT INTO public.after VALUES ('hYk7v7Dpmo', '2010-03-24', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 4, '後継者', '新入生への思いを語った。１年前のある人と同じようなことを言ってる人がいた。後継者に期待。');
INSERT INTO public.after VALUES ('34cEfywltz', '2010-03-27', '土', '土曜練', 1, 'ジョナサン', 'ファミレス', false, false, 900, 900, 0, 0, 0, 11, 11, '渋谷', 10, 'コスプレ', '五月の駅伝カーニバルで何をコスプレするかを話した。ある人はいつもの自分とは違うギャップを見せたいと語っていた。');
INSERT INTO public.after VALUES ('YeN7TyCHnn', '2010-03-27', '土', '土曜練', 2, 'VELOCE', 'カフェ', false, false, 200, 200, 0, 0, 0, 10, 10, '神田', 9, '新歓', 'アフターズにしては珍しく多くのホノマラー達が残ってくれた。新歓のビラの内容について話し合った。ホノマラおじさんを強調した今年のビラは注目度抜群であろう。');
INSERT INTO public.after VALUES ('p417ZgwGDi', '2010-03-31', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 10, '明日から', '明日から新歓活動が始まるということで、もちろん新歓の話題に。
INSERT INTO public.after VALUES ('QLF5QatH3K', '2010-04-03', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 15, '後輩', 'もうすぐ新しい後輩がやってくる。１年生に対してある人はいつまで先輩の威厳がもつだろうか。前年度は夏合宿まで？らしい。');
INSERT INTO public.after VALUES ('kcXsfBB8Hu', '2010-04-03', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 500, 500, 0, 0, 0, 10, 10, '渋谷', 12, '手相', '手相を見た。結婚線が３本あるとどうなるのか。信じるか信じないかはその人しだいだと思う。');
INSERT INTO public.after VALUES ('UOhvDtrwQ2', '2010-04-07', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 13, 13, '渋谷', 13, '完熟たまご？', 'みなさん御存じ半熟たまご。
INSERT INTO public.after VALUES ('zxQKfRubK1', '2010-04-07', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 10, '秋田に行きたい', 'かねてから秋田100キロへの参戦が噂されていたあの人の秋田行きがほぼ確実なものとなった。小谷氏と練習メニューを真剣に考えていた。');
INSERT INTO public.after VALUES ('qRyJVAHBzT', '2010-04-10', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 7, 7, '渋谷', 7, 'はじめまして', '新入生4人は全員卵と私へ。
INSERT INTO public.after VALUES ('k3Ij2X7yto', '2010-04-10', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 700, 700, 0, 0, 0, 10, 10, '渋谷', 10, '肉', '鶏肉・豚・イノシシなどいろんな種類の肉の話をした。場所によってはガチョウの肉を食べるところがあるらしいが、果たしてうまいのだろうか。');
INSERT INTO public.after VALUES ('Qete8GpqoQ', '2010-04-10', '土', '土曜練', 1, 'かにチャーハンの店', '中華', false, false, 700, 700, 0, 0, 0, 6, 6, '渋谷', 6, '空いてた', 'ゴールドラッシュに入れず、スープ屋はなくなり困ってたら偶然空いてた。ラッキーだった。新歓の話をしたりした。');
INSERT INTO public.after VALUES ('0TLIV91hUN', '2010-04-10', '土', '土曜練', 1, 'カレー研究所', 'インド料理', false, false, 1000, 1000, 0, 0, 0, 11, 11, '渋谷', 11, '誕生日', 'もうすぐ誕生日で20歳になる人がいた。世の中には覚えやすい誕生日の人もいれば覚えにくい誕生日の人もいるようだ。');
INSERT INTO public.after VALUES ('ihUksoCcgQ', '2010-04-10', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 8, 8, '渋谷', 8, '名前占い', '名前占いで盛り上がった。
INSERT INTO public.after VALUES ('bMWmXGsYBU', '2010-04-10', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 16, 16, '渋谷', 15, 'ホノマラの楽しみ方', '新入生に対してホノマラの楽しみ方、ホノマラでの生き方を説こうとした。
INSERT INTO public.after VALUES ('cnKe5KIriO', '2010-04-14', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 22, 22, '渋谷', 22, '運命', '新入生の中野くん。
INSERT INTO public.after VALUES ('IN8bq5uKtS', '2010-04-14', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 350, 350, 0, 0, 0, 12, 12, '渋谷', 12, '人の体について', '人の体の不思議について議論した。
INSERT INTO public.after VALUES ('lu3Yqfs5oO', '2010-04-17', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 18, 18, '渋谷', 18, '裕士さん', '18人が一斉に入店すると店員が混乱していた。
INSERT INTO public.after VALUES ('hrEqMYuQX3', '2010-04-17', '土', '土曜練', 3, 'エスト', 'ビリヤード', false, false, 340, 340, 0, 0, 0, 6, 6, '渋谷', 6, '似てる', '夜もすがら走っている人がいることなどすっかり忘れてビリヤードに興じた。
INSERT INTO public.after VALUES ('XWYS7YRFsi', '2010-04-17', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 15, 'ホノマラのよさ', '新入生にホノマラの良さを話したり、どういうイベントがあるかを話した。');
INSERT INTO public.after VALUES ('ZRYJFcUf7t', '2010-04-21', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 10, 'やきそばの箱', 'カップラーメンのカップには印刷が施されているのに、インスタントやきそばにはどうして箱に印刷が施されていないのか、について検討した。
INSERT INTO public.after VALUES ('47JryxZAH0', '2010-04-21', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 18, '佐賀県', 'アンドナンドのスウェーデン化が進んでいると思われる。
INSERT INTO public.after VALUES ('t3v6UeDnZX', '2010-04-21', '水', '水曜練', 1, '卵と私', '洋食', false, false, 1030, 1030, 0, 0, 0, 6, 6, '渋谷', 6, 'サークル', '今野の怪しいサークルの話を新入生に聞かせた。ホノマラが怪しいサークルと勘違いされてたらどうしよう。');
INSERT INTO public.after VALUES ('LKy1GNSSs9', '2010-04-21', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 10, 10, '渋谷', 15, '新歓コンパ', '新歓コンパからホノマラに来る人がたくさんいるらしい。そこでいかにホノマラの雰囲気を伝えられるかだと思う。あとは副キャプの意義について語った。');
INSERT INTO public.after VALUES ('3amMVKFJgL', '2010-04-24', '土', '土曜練', 3, '吉野家', '牛丼', false, false, 380, 380, 0, 0, 0, 2, 2, '渋谷', 2, '値上がり', '1次会と2次会ではまともに食べることができなかったので、食べなおした。
INSERT INTO public.after VALUES ('Ei64l3Sksr', '2010-04-24', '土', '土曜練', 1, 'ダイニング　TOM　BOY　106', 'dining bar', false, false, 2800, 2800, 0, 0, 0, 60, 60, '渋谷', 65, 'やっぱり狭かった', 'さらに人が増えた。
INSERT INTO public.after VALUES ('NIRe23WA6Z', '2010-04-24', '土', '土曜練', 2, 'つぼ八', '和食（飲み）', false, false, 2500, 2500, 0, 0, 0, 40, 40, '渋谷', 35, 'ぼくプーさんだよ', '清さんと落合さんがプーさんのものまねをしていた。');
INSERT INTO public.after VALUES ('4ZMeUytMjZ', '2010-04-24', '土', '土曜練', 4, '歌広場', 'カラオケ', false, false, 600, 600, 0, 0, 0, 6, 6, '渋谷', 10, '女性歌手', '女性は一人しかいなかったのに、歌われる曲はなぜか大塚愛ばっかりだった。日の出を久しぶりに見た。なんて健康的なんだ！');
INSERT INTO public.after VALUES ('GBWUi5dvdZ', '2010-04-26', '月', '本郷練', 1, '中央食堂', '学食', false, false, 400, 400, 0, 0, 0, 6, 6, '本郷', 6, '新勧コンパ', '土曜日におこなわれたコンパでの今年の一年生の印象やら新勧合宿の内容に驚愕したり。これからどんなホノマラになるのだろうか。');
INSERT INTO public.after VALUES ('noNoQKckK6', '2010-04-28', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 14, 14, '渋谷', 14, '激○○', '「激辛」という言葉が存在する。
INSERT INTO public.after VALUES ('4wE8pcaj65', '2010-04-28', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 11, 11, '渋谷', 11, '東大寺学園の人々', 'ホノマラには東大寺学園出身の人が多く在籍している。
INSERT INTO public.after VALUES ('NhefzGAamD', '2010-04-28', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 10, 10, '渋谷', 5, '連休', 'ある人は授業がなく30日も休みらしい。
INSERT INTO public.after VALUES ('baJpNkFf3y', '2010-05-01', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 20, 20, '渋谷', 20, '日本のアニメ', '新人の庄子くんがジブリのキャラクターに詳しかったことから、アニメの話になった。
INSERT INTO public.after VALUES ('Yjy3Mxa8LD', '2010-05-01', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 350, 350, 0, 0, 0, 23, 23, '渋谷', 23, 'ブンちゃん', 'ご存じブンちゃんは日本語がペラペラだ。
INSERT INTO public.after VALUES ('j5zCf4269Y', '2010-05-01', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 630, 630, 0, 0, 0, 9, 9, '渋谷', 9, '野菜たっぷりカレー！？', '野菜たっぷりカレーなるものを注文したところ野菜らしきものはほとんどなく、じゃがいもばかりであった…
INSERT INTO public.after VALUES ('fgWOaebQr7', '2010-05-01', '土', '土曜練', 1, 'かにチャーハンの店', '中華', false, false, 600, 600, 0, 0, 0, 4, 4, '渋谷', 3, '進路…', '東大では進学振り分けがあるため２年生になると進路について考えるようだ。ある人は理転したいとか…
INSERT INTO public.after VALUES ('fgbp1gm5xC', '2010-05-05', '水', '水曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 8, '水', '今日は暑かったせいか、練習した後に出される水の味は格別であった。');
INSERT INTO public.after VALUES ('IummdtW329', '2010-05-05', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 10, 'ブレストファイヤー！', 'ある話題からマジンガーＺの話題へと移った。僕もブレストファイヤーを受けてみたいです笑');
INSERT INTO public.after VALUES ('nUrZmgMB2x', '2010-05-05', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 6, '遠距離恋愛', '遠距離恋愛について定量的な議論をした。しかしながら、結局の所愛の力は偉大であるというありふれた結論になっていた。');
INSERT INTO public.after VALUES ('ZSlCl33iT0', '2010-05-08', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 700, 700, 0, 0, 0, 20, 20, '渋谷', 6, '料理', '料理をする男性は意外に多い。ｓ先輩の得意料理はきんぴらごぼうらしい。');
INSERT INTO public.after VALUES ('RqbaMh8s1A', '2010-05-08', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 20, 20, '渋谷', 21, '気が早い', '早くも次のキャプテンが誰になるのかという話をした。
INSERT INTO public.after VALUES ('23T3RCTFgG', '2010-05-08', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 18, '川村君', 'ドトールに楽に入れたと思ったら、九時で追い出された。
INSERT INTO public.after VALUES ('Ohg29jG19W', '2010-05-10', '月', '本郷練', 1, '中央食堂', '学食', false, false, 390, 390, 0, 0, 0, 6, 6, '本郷', 6, '無謀な挑戦', 'やはり人気のホノルルマラソン。フルマラソン経験者による「マラソンはだれでも走れる」といった言葉から、どれくらいで１００ｋｍそれ以上走れるかなどいろいろな質問がされていた。');
INSERT INTO public.after VALUES ('k8CsThcOTm', '2010-05-12', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 20, 20, '渋谷', 21, '進学振分制度', '進学振分制度はよくない。
INSERT INTO public.after VALUES ('7OrvOhUexK', '2010-05-12', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 13, 'よくしゃべる人', '当初、細越氏がよくしゃべっていた。
INSERT INTO public.after VALUES ('tcWTllOPOQ', '2010-05-15', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 3, '少人数アフター', '卵と私の人気があまりなく、3人で入店した。新歓期、ずっと喧騒にまみれたアフターを過ごしてきたのでとても新鮮な気分でオムライスを口に運んだ。');
INSERT INTO public.after VALUES ('UP5hbrkE6r', '2010-05-15', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 24, '夏休みの大会', '去年は一部の一年生が秋田メロンマラソンに参加し友好を深めた。
INSERT INTO public.after VALUES ('ZMjK1otw7E', '2010-05-15', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 13, 13, '渋谷', 13, '自己紹介', '新しく来た一年生と自己紹介などをした。');
