INSERT INTO public.after VALUES ('ZzttMkLsAO', '2010-05-16', '日', '峠走', 1, 'かついち', '和食', false, false, 1000, 1000, 0, 0, 0, 10, 10, '秦野', 5, '疲労', 'こんなに運動した。だからいっぱいがっつけるはずだ。
INSERT INTO public.after VALUES ('TUIIikHS7B', '2010-05-19', '水', '水曜練', 1, 'GOLDRUSH', '洋食', false, false, 1200, 1200, 0, 0, 0, 4, 4, '渋谷', 4, '懐かしい組み合わせ', '久々に今年の２，３月ごろのアフターズの顔ぶれで練習後を過ごした。
INSERT INTO public.after VALUES ('4uiahxNhmE', '2010-05-19', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 4, '新歓合宿', '新歓合宿について、優しい先輩方から助言をいただいた。
INSERT INTO public.after VALUES ('DTan5QsqlM', '2010-05-26', '水', '水曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 10, 10, '渋谷', 16, 'アンケート', 'テーブルの端にアンケート用紙がおいてあったので、河田と細井が回答しておいた。
INSERT INTO public.after VALUES ('mQeqSafJDe', '2010-05-26', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 12, '企画会議', '新歓合宿も無事終わったことだし、今後の企画について話した。
INSERT INTO public.after VALUES ('Q5jNiv9qo4', '2010-05-29', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 16, 'キャベツ炒め', '三輪さんたちが遅れてやってきた。
