INSERT INTO public.after VALUES ('NGdghZN34X', '2010-05-29', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 11, 11, '渋谷', 11, '折り紙', 'レシートで超ミニマムサイズの鶴を折る強者がいた。彼女によると、恐竜もその気になれば折ることができるとか。');
