INSERT INTO public.after VALUES ('xEE2vcxCSr', '2010-06-12', '土', '土曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 7, 'ワールドカップ', '昨日ワールドカップが開催した。
