INSERT INTO public.after VALUES ('Cf4e6wRadU', '2010-06-16', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 8, 8, '渋谷', 12, '人間関係', '小谷氏に誘われて、人間関係をパソコンに打ち込む作業をした。
INSERT INTO public.after VALUES ('9eCWUW42O2', '2010-06-19', '土', '土曜練', 1, 'ロコモコ', '洋食', false, false, 750, 750, 0, 0, 0, 4, 4, '渋谷', 8, 'また一軒', '人数も少なくなったので、久しぶりにロコモコに行くことに。すると、レジ前に「27日をもって閉店」との看板が。せっかくハワイアンオリジナルロコモコという新メニューがあったのに食べることなく終わってしまいそうだ。');
INSERT INTO public.after VALUES ('kZZYOIcMpo', '2010-06-23', '水', '水曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 6, '脱おっさん', '河田は最近おっさんと呼ばれることが多い。
INSERT INTO public.after VALUES ('NuEcuIkCvh', '2010-06-23', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 300, 300, 0, 0, 0, 6, 6, '渋谷', 16, '新企画', '合宿での新企画について議論した。
INSERT INTO public.after VALUES ('tcWHG0pvO6', '2010-06-23', '水', '水曜練', 1, 'ロコモコ', 'どんぶり', false, false, 500, 500, 0, 0, 0, 13, 13, '渋谷', 13, '大会', '大会の話をした。２４時間リレーは参加費が７０００円くらいだった気がするが、裏技を使えばもっと安くなるかも。ぜひ参加しましょう。');
INSERT INTO public.after VALUES ('UkRDI4yCEI', '2010-06-26', '土', '土曜練', 1, 'GOLDRUSH', '洋食', false, false, 1200, 1200, 0, 0, 0, 4, 4, '渋谷', 5, '趣味', '食べることしか趣味がないと嘆く人に、一緒に走りましょうと言う先輩とギャンブルを勧めている後輩がいた。実に対照的である。');
INSERT INTO public.after VALUES ('ZGMkqQdLUa', '2010-06-30', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 15, 15, '渋谷', 15, 'がらがら', 'ホノマラーが愛用するタパスタパスは最近客足が少ないのか、がらがらな気がする。閉店しないことを願います。');
INSERT INTO public.after VALUES ('QJP0VTvo7t', '2010-06-30', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 180, 180, 0, 0, 0, 12, 12, '渋谷', 12, '合宿', '夏合宿恒例のランキングの話をした。今年は新しい項目として「おっさん」を推しますよ笑');
INSERT INTO public.after VALUES ('58FesBAr9l', '2010-07-03', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 7, 'G', '濱崎さんが遅れてやってきた。したがって濱崎さんの料理は遅れて出てきたわけだが、そのあいだずっと、小谷氏が自宅のゴキブリについての話をしていた。
INSERT INTO public.after VALUES ('JRInfCjl82', '2010-06-12', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 4, '好み', '男女の視点の違いについて。');
INSERT INTO public.after VALUES ('mfup8bUEiS', '2010-07-03', '土', '土曜練', 1, 'カレー研究所', 'インド料理', true, false, 1000, 1000, 0, 0, 0, 5, 5, '渋谷', 5, 'ランキング', '１つぐらいはランクインしたいもの！去年１つもランクインしなかった２年生が今年こそランクインするためにはどうすればいいのか話し合った。');
INSERT INTO public.after VALUES ('Nh1qpB2lQ4', '2010-07-03', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 300, 300, 0, 0, 0, 5, 5, '渋谷', 13, 'ストレス', 'アフターズでストレスを与え続けられ、ストレスをためこんだまま帰ったかわいそうな人がいた。一方で周りのストレスを吸収してしまう癒し系もいる。癒し系は非常に貴重な存在だ。');
INSERT INTO public.after VALUES ('HpttWZhEiA', '2010-07-03', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 10, 10, '渋谷', 8, '値段', '店に掲示してある値段とメニューに書いてある値段が違うものがあった。
INSERT INTO public.after VALUES ('d0mHKWA2MI', '2010-07-07', '水', '水曜練', 1, 'てんや', 'どんぶり', false, false, 650, 650, 0, 0, 0, 4, 4, '渋谷', 4, 'いつもと違うところ', '人数が少ないということで、いつもと違うところに行きたいという要望があったので、天丼を食べた。');
INSERT INTO public.after VALUES ('rjCDYgRWlc', '2010-07-07', '水', '水曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 200, 200, 0, 0, 0, 4, 4, '渋谷', 8, 'なにが怖いのか', '小谷さんが最近書いた小説の実は怖いポイントについて。みんな全然分からず、結局答えを教えてもらった。うん、たしかに・・・という感じ。
INSERT INTO public.after VALUES ('a9Rr7hXdsc', '2010-07-07', '水', '水曜練', 1, '街の洋食屋　COPAN', '洋食', false, false, 1000, 1000, 0, 0, 0, 7, 7, '渋谷', 7, '浦佐', '浦佐山岳耐久マラソンについて話した。過酷なレースにも関わらず、今年は女子の参加者が増えそうである。');
INSERT INTO public.after VALUES ('fQucdClp30', '2010-07-10', '土', '土曜練', 1, '日高屋', 'ラーメン', false, false, 500, 500, 0, 0, 0, 5, 5, '渋谷', 5, 'すみません', '多くがおひつ屋へ流れる中、冷たいもの（例えば冷やし中華）が食べたいという衝動に駆られ日高屋へ。でもあったかいつけ麺を頼んじゃいました(＞_＜)');
INSERT INTO public.after VALUES ('Q61Wc6H8q3', '2010-07-10', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 18, '干物女', '植竹と細井が観ているドラマには綾瀬はるか演じる「干物女」が登場する。
