INSERT INTO public.after VALUES ('4rvKIh3A8o', '2010-07-14', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 20, '前提', '女性は男性の内面を見て判断していると言う人が多いような気がするが、やっぱり外見も必要なのかなぁ、という嘆きが聞こえた。');
INSERT INTO public.after VALUES ('WaiuuH1WdS', '2010-07-17', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 300, 300, 0, 0, 0, 8, 8, '渋谷', 18, '合宿', 'ついに合宿が一か月後に迫った。
