INSERT INTO public.after VALUES ('6P4CfmYzc7', '2010-08-07', '土', '土曜練', 1, '卵と私', '洋食', false, false, 1000, 1000, 0, 0, 0, 13, 13, '渋谷', 13, 'ニューオープン！', '潰れてしまったカレー研究所の跡地に新しいパスタ店！？が八月十日にオープンするらしい。新しいアフター先となるのだろうか？');
INSERT INTO public.after VALUES ('iKJs6zpOWR', '2010-08-07', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 180, 180, 0, 0, 0, 12, 12, '渋谷', 12, '潰れんじゃね？', 'アフターズ先である「and on and」は最近やたらすいている気が…
INSERT INTO public.after VALUES ('dyTBBxvw12', '2010-08-07', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 900, 900, 0, 0, 0, 8, 8, '渋谷', 7, 'パパっぽい', 'ある人は双子のパパであるという嘘話を本気で信じる１年生がいた。');
INSERT INTO public.after VALUES ('F5FK4jXjSf', '2010-08-14', '土', '土曜練', 1, '金の蔵', '飲み', false, false, 1400, 1400, 0, 0, 0, 4, 4, '渋谷', 4, 'プチ3年会', '初めはカレ研跡地にできた店に行こうとしたが、メニューの少なさに衝撃を受けて結局やめた。
INSERT INTO public.after VALUES ('bLXdVbeMJU', '2010-08-18', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 8, 8, '渋谷', 8, 'メガマッチョ', 'カレー研究所の跡地にできたスパマッチョなるパスタ店に入ってみた。当店は大盛りのパスタが比較的安く食べられることが売りのようで、二人が中でも最大のメガマッチョに挑戦した。');
INSERT INTO public.after VALUES ('vlks0uKAjY', '2010-08-18', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 300, 300, 0, 0, 0, 16, 16, '渋谷', 16, '富士山', '明日が富士登山だというのに十時ごろまでアフターズに残っているホノマラーたちがいた。富士山をなめているようだ。');
INSERT INTO public.after VALUES ('OzrepQ0YnM', '2010-08-21', '土', '土曜練', 1, 'SPICE MAGIC', 'その他', false, false, 600, 600, 0, 0, 0, 7, 7, '渋谷', 7, 'アフターズ', 'アフターの場所は探せばいろいろあるようだが、アフターズ先はそれなりの人数で長時間いられる場所という条件のため、この頃「and on and」ばかりである。そろそろ違う場所に行きたいものである。');
INSERT INTO public.after VALUES ('xdPWCekoiU', '2010-08-21', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 180, 180, 0, 0, 0, 15, 15, '渋谷', 15, '合宿', '合宿を振り返った。酔って暴れていた人の印象が強かったようである。');
