INSERT INTO public.after VALUES ('5nsEUEeJbq', '2010-10-16', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 12, 'toto', 'つくばマラソンに出る人が多いのでtotoをしたいと言う人がいた。タイムを当てるという案も…');
