INSERT INTO public.after VALUES ('X6DFdsG3Iu', '2010-10-23', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 180, 180, 0, 0, 0, 15, 15, '渋谷', 20, '芸能界', '芸能界の裏事情について話した。芸能界はかなりドロドロしているそうで、ショックを隠しきれないです。');
INSERT INTO public.after VALUES ('pHSBdcJoK0', '2010-10-23', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 12, 'ドライブ', '明日ドライブに行くという1年生がいた。同じ1年の川村を誘うという案もあったが、運転させるのが怖ので却下！！');
