INSERT INTO public.after VALUES ('6VHPTF8yiz', '2010-11-20', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 14, 'コミュニケーション', '初対面の人に話しかけ、さらにどのように仲良くなっていくか。
INSERT INTO public.after VALUES ('KFvoFwb8fF', '2010-11-20', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 15, 15, '渋谷', 13, '最後のおひつ屋', '代替わり前の最後のおひつ屋アフターになった。11月でおひつ屋は閉店なので、ここに足を運ぶことができるのもあとわずかである。');
