INSERT INTO public.after VALUES ('IcRhK0EonA', '2010-11-24', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 200, 200, 0, 0, 0, 6, 6, '渋谷', 13, '口説き方', '面識のない女の子にするメールの話題について語った。ののさんや中野は下心を見せてガンガン行くべきだと主張した。');
INSERT INTO public.after VALUES ('E0UolSFXyZ', '2010-11-27', '土', '土曜練', 1, 'GOLDRUSH', '洋食', false, false, 1100, 1100, 0, 0, 0, 6, 6, '渋谷', 7, 'キャプテン集結、そしてあの店が・・・', 'キャプテンはおひつ屋に飽きた、とうことで最後にもかかわらず４人ともGOLDRUSHに集結。
INSERT INTO public.after VALUES ('CVP2nYGb5k', '2010-11-27', '土', '土曜練', 1, 'おひつ屋', '和食', false, false, 800, 800, 0, 0, 0, 8, 8, '渋谷', 17, '今までお世話になりました', '長い間お世話になったおひつ屋とも今日でお別れ。皆別れを惜しみつつ、最後の晩餐を楽しんでいた。マヨネーズの無益さを語る2人の男がいた。');
INSERT INTO public.after VALUES ('GzKLxKbhty', '2010-11-27', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 200, 200, 0, 0, 0, 15, 15, '渋谷', 21, 'クリパ', '一年生がクリパについて話し合っていた。全体メーリスをはじめて使うらしく、怖くて書けないと嘆いている者がいた。今年のクリパは果たしてどうなるのでしょうか。');
INSERT INTO public.after VALUES ('FuUlhDFu42', '2010-12-01', '水', '水曜練', 1, '五右衛門', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 15, 15, '渋谷', 12, '初', 'タパタパのすぐ近くにある五右衛門に入ってみた。種類が豊富でタパタパよりは量もある気がした。ただ例によって客が少なく潰れそうな気がした。');
INSERT INTO public.after VALUES ('fTU5kAmZ3b', '2010-12-01', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 200, 200, 0, 0, 0, 10, 10, '渋谷', 14, '企画いろいろ', '箱根や太平洋〜日本海横断などの企画について話し合った。東海道企画を走った人たちがその辛さをしみじみと語っていた。');
INSERT INTO public.after VALUES ('M9H2PPj1S0', '2010-12-01', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 5, 5, '渋谷', 5, '奥多摩駅伝', '間近に迫った奥多摩駅伝の話をした。なんと当日はののさんが応援に来てくれるらしい。なんていい人なんだ。');
INSERT INTO public.after VALUES ('YKtBbZIjPW', '2010-12-04', '土', '土曜練', 1, 'らんぷ亭', 'どんぶり', false, false, 500, 500, 0, 0, 0, 5, 5, '渋谷', 9, '奥多摩予想', '明日の駅伝にはホノマラから２チームが出場する。各チームの選手を分析しながらどちらが勝つか予想した。');
INSERT INTO public.after VALUES ('9ckyzP50Bw', '2011-05-21', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 20, 20, '渋谷', 24, '学科とか', 'Ｏ氏が工学部についていろいろ説明してくれた。一部卑猥なトークで盛り上がっていた。');
INSERT INTO public.after VALUES ('EYLwWTPVU4', '2010-12-04', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 180, 180, 0, 0, 0, 10, 10, '渋谷', 16, 'マネージャー', '女子マネージャーが欲しいという人がいた。しかし野球やサッカーと違って、マラソンは見てても楽しくないしマネージャーとしてやることも面白味がないので、誰もやりたがらないだろうという結論に至った。');
INSERT INTO public.after VALUES ('PbpRmjYuO5', '2010-12-04', '土', '土曜練', 1, '絶品純豆腐', '韓国料理', false, false, 900, 900, 0, 0, 0, 4, 4, '渋谷', 11, 'スンドゥブ', '卵と私の跡地にできたお店に初入店。純豆腐と書いてスンドゥブと読むそうです。普通でもそこそこ辛い。岡村さんと町田が激辛と格闘していた。');
INSERT INTO public.after VALUES ('FgJ8HeJZU2', '2010-12-05', '日', '奥多摩駅伝', 1, 'にんにく屋', '洋食', false, false, 900, 900, 0, 0, 0, 9, 9, '立川', 8, '忘れた', 'にんにくおいしい。でもにんにく臭かった。');
INSERT INTO public.after VALUES ('Haig25XUA2', '2010-12-01', '水', '水曜練', 1, 'らんぷ亭', 'どんぶり', false, false, 800, 800, 0, 0, 0, 8, 8, '渋谷', 3, '由来', '神戸ランプ亭は神戸には一軒もない。その名前の由来を知ろうとしたが良く分からなかった。');
INSERT INTO public.after VALUES ('HIEnwNbmgk', '2010-12-08', '水', '水曜練', 1, '絶品純豆腐', '韓国料理', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 4, 'お疲れ様です', 'クリパ係はたいへんだなぁ、と思った。
INSERT INTO public.after VALUES ('hyX8aTpmgR', '2010-12-08', '水', '水曜練', 1, 'らんぷ亭', 'どんぶり', false, false, 600, 600, 0, 0, 0, 8, 8, '渋谷', 7, '出し物', 'クリパの出し物について各々語った。駒場祭の時から練習しているという殊勝な方がいた。');
INSERT INTO public.after VALUES ('u9aYEnHlHp', '2010-12-08', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 200, 200, 0, 0, 0, 5, 5, '渋谷', 7, 'ささやか', 'ついにアフターズが一桁人数になってしまった。これが冬のスタンダードらしい。全員閉店直前まで残っていた。');
INSERT INTO public.after VALUES ('OgvefErm6K', '2010-12-08', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 3, 3, '渋谷', 3, '合コン', '合コンでチャラい男にひっかかると大変である。そんなときは終電を言い訳に帰るといいかもしれない。');
INSERT INTO public.after VALUES ('UJPIPDYPl2', '2010-12-11', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 6, '渋谷', 17, '遊園地', '関西にはずいぶんたくさんの遊園地があったという話で盛り上がった．今つぶれてしまったところが多いのだが，それはUSJができたせいなのか不況のせいなのか．
INSERT INTO public.after VALUES ('SUFi5VrwQk', '2010-12-11', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 250, 250, 0, 0, 0, 6, 8, '渋谷', 13, '暗証番号', 'ゆっこのiPhoneのロックを勝手に解除しようと試みる人がいたが，暗証番号を解読できず失敗に終わっていた．
INSERT INTO public.after VALUES ('tMi3Xsp8b3', '2010-12-15', '水', '水曜練', 1, '唐めん', 'ラーメン', false, false, 800, 800, 0, 0, 0, 5, 5, '渋谷', 5, '少人数なので・・・', 'スタバの隣にあるつけ麺屋に入ってみた。つけ麺は量に関係なく一律800円で、スパマッチョを想起させたが、スパマッチョ以上であるのは間違いない。');
INSERT INTO public.after VALUES ('GJvQSPqdb5', '2010-12-15', '水', '水曜練', 1, 'スンドゥブ', '中華', false, false, 900, 900, 0, 0, 0, 11, 11, '渋谷', 11, '指で蛙', '寒さを紛らわすため、指でカエルを作ることにみんな夢中になっていた。関東スタイルと関西スタイルがあるらしい。');
INSERT INTO public.after VALUES ('H2AGMZoVe4', '2010-12-15', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 700, 700, 0, 0, 0, 4, 4, '渋谷', 3, '田舎の条件', '徳島には電車がなく、ディーゼル機関車が走っているらしい。学校への登下校は自転車→ディーゼル→自転車らしい。つまり、自転車が2台いるらしい。');
INSERT INTO public.after VALUES ('fRant4RjQW', '2010-12-15', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 250, 250, 0, 0, 0, 10, 10, '渋谷', 10, 'いろいろ', '色々な企画を話した。その後は、未来のことについて空想を巡らせた。タケコプターは現実のものとなるのか。北朝鮮には軍事クーデタが必要なのか。');
INSERT INTO public.after VALUES ('oH8zkdgYDg', '2010-12-18', '土', '土曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 5, 5, '渋谷', 5, 'しゅうかつ', '小川氏と細越氏が就職活動について語っていた。小川氏がこういう話をするのは新鮮だった。');
INSERT INTO public.after VALUES ('3JImpKnDxV', '2010-12-18', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 200, 200, 0, 0, 0, 15, 15, '渋谷', 14, 'アフターズ', 'アフターズに毎回参加すると仮定しよう。週に５時間とすれば、一ヶ月で２０時間、１年で２４０時間である。つまりアフターズに１０日間かけているわけである。すごい・・・');
INSERT INTO public.after VALUES ('2CAdK4gfJg', '2010-12-18', '土', '土曜練', 1, 'らんぷ亭', 'どんぶり', false, false, 600, 600, 0, 0, 0, 10, 10, '渋谷', 11, '安い', 'ランプ亭は他のアフターの店に比べて安い。明日1日で1万円弱飛んでいきそうな人々にとっては優しい価格設定である。');
INSERT INTO public.after VALUES ('t7R0wrx2PD', '2010-12-18', '土', '土曜練', 1, '日本一の食堂', '和食', false, false, 700, 700, 0, 0, 0, 6, 6, '新宿', 5, '仲良し', 'La Voceのライブを観に行った帰りに近くの定食屋へ。
INSERT INTO public.after VALUES ('JGtgKDStA3', '2010-12-18', '土', '土曜練', 1, '絶品純豆腐', '韓国料理', false, false, 900, 900, 0, 0, 0, 5, 5, '渋谷', 8, 'ロールケーキってバウムクーヘンのことですか？', '翌日に控えたクリパで植竹がロールケーキを作る予定だという話をしているときに細井が発した一言．みんなが丁寧に違いを説明したらどうやら理解したようだ．');
INSERT INTO public.after VALUES ('D9dadI3AVG', '2010-12-22', '水', '水曜練', 1, '唐めん', 'ラーメン', false, false, 800, 800, 0, 0, 0, 8, 8, '渋谷', 9, 'たぱたぱ貸切', 'だった。人数が少ないこともあったので、つけ麺屋へ。つけ麺通の町田には何かもの足りなかったようだ。');
INSERT INTO public.after VALUES ('jKc7FcRGfn', '2010-12-22', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 200, 200, 0, 0, 0, 2, 2, '渋谷', 6, '少人数', '少人数のアフターズだったが、三嶋氏とのアフターズはなかなか経験できないことなので新鮮だった。');
INSERT INTO public.after VALUES ('2fgnM7gh3E', '2010-12-23', '木', '大会', 1, '和民', '居酒屋', false, false, 1000, 1000, 0, 0, 0, 13, 13, '北千住', 10, '遅い、高い', 'ランチ目的で入ったけど、時すでに遅し。お酒を頼んだ方はお通しという罠にはまっていた。');
INSERT INTO public.after VALUES ('kbtokXf9CO', '2010-12-25', '土', '土曜練', 1, 'らんぷ亭', 'どんぶり', false, false, 500, 500, 0, 0, 0, 5, 5, '渋谷', 5, 'ごめんなさい', '来なかったキャプテンに無駄絡みをした。ごめんなさい。');
INSERT INTO public.after VALUES ('Rern1Db3Y7', '2010-12-25', '土', '土曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 4, 4, '渋谷', 4, '左右で段違いのポケット', '今日の夜行バスで規制する川村は，いつものようなジャージではなく，ちゃんとした私服を着ていた．ちゃんとした服を着て帰らないと親に怒られると本人はコメントしていたが，シャツのボタンを掛け違えていては結局親に何か言われるだろう．');
INSERT INTO public.after VALUES ('9iTnWXRLzc', '2010-12-25', '土', '土曜練', 1, 'スンドゥブ', '中華', false, false, 900, 900, 0, 0, 0, 7, 7, '渋谷', 7, '食堂', '高校時代の食堂事情について意見が飛び交った。昼休みにダッシュするのはどこでもおなじみらしい。
INSERT INTO public.after VALUES ('9Tc5mokPB1', '2010-12-25', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 250, 250, 0, 0, 0, 4, 6, '渋谷', 11, '電子書籍', 'スマートフォンやIPadが浸透するに連れて電子書籍の売れ行きが上がっているというが，紙の本が全く売れなくなるくらいまで電子書籍は浸透するのだろうか．確かに電子書籍は手軽だしかさばらないけど，本屋に立ち寄って本を適当に物色するという楽しみは紙の本にしかないよねという話になった');
INSERT INTO public.after VALUES ('GzmCen78DG', '2010-12-29', '水', '水曜練', 1, 'らんぷ亭', 'どんぶり', false, false, 500, 500, 0, 0, 0, 5, 5, '渋谷', 2, '二人', 'ひそかに一人アフターを狙っていたが、二人になってしまった。');
INSERT INTO public.after VALUES ('N3lWU5xtFp', '2010-12-29', '水', '水曜練', 2, 'かにチャーハンの店', '中華', false, false, 700, 700, 0, 0, 0, 4, 4, '渋谷', 4, 'フライパンの動き', '相変わらずチャーハンを作るコックのフライパンさばきに感嘆しつつも、結局見てないところでは大してやっていないことを濱崎がピザの例を挙げて説明していた。');
INSERT INTO public.after VALUES ('epARy9vrrr', '2010-12-29', '水', '水曜練', 3, 'エスト', 'ゲーセン', false, false, 200, 200, 0, 0, 0, 4, 4, '渋谷', 4, '久しぶりに・・・', '細越が最近はまった音楽ゲームがやりたい！と熱心に提案したためゲーセンへ。エストで懐かしのクイズゲームで対戦した後反対側のセガで遊ぶなどして楽しんだ。田儀がクイズの腕が落ちたと悔やんでいた');
INSERT INTO public.after VALUES ('pQWzFKLizW', '2012-08-29', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 650, 650, 0, 0, 0, 10, 10, '渋谷', 10, 'ゲーム', '試験前にも関わらず、前日17時間ゲームをしていたため眠そうな人がいた。');
INSERT INTO public.after VALUES ('OPjodk9RQn', '2010-12-29', '水', '水曜練', 4, 'マクドナルド', 'ファーストフード', false, false, 250, 250, 0, 0, 0, 3, 3, '渋谷', 3, 'あるある？', '１．マックのごみ入れにトレイごと捨ててしまう（濱崎）
INSERT INTO public.after VALUES ('i6jECXGNis', '2010-12-29', '水', '水曜練', 1, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 4, '会いたかった・・・会えた（笑）', '練習している中野達とすれちがいでドトールで暖をとっていると、濱崎と田儀が合流してにぎやかになった。');
INSERT INTO public.after VALUES ('PzTg13u2A3', '2011-01-05', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 7, 'たまには', '４人で取り分けるのも飽きたということで、一人一品で注文した。が、みんな最後の方は味に飽きていた。やっぱり取り分けるのがベストかもしれない。');
INSERT INTO public.after VALUES ('I6vKA2DTqH', '2011-01-08', '土', '土曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 10, 10, '渋谷', 11, '文京区', '文京区は23区の中では田舎である。高い建物があまりない。しかし、そんな文京区を通る白山通りは道幅が広く自主練に適しているようだ。');
INSERT INTO public.after VALUES ('1w8fcYhN5y', '2011-01-08', '土', '土曜練', 2, 'カチカチ山', '飲み', false, false, 1500, 1500, 0, 0, 0, 10, 10, '渋谷', 10, '新年会', '新年会が行われた。カチカチ山のTVではAKB48のPVが流れ続け、AKBに関する話が繰り広げられた。コンパ係は生きていた。');
INSERT INTO public.after VALUES ('Iv6wfVxkMy', '2011-01-08', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 15, '春休み', '春休みの遊ぶ計画を立てた。
INSERT INTO public.after VALUES ('HjnRNLuuMH', '2011-01-08', '土', '土曜練', 1, 'らんぷ亭', 'どんぶり', false, false, 600, 600, 0, 0, 0, 6, 6, '渋谷', 6, 'バイト', 'みんないろいろなバイトを経験しているそうだ。ゴディバで働ければあまりのチョコがもらえるのでは、と思いを巡らしていた。');
INSERT INTO public.after VALUES ('Pouzmfit5a', '2011-01-08', '土', '土曜練', 1, 'ホァングン', 'ベトナム料理', false, false, 800, 800, 0, 0, 0, 8, 8, '渋谷', 5, '1/48', '細越氏の妹さんがAKBのファンらしい。
INSERT INTO public.after VALUES ('uvPYxMyXWn', '2011-01-12', '水', '水曜練', 1, '吉祥寺どんぶり', 'どんぶり', false, false, 700, 700, 0, 0, 0, 4, 4, '渋谷', 4, '前から行ってみたかった。', '練習で軽くアップを済ませ、ねぎしに行くみんなを見送り、吉祥寺どんぶりののれんをくぐると、松屋の特盛を凌ぐであろう丼が現れた。
INSERT INTO public.after VALUES ('HOEIyau0fI', '2011-01-12', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 5, 5, '渋谷', 12, 'キャベツＶＳレタス', '勝者：キャベツ');
INSERT INTO public.after VALUES ('h9kD9JPyDH', '2011-01-12', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 200, 200, 0, 0, 0, 6, 6, '渋谷', 5, 'チーム名', '土曜日に行われる駅伝のチーム名を考えていた。AKBとか御三家とかとりあえず三文字で探してた。結局猪鹿蝶になった。花札やりたいなぁ。');
INSERT INTO public.after VALUES ('CRZEVQ9w1T', '2011-01-15', '土', '土曜練', 1, 'スパマッチョ', 'パスタ', false, false, 600, 600, 0, 0, 0, 7, 7, '渋谷', 6, 'ウイルスの黒幕', 'コンピュータウイルスなどは、蔓延したらウイルス駆除ソフトが売れるので実はウイルスをばらまいてるのはソフトの製作側だったりするのではなかろうか。そしてこれは他にも当てはまるところが多いかもしれない。');
INSERT INTO public.after VALUES ('OKniCTuZRO', '2011-01-15', '土', '土曜練', 2, 'and on and', 'ドーナツ', false, false, 5, 5, 0, 0, 0, 5, 5, '渋谷', 14, 'こいばな', '一人、やたら恋愛話をしたがる女の子がいた。一方で画面の向こう側の女の子に想いを馳せる男の子もいた。しかし、どちらも一人の異性を口説き落とそうとしているという点においては似ていた・・・かもしれない。');
INSERT INTO public.after VALUES ('kc1pFqEsB6', '2011-01-15', '土', '土曜練', 1, '絶品純豆腐', '韓国料理', false, false, 900, 900, 0, 0, 0, 4, 4, '渋谷', 4, 'プレッシャー', '(メニューを見て)
INSERT INTO public.after VALUES ('c7LdpP93NI', '2011-01-18', '火', '本郷練', 1, '中央食堂', '学食', false, false, 500, 500, 0, 0, 0, 7, 7, '本郷', 7, '年賀状', '年賀状の話になった。「ファイト」と間違えて「ファイア」と書いてしまった人や、家も年賀状もシェアした人がいるらしい。');
INSERT INTO public.after VALUES ('9ybiBaoGl6', '2011-01-19', '水', '水曜練', 1, '唐めん', 'ラーメン', false, false, 800, 800, 0, 0, 0, 4, 4, '渋谷', 5, '天下一品', '関西では天下一品が一押しらしいが、あのこってりラーメンは胃にきつい。しかしまあ、あの柚子胡椒の味ブレイカーっぷりは何とかならないのだろうか。');
INSERT INTO public.after VALUES ('KyN1FuBzsw', '2011-01-19', '水', '水曜練', 1, 'らんぷ亭', 'どんぶり', false, false, 600, 600, 0, 0, 0, 5, 5, '渋谷', 5, 'クラス', '最近中野はクラスで話すヤツが林ぐらいらしい。クラスとの関わりがだんだん希薄になっているとか。一年生で一番クラスリア充な奴は町田のようだ。羨ましい限りである。');
INSERT INTO public.after VALUES ('kLXw0h73QG', '2011-01-19', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 200, 200, 0, 0, 0, 5, 5, '渋谷', 5, '将来のこととか', 'and on andは非常に閑散としており、閉店の臭いをプンプン漂わせていた。どうやら一月いっぱいで本当に閉店するようだ。細越氏が持ち前のマシンガントークで松浦をまくしたてていた。');
INSERT INTO public.after VALUES ('hdBy4MInbU', '2011-01-15', '土', '土曜練', 1, 'らんぷ亭', 'どんぶり', false, false, 600, 600, 0, 0, 0, 4, 4, '渋谷', 5, 'ヒマ', '明日、日曜日は皆暇らしい。「武士の家計簿」を見てみたいと言ったら映画を観に行くことになった。');
INSERT INTO public.after VALUES ('BllTUZJa9J', '2011-01-22', '土', '土曜練', 1, 'バンビ', '洋食', false, false, 700, 700, 0, 0, 0, 7, 7, '本郷', 10, 'いいね', '本郷周辺の店は、休みは人は少なく渋谷ほど値段も高くないのでアフターには非常に適している。渋谷という町の限界を感じた。');
INSERT INTO public.after VALUES ('zKXoHph0h9', '2011-01-22', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 200, 200, 0, 0, 0, 6, 6, '本郷', 8, '千葉マリン', '明日は千葉マリンマラソンだが、モチベーションの上がらない人がたくさんいた。出場を断念していた人がいたが、皆に煽られ気持ちが揺らいでいた。頑張ってください。');
INSERT INTO public.after VALUES ('UuZiE0aXmI', '2011-01-26', '水', '水曜練', 1, 'サイゼリヤ', 'ファミレス', false, false, 700, 700, 0, 0, 0, 7, 7, '渋谷', 7, '男', '男だけだったので、皆２品目頼んでいた。人によっては３品目であった。小谷氏が４冠を達成したようだが、この記録はいつか破られるのだろうか・・・？');
INSERT INTO public.after VALUES ('TI2oL5RcHZ', '2011-01-26', '水', '水曜練', 2, 'and on and', 'ドーナツ', false, false, 200, 200, 0, 0, 0, 6, 6, '渋谷', 7, 'ラストナンド', '今日でアフターズとしてアンドナンドを訪れるのは最後。長い間お世話になりました。新歓コンパは能率が極めて低いということで、廃止になるかもしれない。今年の新歓は新歓合宿メインでいこうなどと、議論していた。');
INSERT INTO public.after VALUES ('RDsqF7ujlq', '2011-02-02', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 10, '久々', '一か月ぶりのタパタパである。海Pはやはり至高。');
INSERT INTO public.after VALUES ('Fa0z4F1VNk', '2011-02-02', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 8, 8, '渋谷', 7, '即決', '河田氏が箱根企画の予定を即決し、それに中野は従いメーリスを送っていた。一方で、細井氏が卒業旅行の過酷さを熱弁し、河田氏は意気消沈していた。');
INSERT INTO public.after VALUES ('50eEtzBrgG', '2011-02-05', '土', '皇居練', 1, '海人', 'ラーメン', false, false, 700, 700, 0, 0, 0, 5, 5, '神田', 3, 'エントリーシート', 'ラーメン店なのに３人とも定食を食べていた。最近の企業のエントリーシートは、自分の必殺技についての説明を書かせるようだ。みんな何を書いていくのだろうか・・・？');
INSERT INTO public.after VALUES ('A66KuL8utn', '2011-02-09', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 200, 200, 0, 0, 0, 8, 8, '渋谷', 10, '川村智樹', '「ひげは29日から剃ってない」「進級は神のみぞ知る」「最善は尽くした」「あと2科目(無勉)」だそうです。');
INSERT INTO public.after VALUES ('byNJae5rVD', '2011-02-09', '水', '水曜練', 1, 'つけめん　大臣', 'ラーメン', false, false, 800, 800, 0, 0, 0, 5, 5, '渋谷', 5, 'グルメ評論家', 'グルメ評論家の人たちはそんなに頻繁にうまいものを食う金がどこにあるのか疑問である。とりあえずつけ麺がうまかった。');
INSERT INTO public.after VALUES ('C5OmG72LmH', '2011-02-09', '水', '水曜練', 1, '神座', 'ラーメン', false, false, 800, 800, 0, 0, 0, 2, 2, '渋谷', 2, 'ラーメンというか', '五目ラーメンっていう感じがする。でもなんかハマる味。
INSERT INTO public.after VALUES ('XO94hSkO2T', '2011-05-21', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 3, 3, '渋谷', 18, 'マラソン事業', '小谷氏がウルトラマラソン界で名を上げてビジネスをやりたいらしい。小谷氏「とりあえず日本代表になってからだな」');
INSERT INTO public.after VALUES ('XwmYdDYoZS', '2011-02-09', '水', '水曜練', 1, '絶品純豆腐', '韓国料理', false, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 5, '整形', '韓国では整形が親からプレゼントされるほど一般的なものらしいが、日本では整形は未だに黒歴史的なイメージを持たれている。
INSERT INTO public.after VALUES ('8HDFu7bgmp', '2011-02-12', '土', '土曜練', 1, '？', 'ボウリング', false, false, 1000, 1000, 0, 0, 0, 5, 5, '水道橋', 5, 'ボウリングバトル', '【スコア】
INSERT INTO public.after VALUES ('i2ugDqZ0dm', '2011-02-12', '土', '土曜練', 2, '王将', '中華', false, false, 700, 700, 0, 0, 0, 8, 8, '水道橋', 8, 'おいこんとか', 'おいコンの話をした。まだまだ人は集まるだろう、ということで、参加表明してない方で参加しようか迷ってる人はじゃんじゃん参加していきましょう。');
INSERT INTO public.after VALUES ('6Q5dS1EuIp', '2011-02-12', '土', '土曜練', 3, 'マクドナルド', 'ファーストフード', false, false, 100, 100, 0, 0, 0, 4, 4, '水道橋', 4, 'Ｒ１', '昨日のＲ１、一回戦で敗れたヒューマン中村が一番おもろかったと思ったが、そう思っていたのは自分だけではなかったようだ。');
INSERT INTO public.after VALUES ('UBmcxk2DMQ', '2011-02-16', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 17, '何が起きた', 'タパス風スパゲッティを三つ注文している四人席があった。そんなに頼んだら飽きるんじゃないかと聞いたら、一つは濃いめにしたから飽きないよと言う人がいた。');
INSERT INTO public.after VALUES ('0pw0XClNAI', '2011-02-16', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 200, 200, 0, 0, 0, 8, 8, '渋谷', 13, '朝の連ドラ', '河田氏が今やっている鉄板がおもしろいという話をした流れから、昔の連ドラが列挙されていった。みなさんは何が一番印象的だっただろうか。ちなみに僕が一番好きだったのはてるてる家族です。');
INSERT INTO public.after VALUES ('01gqiGAfGb', '2011-02-19', '土', '本郷練', 1, 'カフェテラス本郷', '洋食', false, false, 1000, 1000, 0, 0, 0, 10, 10, '本郷', 16, 'カフェテラス本郷と言えば', '大盛りメニューが名物なので頼んでいる人達がいた。かなりの量だったが残さずに食べていた。');
INSERT INTO public.after VALUES ('JzGxv4a8Lr', '2011-02-19', '土', '本郷練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 12, '山梨県は関東地方？', '山梨県は関東地方であると主張する人がいた。しかし多くは中部地方だと主張していた。');
INSERT INTO public.after VALUES ('C0byqZRHu4', '2011-02-23', '水', '水曜練', 1, 'らんぷ亭', 'どんぶり', false, false, 600, 600, 0, 0, 0, 5, 5, '渋谷', 5, 'アメリカ', '猫を暖めようと電子レンジに入れたら死んで訴えたら勝訴したという話はよく聞くが、木が選挙で勝ったという事例があるとか。アメリカってこわい。');
INSERT INTO public.after VALUES ('sznNebKwWv', '2011-02-23', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 10, '新歓', '新歓の苦労話を聞いて危機感を覚える中野であった。
INSERT INTO public.after VALUES ('FbDmP91STt', '2011-02-23', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 700, 700, 0, 0, 0, 5, 5, '渋谷', 5, 'テストおつかれさまっちょ', '法学部と経済学部の試験が終わり、植竹と細井が久しぶりにサークルに顔を出した。
INSERT INTO public.after VALUES ('aZ2aoli1FC', '2011-02-26', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 20, 20, '渋谷', 19, 'やたらでかい', 'サンマルクカフェを見つけたので入ってみた。３階建てで１４３席もある。３階をホノマラが牛耳っており、コンパの風景に似ていた。');
INSERT INTO public.after VALUES ('9x6g8997j0', '2011-02-26', '土', '土曜練', 1, 'coco壱', 'カレー', false, false, 700, 700, 0, 0, 0, 8, 8, '渋谷', 5, '新歓', 'みなさん今年はどんな女の子が入ってくるか楽しみなようだ。');
INSERT INTO public.after VALUES ('0aqSAdncQY', '2011-02-26', '土', '土曜練', 1, '？', 'ラーメン', false, false, 800, 800, 0, 0, 0, 3, 3, '渋谷', 3, 'つけめん', '古市が大盛りで苦しんでいるのを尻目に、特盛りをぺロリと平らげる人がいた。');
INSERT INTO public.after VALUES ('pceieZ9tXR', '2011-02-26', '土', '土曜練', 1, 'あらき水産', '和食', false, false, 750, 750, 0, 0, 0, 4, 4, '神田', 4, 'フェロモン', '死に際になるとフェロモンが出るらしい。');
INSERT INTO public.after VALUES ('x34PcU9a95', '2011-03-02', '水', '水曜練', 1, '大臣', 'つけ麺', false, false, 800, 800, 0, 0, 0, 2, 2, '渋谷', 2, '高い満足度', '寒い日の旨辛つけ麺は絶品であった。満足度が高かった。');
INSERT INTO public.after VALUES ('qmrADWbzqC', '2011-02-26', '土', '土曜練', 1, '三田製麺所', 'ラーメン', false, false, 700, 700, 0, 0, 0, 4, 4, '神田', 7, '残念なこと', '同じテーブルに座った3人がまったく同じトッピングを頼んだのに，1人だけメンマの量が3分の1しかなくてしょんぼりしている人がいた．');
INSERT INTO public.after VALUES ('lxrHkSL4Dr', '2011-03-02', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 8, 8, '渋谷', 8, '気になる二人', 'たまたま席が隣同士になった二人がにや付いている場面が目立っていた．そんな大したことを話しているわけではなかったようだが，そこには二人の世界が出来上がっていた．');
INSERT INTO public.after VALUES ('fdS80cW0wB', '2011-03-02', '水', '水曜練', 1, 'かにチャーハンの店', '中華', false, false, 700, 700, 0, 0, 0, 4, 4, '渋谷', 7, '味噌汁のかに', '味噌汁の中にささやかに入っているカニの甲羅．そこから頑張って身を取り出そうとするのか面倒で手をつけないかは人それぞれ．さらに身を取り出す人には，器具を使って丁寧に取り出そうとする人もいれば，豪快に(と言えるほどの量はないのだが)かぶりつく人もいる．非常に奥深い．');
INSERT INTO public.after VALUES ('IPPHA5V8CA', '2011-03-05', '土', '土曜練', 1, 'バンビ', '洋食', false, false, 700, 700, 0, 0, 0, 10, 10, '本郷', 12, '2次元', 'モバゲーの恋愛ゲームは裏切らない、と川村君が言ってました。');
INSERT INTO public.after VALUES ('QWlt6rgOZ5', '2011-03-05', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 7, 7, '本郷', 7, 'aicezuki', '世間で話題の京大受験生の話からそれぞれの受験時代の話になった．理系でありながら国語が大得意という人がいた．');
INSERT INTO public.after VALUES ('ngS1j6haid', '2011-03-09', '水', '水曜練', 1, 'かにチャーハンの店', '中華', false, false, 650, 650, 0, 0, 0, 4, 4, '渋谷', 7, '同クラ', '西田さんと古市さんが同クラだという事実を今更知って驚いた。かにチャーハン大盛りに130円分の価値はないようだった。');
INSERT INTO public.after VALUES ('5iRqKtUXbb', '2011-03-09', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 250, 250, 0, 0, 0, 10, 10, '渋谷', 17, '忍び寄る不幸', '最近のゆっこさんには、マドリードでスリに遭うなど不幸が続いているそうだ。明日から農業体験だそうなので肥溜めに落ちない事を祈るのみである。');
INSERT INTO public.after VALUES ('c4Xexq6qCG', '2011-03-09', '水', '水曜練', 1, 'らんぷ亭', 'どんぶり', false, false, 600, 600, 0, 0, 0, 6, 6, '渋谷', 6, 'ソウルマラソン', 'ソウルでエステに行きたいという女性陣。その間男性陣はグルメツアーでもしとこうかという話になったが、エステもグルメも欲張りたいというのが乙女心らしい。');
INSERT INTO public.after VALUES ('Eh8DBSBsfP', '2011-03-09', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 4, 4, '渋谷', 5, 'とあるランナーの一日', '超長距離部門の世界的ランナーは、通勤ランで20キロ。昼休みに10キロ。昼食はとらないらしい。
INSERT INTO public.after VALUES ('x1W35zNG50', '2011-03-19', '土', '土曜練', 1, '海人', 'ラーメン', false, false, 800, 800, 0, 0, 0, 4, 4, '神田', 4, '震度３', 'アフター中に地震が来た。震度を見て地震への感覚が麻痺してると思った。');
INSERT INTO public.after VALUES ('2Fxesg0wzP', '2011-03-16', '水', '水曜練', 1, 'にじゅうまる', '飲み', false, false, 3000, 3000, 0, 0, 0, 10, 10, '渋谷', 11, '"三年＋三人"会', '三年会に交じって三年生じゃない人が三人いた。どんよりとした世の中の風を吹き飛ばす勢いで楽しい会合が展開された。');
INSERT INTO public.after VALUES ('ubceA1l0eb', '2011-03-19', '土', '土曜練', 2, 'VELOCE', 'カフェ', false, false, 300, 300, 0, 0, 0, 8, 8, '神田', 3, 'キャッチコピー', '就職活動の話をした。その中でたまに聞かれる質問が「あなたのキャッチコピーは何ですか？」この３人のキャッチコピーを是非考えてみてくだい(笑)');
INSERT INTO public.after VALUES ('QY8oaAHuJs', '2011-03-23', '水', '水曜練', 1, 'にじゅうまる', '飲み', false, false, 3000, 3000, 0, 0, 0, 10, 10, '渋谷', 10, 'プチ追いコン', '先週と同じ場所でＭ２の方の追いコンという名目で飲み食べ放題をした。現コンパ係がアフター録を毎回見てないことが判明した。');
INSERT INTO public.after VALUES ('U2QglI7izQ', '2011-03-26', '土', '土曜練', 1, '大臣', 'つけ麺', false, false, 780, 780, 0, 0, 0, 2, 2, '渋谷', 2, '大将がいない', '女性の店員しかいないという、ラーメン屋にしては珍しい店だと感じた。森田は最近つけ麺にハマっているらしい。');
INSERT INTO public.after VALUES ('1txGGtwmXr', '2011-03-26', '土', '土曜練', 2, 'ゲーセン', 'その他', false, false, 200, 200, 0, 0, 0, 3, 3, '渋谷', 3, 'クイズ大会', '岩崎がやるゲームひとつひとつにのめりこんでいた。細越と岩崎が早押しクイズに挑戦したが、両者とも間違いを恐れて解答しないのが印象的であった。');
INSERT INTO public.after VALUES ('Ahu5Aog8wD', '2011-03-26', '土', '土曜練', 1, 'かにチャーハンの店', '中華', false, false, 700, 700, 0, 0, 0, 4, 4, '渋谷', 3, 'スパマッチョ', 'スパマッチョがどこにあれば繁盛するかを考えた。女子大の前にあるよりはＷ大の近くの方がいいと思った。かにチャーハンはどこでも繁盛しそうである。');
INSERT INTO public.after VALUES ('Bjy97Nxhdp', '2011-03-26', '土', '土曜練', 2, 'マクドナルド', 'ファーストフード', false, false, 200, 200, 0, 0, 0, 4, 4, '渋谷', 14, 'ミュージカル', 'ミュージカルが面白くないという主張をする人に対して，経験者を中心にその面白さをいろんな角度から説明していたが，その人はいまいち納得がいかなかったようだ．');
INSERT INTO public.after VALUES ('IaGdYUp821', '2011-03-26', '土', '土曜練', 1, 'らんぷ亭', 'どんぶり', false, false, 600, 600, 0, 0, 0, 6, 6, '渋谷', 6, '新居', 'とある人の新居が大学からも最寄駅からも徒歩一分らしい．何ともうらやましい話である．せっかくだから引越祝いをしたいという話になった．');
INSERT INTO public.after VALUES ('vrubpOvE7F', '2011-03-30', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 9, 9, '渋谷', 17, '練習形式について', '練習場所変える、テント買う、時間早める、など様々な意見が出たが、どれもデメリットがあり厳しそう。');
INSERT INTO public.after VALUES ('IFSP6DztMY', '2011-03-30', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 200, 200, 0, 0, 0, 10, 10, '渋谷', 16, '練習形式について', 'いろんな意見が出たものの、結局今のままで落ち着きそうな空気が漂っていた。');
INSERT INTO public.after VALUES ('zS7qDZUfhI', '2011-04-02', '土', '土曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 4, 4, '渋谷', 4, '応援ツアー', '小谷氏の気分はもうネイチャー。一週間前から現地入りし調整に入る。
INSERT INTO public.after VALUES ('DL2drhyXBr', '2011-04-02', '土', '土曜練', 1, 'スンドゥブ', '中華', false, false, 880, 880, 0, 0, 0, 4, 4, '渋谷', 9, '激辛？', '大村によるとソウルでのホットドックのソースが激辛だったらしく、ケチャップだと思っていたホノマラー達が大苦戦していたらしい。森田が石焼ビビンバの辛さに苦しんでいた。');
INSERT INTO public.after VALUES ('VXgOrGyoRq', '2011-04-02', '土', '土曜練', 1, 'いんでいら', 'インド料理', true, false, 800, 800, 0, 0, 0, 4, 4, '渋谷', 9, 'キャベツ・大根…', 'らんぷ亭が閉店，ねぎしが満席という予想外の事態が次々起こったので，ねぎしの横にあったカレー屋さんに入ることに．
INSERT INTO public.after VALUES ('NqTf512RY0', '2011-04-02', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 5, 5, '渋谷', 12, '成績表', 'あるテレビ番組で東大生に調査をしたところ，成績がオール5だったのは15%くらいだったらしい．体育や芸術系が難関なのか．
INSERT INTO public.after VALUES ('j0tPZDL9yE', '2011-04-06', '水', '水曜練', 1, 'いんでいら', 'インド料理', true, false, 800, 800, 0, 0, 0, 5, 5, '渋谷', 5, '辛い？', '森田氏がまたもや辛さに悶えていた。中辛なのに・・');
