INSERT INTO public.after VALUES ('o7p0frSUt8', '2011-06-15', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 9, 'AKINATER', '吉江氏のアイパッドでアキネーターというアプリを遊んだ。おもしろすぎて皆熱中していた。というか、すごすぎてやばい。
INSERT INTO public.after VALUES ('KyLG7Gc6Y9', '2011-06-18', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 700, 1000, 0, 0, 0, 4, 4, '渋谷', 15, '忘れた', '誰か覚えてる人編集してください！');
