INSERT INTO public.after VALUES ('mQi8WLPMjk', '2011-06-22', '水', '水曜練', 2, '金の蔵', '飲み', false, true, 1000, 1000, 0, 0, 0, 15, 15, '渋谷', 9, 'ヽ(^o^)丿', '最近何かとネガティブだったお二人を祝う会。約３名、無礼講な２年生がいた。');
INSERT INTO public.after VALUES ('4L8CZaSUGs', '2011-06-22', '水', '水曜練', 1, 'GOLDRUSH', '洋食', false, false, 1100, 1100, 0, 0, 0, 5, 5, '渋谷', 5, 'テストとか', '一年生はそろそろ初めてのテストのことが気になっているようだ。英語１のつまらなさは学年も国籍も関係なく不変である。');
INSERT INTO public.after VALUES ('NIiUWbuFIL', '2011-06-25', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 10, '進路', '三年生達が各々の進路のことを話していた．その中で，理系の人間が特に何も考えずに周囲に流されて大学院に進学するという選択肢をとるのはよくないと主張する人がいた．');
INSERT INTO public.after VALUES ('RZb46ASqHd', '2011-06-25', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 6, 6, '渋谷', 21, '取締役', '取締役の定義が何だという話から転じて，会社の役職をホノマラに当てはめてみたらという話題になった．社長はキャプテンなのかというと実はそうではないらしい．');
INSERT INTO public.after VALUES ('kushEChXVm', '2011-06-22', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 5, 5, '渋谷', 5, '意気込み', 'うれしいお知らせを運んでくるとともに，この後行われる二年会の二次会に合流して飲むぞと意気込みを語っている人がいた．');
INSERT INTO public.after VALUES ('1pYewi1Pjv', '2011-06-25', '土', '土曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 6, 6, '渋谷', 6, '俺たちは怖いのか？', '一年生と絡んでいないとばやく原の言葉をきっかけに、次々に同意者が現れた。そしてスパマッチョにやはりいない一年生。とりあえず練習からもっと絡みましょう。');
INSERT INTO public.after VALUES ('Ulh5wlGlHf', '2011-06-29', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 800, 800, 0, 0, 0, 10, 10, '渋谷', 13, 'カレー', 'ガストのカレーは多彩。サイズを選びトッピングも様々。タイカレーなどのちょっと上品なカレーも食べられる。潰れる前に是非一度ご賞味あれ。');
INSERT INTO public.after VALUES ('rAl1zWwvUf', '2011-06-29', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 200, 200, 0, 0, 0, 10, 10, '渋谷', 8, '内部事情', '西はとある男子ホノマラーに恋心を抱いている。今日もマイミク申請が承諾されて喜んでいた。あと、のろけ話をちょっと聞いたり、野球の話をしたり。');
INSERT INTO public.after VALUES ('30bKFxRepM', '2011-07-02', '土', '土曜練', 1, 'がんこ爺', '飲み', false, false, 3100, 3100, 0, 0, 0, 40, 40, '渋谷', 43, '忘れた', 'メンバー間違ってたらごめんなさい。楽しい飲みでした。');
INSERT INTO public.after VALUES ('DxnY4l91ii', '2011-07-04', '月', '本郷練', 1, 'サイゼリヤ', 'ファミレス', false, false, 680, 680, 0, 0, 0, 8, 8, '本郷', 9, '誕生日席', '誕生日にお誕生日席に座る。ありそうでないことが起こっていた。そして暑さのせいかドリンクバーが大盛況。');
INSERT INTO public.after VALUES ('OgYccGvnBM', '2011-07-02', '土', '土曜練', 2, '金の蔵', '飲み', false, false, 1300, 1300, 0, 0, 0, 20, 20, '渋谷', 26, '忘却の彼方', '同じくメンバーの記憶に不安あり。いろんな会話ができて楽しかった。そしてまっつんさんが９９９回目のアフターです。');
INSERT INTO public.after VALUES ('xnzhnqcX36', '2011-07-06', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 12, 'たぱたぱ！', '久しぶりにタパタパに来るとその美味しさに感動してしまう。夏限定メニューもあり楽しめた。月１、２回が妥当かもしれない。');
INSERT INTO public.after VALUES ('KkkDMPv0fE', '2011-07-06', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 8, '水鉄砲', '水鉄砲大会の詳細なルールについて話し合った。なかなか難航したが、ルールの大枠は決まった？ようだ。陣地を作るのが大切。');
INSERT INTO public.after VALUES ('nSTWlgClTh', '2011-07-09', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 700, 700, 0, 0, 0, 20, 20, '渋谷', 18, '誕生日', '誕生日の近い人がいたので、みんなでイチゴシフォンなるものを食べた。ごちそうさまです。');
INSERT INTO public.after VALUES ('E4a6pMHQdS', '2011-07-09', '土', '土曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 10, 10, '渋谷', 4, '固定', 'スパマッチョのアフターはメンバーがいつも固定されている気がする。もう少し皆を呼び込めないものか・・・。ぺぺマッチョがおいしかった。');
INSERT INTO public.after VALUES ('r4m8ZbxcyW', '2011-07-09', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 15, '合宿のしおり', '合宿のしおりを作成したらどうかという話になった。内容を充実をさせるために様々な提案がなされた。果たしてどうなるのか・・・');
INSERT INTO public.after VALUES ('5D040qYfN5', '2011-07-10', '日', '皇居練', 2, 'VELOCE', 'カフェ', false, false, 220, 260, 0, 0, 0, 8, 8, '神田', 7, 'ボウリング', 'ほのまらーのボウリングのフォームについて。
INSERT INTO public.after VALUES ('EXKLPpuII2', '2011-07-10', '日', '皇居練', 1, '三田製麺所', 'ラーメン', false, false, 700, 700, 0, 0, 0, 4, 4, '神田', 9, '合宿', '合宿のバスの席順はどうやって決められているのか、というよくある話題。
INSERT INTO public.after VALUES ('5S4CWJABMy', '2011-07-13', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 12, 12, '渋谷', 12, '熊本城', '去年の１１月に行った熊本城の話をしたら、城好きの遠山が予想以上にくいついてきた。これがギャップ萌えというやつか・・・');
INSERT INTO public.after VALUES ('bETdc71Y3f', '2011-07-13', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 20, 20, '渋谷', 17, '紹介分', 'しおりに載せようという話になっているメンバー紹介をみんなで書いた。楽しそうに書いていた。');
INSERT INTO public.after VALUES ('wBSUeM56BN', '2011-07-13', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 750, 750, 0, 0, 0, 10, 10, '渋谷', 11, 'ノーマネー', '財布の中に400円しか入っていない1年生を含むノーマネー組はガストに行きました。ノーマネーとか言いつつ、今日誕生日の人と昨日誕生日だった人をビールでお祝いしました。');
INSERT INTO public.after VALUES ('GIaPwafF1V', '2011-07-14', '木', '本郷練', 1, 'サイゼリヤ', 'ファミレス', false, false, 650, 650, 0, 0, 0, 6, 6, '本郷', 6, 'ドリンクバー', '暑い中走る→喉がかわく→ドリンクバー！→サイゼリ行こうか、という最近のパターン。岩崎がタルトとパスタを一緒に食べていた。');
INSERT INTO public.after VALUES ('wOBuTXfaMM', '2011-07-16', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 700, 700, 0, 0, 0, 15, 15, '渋谷', 13, '激辛企画', '下北沢の某店に激辛カレーを食べに行こうという話が持ち上がった。辛党でも涙が出るレベルらしい。');
INSERT INTO public.after VALUES ('YENmiWhNAv', '2011-07-16', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 11, '3種の話題', 'Twitterが白熱した後、遠距離恋愛の何たるかを語り、そしてダイエットについて話した。やはり色々な価値観があるようだった。');
INSERT INTO public.after VALUES ('lEgL4F4d0q', '2011-07-20', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 9, 'むりやり', '狭くて近いほうのねぎしに入った。西は口ばかり動かして食べるのがとても遅い');
INSERT INTO public.after VALUES ('w04YGfAvAX', '2011-07-20', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 5, 5, '渋谷', 5, 'バス座席', '厳正なるアミダくじでバスの座席表を決めた。');
INSERT INTO public.after VALUES ('C2CB62Jk8d', '2011-07-23', '土', '土曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 10, 10, '渋谷', 9, 'ついに新アフターズ先が・・・', '潰れる前にすぱまっちょに来たという人が数名いたが、まだまだその気配は見せないほど盛況だった。そういえば、アンドナンドの跡地にサンマルクカフェができていた。7/30オープンなので、7/30はホノマラに来てアフターズ参加決定！');
INSERT INTO public.after VALUES ('pFcoZ6wQ0x', '2011-07-23', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 17, '新鮮', 'いつもの３人がいないので、いい意味でも悪い意味でも新鮮だった。タコ紹介を書いたりした。褒められるよりもいじられるほうが良いという考え方もある。');
INSERT INTO public.after VALUES ('7rnmvEQG5S', '2011-07-23', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 700, 700, 0, 0, 0, 6, 6, '渋谷', 13, 'なにかがおかしい', '合宿の部屋割について、男4人部屋に2人組を2ペア入れたらダブルデートになるね、という話になった。何もかもおかしい。');
INSERT INTO public.after VALUES ('AxliKcDm1O', '2011-07-24', '日', '皇居練', 2, 'VELOCE', 'カフェ', false, false, 200, 300, 0, 0, 0, 5, 5, '神田駅周辺', 5, '免許', 'ペーパードライバーの運転がいかに恐いかについて語った。ある人は運転するだけで近所がちょっとした騒ぎになったらしい。');
INSERT INTO public.after VALUES ('8t0RyUn20F', '2011-07-24', '日', '皇居練', 1, '斗樹', 'つけ麺', false, false, 800, 800, 0, 0, 0, 5, 5, '神田駅周辺', 6, '大盛', 'ののさんが大盛に挑戦していた。だいぶ多かったようで、最後は近くの人に食べてもらっていた。');
INSERT INTO public.after VALUES ('q502nH4AqV', '2011-07-24', '日', '富士登山', 1, 'サイゼリヤ', 'ファミレス', false, false, 500, 500, 0, 0, 0, 7, 7, '新宿', 7, '硬貨', '硬貨だけで7人分の会計をした。その会計を後輩に任せ、店員を困惑させるホノマラ—達であった。');
INSERT INTO public.after VALUES ('kNQHKozGd8', '2011-07-27', '水', '水曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 9, '部屋割', '合宿の男子部屋割を厳正なくじ引きの下で実施した。皆の監視の下、完全なランダムで行われたわけだが、「ヤバい」としか形容のしようがない部屋が出現した。');
INSERT INTO public.after VALUES ('rGnOH5LnJ1', '2011-07-27', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 700, 700, 0, 0, 0, 17, 17, '渋谷', 17, '手なづける', '某4年生によれば、"手なづける"とは、自分のようになってほしいと思わせることを意味するらしい。');
INSERT INTO public.after VALUES ('KMxozIXtsc', '2011-07-29', '金', '皇居', 1, 'あらき水産', '和食', false, false, 800, 800, 0, 0, 0, 4, 4, '神田駅周辺', 4, '金曜日', '今日は金曜日。日曜日とは違い、あのあらき水産が営業中であることに狂喜乱舞している人がいた。');
INSERT INTO public.after VALUES ('UU0MMW2jXX', '2011-07-30', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 8, '公務員', '地方分権が進む中、これからは国家公務員よりも地方公務員のほうが人気が高まるかもしれない、とのこと。しかし、タパタパのおいしさはいつまでも変わらずにいてほしいと願う僕であった。');
INSERT INTO public.after VALUES ('rAfGeVIwgV', '2011-07-30', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 20, 20, '渋谷', 20, 'アンドナンドからサンマルクへの昇華', 'アンドナンドが潰れてから約半年の沈黙を経て、ついにあの場所がサンマルクカフェとして戻ってきた。店内は机からソファまで豪華でホノマラには若干似つかわしくないが、居心地の良さはこれまでのアフターズのどの店も凌ぐのではないかと感じた。店内を出て階段を上り、帰宅の途につくあの感覚を久しぶりに味わい感動を覚えたホノマラーも少なくないようだった。ありがとうサンマルクカフェ、そしてさようならドトール。');
INSERT INTO public.after VALUES ('roj9tJhnjo', '2011-07-30', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 750, 750, 0, 0, 0, 6, 6, '渋谷', 15, '合宿', 'いよいよ合宿まで1週間を残すだけとなった。最近のホノマラは合宿の話題で持ちきりである。楽しみで仕方がない。');
INSERT INTO public.after VALUES ('uquqwInL1I', '2011-07-31', '日', '皇居練', 1, '鳥貴族', '居酒屋', false, false, 2000, 2000, 0, 0, 0, 6, 6, '神田駅周辺', 5, '内定', 'とある人の内定を祝った。来年からガッポリ稼いでホノマラに還元してくれるとか。');
INSERT INTO public.after VALUES ('mUGDdJSPre', '2011-08-03', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 14, '一年生', '合宿前で浮ついているのか、一年生が多く参加した。しかし、合宿の話かと思いきや麻雀の話しだった。一方で卑猥な話題で盛り上がる席があった。');
INSERT INTO public.after VALUES ('Bsbm7oxobE', '2011-08-03', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 750, 750, 0, 0, 0, 4, 4, '渋谷', 15, '写真', '西が某2年生の写真を見て狂喜乱舞していた。');
INSERT INTO public.after VALUES ('wwHzyIO8FV', '2011-08-03', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 4, 4, '渋谷', 5, '3男', '今日も仲のいい3年生男子なのでした。');
INSERT INTO public.after VALUES ('uZZi1zvyzZ', '2011-08-06', '土', '土曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 4, 4, '渋谷', 7, '早い', 'ガストにも飽きてきたのでうどん。あっという間に食べ終わり、19時30分前にはサンマルクに入っていた。');
INSERT INTO public.after VALUES ('Vsh7DqAeWQ', '2011-08-06', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 250, 250, 0, 0, 0, 8, 8, '渋谷', 11, '名探偵', '名探偵カワタが某漫画の主人公ばりの誘導尋問で1年生の秘密を暴いていた。');
INSERT INTO public.after VALUES ('iqZptKiTWF', '2011-08-10', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 15, 15, '渋谷', 15, 'スパマッチョ', 'ホノマラランキングの「スパマッチョ」も記憶に新しいのでアフターはスパマッチョへ。絶賛する人もいる一方で酷評している人もいた。やはりスパマッチョは人を選ぶようだ。');
INSERT INTO public.after VALUES ('RSR2K14ZjR', '2011-08-10', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 400, 400, 0, 0, 0, 10, 10, '渋谷', 9, 'ホノマラ三昧', '土曜にホノマラ参加している人は５日連続で一日をホノマラー達と過ごしているわけだが、飽きる様子もなく皆思い思いにしゃべっていた。');
INSERT INTO public.after VALUES ('s2uAKXKk3y', '2011-08-13', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 700, 700, 0, 0, 0, 5, 5, '渋谷', 21, '500', '荒木さんがアフター回数500回に達した。まっつんさんへの道のりは遠い。');
INSERT INTO public.after VALUES ('Gh0GLGCD7i', '2011-08-13', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 250, 250, 0, 0, 0, 6, 6, '渋谷', 18, '進級', 'ホノマラのファッションリーダー川村智樹が進級を果たした。めでたい。');
INSERT INTO public.after VALUES ('qT6P2FXUMx', '2011-08-17', '水', '水曜練', 1, 'ねぎし', '焼肉', false, false, 880, 1480, 0, 0, 0, 3, 5, '渋谷', 5, '九州男児', '5人中3人が九州出身ということで、九州トークで盛り上がっていた。九州勢は甲子園ベスト8に一つも入れなかったらしい。無念。');
INSERT INTO public.after VALUES ('HdXDLWfbhs', '2011-08-17', '水', '水曜練', 1, 'はなまるうどん', 'うどん', false, false, 350, 350, 0, 0, 0, 4, 4, '渋谷', 6, '安い', 'はなまるうどんは安くて早い。代替わりが徐々に近づいてきている。');
INSERT INTO public.after VALUES ('oBSFKnfSig', '2011-08-17', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 12, '寒い', '異様に冷房が効いていて寒かった。いつもサンマルクの豪華な内装に気を取られがちだが、上を向いてみると天井は舗装されていなかった。お金が足りなかったのだろうか。');
INSERT INTO public.after VALUES ('ALUz93rY1N', '2011-08-17', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 8, 8, '渋谷', 4, '裏メニュー', '細井が裏メニューを初めて頼んでみた。その名もキムマッチョ。そこそこ辛かった。');
INSERT INTO public.after VALUES ('1NLAjHAnCt', '2011-08-20', '土', '土曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 6, '100km', '歴戦のホノマラーたちが北海道100kmに挑もうとしている。全員完走を願うばかりである。');
INSERT INTO public.after VALUES ('etiKrQuFyi', '2011-08-20', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 350, 350, 0, 0, 0, 5, 5, '渋谷', 14, 'どうしてこうなった', 'ホノマラに入った当初、1年生の多くは「卒業までにフル走れたらなーと思って」と言う。しかしなぜか彼らの多くはそこから1年以内に自主的にフルを経験することになる。この現象に何と言う名前を付けたらいいだろうか。');
INSERT INTO public.after VALUES ('UnVkmf0Iug', '2011-08-24', '水', '水曜練', 1, 'はなまるうどん', '和食', false, false, 400, 400, 0, 0, 0, 12, 12, '渋谷', 12, '次世代', 'もう話題は代替わりである。次の役職は誰になるかを勝手に話していた。大会係は誰だろう。');
INSERT INTO public.after VALUES ('dQuXwirec8', '2011-08-24', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 12, 12, '渋谷', 12, '色んな話', '1男二人と2男一人で広く浅く色んな話をした。途中多くの人が飲みに消えてしまった。');
INSERT INTO public.after VALUES ('jR9JpFWdWe', '2011-08-20', '土', '土曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 6, 6, '渋谷', 5, '最近のアフター事情', '最近のアフター事情を話した。かつてはアフターズでオールすることがあったらしい。');
INSERT INTO public.after VALUES ('pM01VXTs4E', '2011-08-27', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 800, 800, 0, 0, 0, 16, 16, '渋谷', 16, '誕生日', 'やはり気付けば話題は代替わり。そんな中、石河さんと白崎さんの誕生日が同じであることが判明した。');
INSERT INTO public.after VALUES ('dTBhMhauEH', '2011-08-27', '土', '土曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 4, 4, '渋谷', 7, 'ターミネーター', '珍しく客の入りがよく、いつもはターミネーターだけが座っているテーブルを使用することになった。
INSERT INTO public.after VALUES ('zztqiK4OUd', '2011-08-27', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 340, 340, 0, 0, 0, 19, 19, '渋谷', 19, 'バスター', '現在の法律では、コンピュータウイルスは持っているだけで罰せられるらしい。ウイルスをもらってしまう前に、早めのバスターをおすすめしたい。');
INSERT INTO public.after VALUES ('hFFXOsRVRX', '2011-08-31', '水', '水曜練', 1, 'はなまるうどん', '和食', false, false, 400, 400, 0, 0, 0, 10, 10, '渋谷', 10, 'ハナマラー', '当初はガストの予定だったが、多くのホノマラーがはなまった。近所のコンビニの店員に名前を覚えられている人がいた。');
INSERT INTO public.after VALUES ('mICEBGAUfp', '2011-08-31', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 5, 5, '渋谷', 5, '総選挙後・・・', 'ＳＰＭ総選挙で醤油バターが１位になったらしいが、誰も頼んでいる人はいなかった。吉田氏がメガに悪戦苦闘していた。');
INSERT INTO public.after VALUES ('m9wEQa76c0', '2011-08-31', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 400, 400, 0, 0, 0, 10, 10, '渋谷', 17, '諏訪湖', '小谷氏が、来週諏訪湖まで２００ｋｍの旅を企画しているようだ。今回の犠牲者は誰になるのか。');
INSERT INTO public.after VALUES ('0UalenB6hq', '2011-08-31', '水', '水曜練', 3, 'すずめのおやど', '飲み', false, false, 1000, 1000, 0, 0, 0, 11, 11, '渋谷', 8, '合宿打ち上げ', '終始、町田に絡み続ける川村であった。');
INSERT INTO public.after VALUES ('PP2mUjCA0L', '2011-08-31', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 800, 800, 0, 0, 0, 4, 4, '渋谷', 4, 'カレログ', '今話題の浮気防止＆ストーカーアプリ「カレログ」について。
INSERT INTO public.after VALUES ('PuRV3alDq9', '2011-09-03', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 7, '昭和', '川村さんに率いられタパタパへ。平間さんとベテランホノマラ−との年の差にただただ驚いていた。');
INSERT INTO public.after VALUES ('mX7znpVI3f', '2011-09-03', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 400, 400, 0, 0, 0, 8, 8, '渋谷', 7, '田儀さんがんばって！', '田儀さんが店の前で手を振って迎えてくれた。出発前最後のホノマラである田儀さんに対し、浦佐メンバーからも電話があった。');
INSERT INTO public.after VALUES ('vpjTZew0UA', '2011-09-04', '日', '浦佐マラソン', 1, '王将', '中華', false, false, 900, 900, 0, 0, 0, 4, 4, '渋谷', 4, '浦佐マラソン', '浦佐は楽しいという結論に至った。目標を達成できた人も多く、全体的に充実した大会であったように思う。');
INSERT INTO public.after VALUES ('uW4MEY1aAW', '2011-09-07', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 700, 700, 0, 0, 0, 10, 10, '渋谷', 7, '大会とか', '芦田氏を尊敬する二人の女の子が彼女と同じ大会に出る意義について熱く語っていた。「二人は仲がいいね」という言葉に全力で首を振っていたが仲がいいのは誰が見ても明白。');
INSERT INTO public.after VALUES ('X6EE7LrvBr', '2011-09-07', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 6, 6, '渋谷', 6, 'メガマッチョと100km', 'メガマッチョはきつくなるポイント、ペースの上げ下げ、エイド(具)の取り方など、100kmのレースを縮尺したものだと気付いた。きっとツラかったのに再び挑戦してしまう点もだろう。');
INSERT INTO public.after VALUES ('GxJs3JXsJw', '2011-09-07', '水', '水曜練', 1, '丸亀製麺', '和食', false, false, 400, 400, 0, 0, 0, 9, 9, '渋谷', 9, 'うどん屋', '最近アフターでのうどんが流行ってきた。こちらは店内の様子がおひつ屋に似ていた。');
INSERT INTO public.after VALUES ('vjyYpcG55H', '2011-09-07', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 19, 19, '渋谷', 19, 'マザー牧場', '3年生の学年旅行ではマザー牧場に行くらしい。マザー牧場は千葉にある。');
INSERT INTO public.after VALUES ('V1hyIkVEqJ', '2011-09-10', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 13, 13, '渋谷', 13, '芸能人', '自分の地元が出身の芸能人や、初めて会った芸能人の話をした。福岡県出身は多いようだった。初めて会ったのがサバンナの八木だという人がいた。');
INSERT INTO public.after VALUES ('PagJsm4Uyt', '2011-09-10', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 350, 350, 0, 0, 0, 15, 15, '渋谷', 15, 'ウルトラ化', '最近あった浜頓別の他にも、秋田や小豆島などウルトラな話をした。この頃のホノマラはフルマラソンの壁を破り、ウルトラ化傾向にある。');
INSERT INTO public.after VALUES ('05f2334uci', '2011-09-14', '水', '水曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 8, 8, '渋谷', 8, '大学院', '小谷氏はたまに研究室に行って研究しているふりをしているらしい。最近は院に行った意味を考えているとか。。');
INSERT INTO public.after VALUES ('oUTb7T07UZ', '2011-09-14', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 800, 800, 0, 0, 0, 8, 8, '渋谷', 7, '法学部生', '法学部の試験が終わったらしく、法学部生が開放感にひたっていた。
INSERT INTO public.after VALUES ('KDCAwLfpz1', '2011-09-14', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 13, 13, '渋谷', 13, 'ゲーム', '学年旅行や泊まりがけの大会などの時にやるゲームの話をした。やはり罰ゲーム的なものがあると盛り上がるようだった。細越氏がおもしろそうなポーカーの話をしていた。');
INSERT INTO public.after VALUES ('twkUCyxhwg', '2011-09-17', '土', '土曜練', 1, 'はなまるうどん', '和食', false, false, 400, 400, 0, 0, 0, 10, 10, '渋谷', 9, '女性限定', '男性は、名古屋ウィメンズマラソンにエントリーできないのか、それともエントリーはできるけど失格になるのか。女子大を受験できないのか、それとも受験はできるけど必ず不合格になるのか。そんな話でした。');
INSERT INTO public.after VALUES ('SAFRhNQnmG', '2011-09-17', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 6, 6, '渋谷', 6, 'ハリポタ', '人数が少なく小規模なアフターズになった。山下と錦織がハリーポッターについて熱く語っていた。');
INSERT INTO public.after VALUES ('PVZl8QuV0R', '2011-09-26', '月', '本郷練', 1, 'サイゼリヤ', 'ファミレス', false, false, 700, 700, 0, 0, 0, 6, 6, '本郷', 7, 'やきとり大将', '時差ボケのせいか、とても眠そうだった金崎。NYで楽しかったとこはどこ？と聞かれて「東京のほうが楽しい。やきとり大将というお店が一番よかった」と語っていた。');
INSERT INTO public.after VALUES ('kV2gqy4Hek', '2011-09-28', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 5, 5, '渋谷', 5, 'ねぎし', '人数少なかったので近いほうのねぎしへ。やはり肉はおいしい。進振りが終わった２年生の学科について話した。');
INSERT INTO public.after VALUES ('sfsLMM4k9H', '2011-09-28', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 8, 8, '渋谷', 12, '名前とか', '自分の子供にどういう名前をつけるか、という話。「仁」とか「修」とか「春夏秋冬」をいれたいとか漢字３文字がいいとか。ちなみにとある人はネットでは女の子の名前を騙っているらし。、');
INSERT INTO public.after VALUES ('gcWBx4LE0e', '2011-09-28', '水', '水曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 4, 4, '渋谷', 6, '成績', '10/3に1年生の成績が発表される。西がどのような成績を取ったらみんなが盛り上がれるかについて妄想した。');
INSERT INTO public.after VALUES ('ayq9pOQwZY', '2011-10-01', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 12, 12, '渋谷', 15, 'タパタパ', 'かなり久しぶりのタパタパアフター。行ってない間に新しい店員さんが加入していたことに気づくほど久しぶり。タパタパが初という１年生がいて意外だった。タパタパはもはやホノマラのアフター先の古株ともいえるので、これからはもう少し増やしていこうと思うキャプテンであった。');
INSERT INTO public.after VALUES ('Ti0357a1MP', '2011-10-01', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 350, 350, 0, 0, 0, 15, 15, '渋谷', 22, '長い', '練習が４時からになったので、アフターズが普段より１時間ほど長くなる。それでも話題のタネは尽きることなく皆思い思いの雑談を交わしていた。西が履修の仕組みについてまったくといっていいほど理解していなかったので細越氏が熱心に解説していた。');
INSERT INTO public.after VALUES ('cnRZoiYVVW', '2011-10-02', '日', '白井梨マラソン', 1, 'サイゼリヤ', 'ファミレス', false, false, 700, 700, 0, 0, 0, 4, 4, '白井', 7, '何もない', '昔は色々あったという白井駅前だが、ケーズデンキに侵食されて何もなかったので、割と歩いてサイゼリヤに行った。');
INSERT INTO public.after VALUES ('eKTqcSfZIj', '2011-10-05', '水', '水曜練', 1, 'まるげん', '飲み', false, false, 2000, 2000, 0, 0, 0, 5, 5, '渋谷', 5, '一年後のそれぞれ', 'なんとメンバー全員１００キロランナーというメンツで飲み。それぞれの内定式の様子を話し、違いに一喜一憂していた。芦田が来年から一人暮らしが始まることにテンションが上がっていた。');
INSERT INTO public.after VALUES ('zx9T5j0a5i', '2011-10-05', '水', '水曜練', 1, 'MARUGEN', '飲み', false, false, 2500, 2500, 0, 0, 0, 14, 14, '渋谷', 17, '1年会', '多くの1年生が集まってくれた。高羽のきのこの山ネタに動揺を隠せない1女さんだった。');
INSERT INTO public.after VALUES ('hqPDg9ahwV', '2011-10-08', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 750, 750, 0, 0, 0, 4, 4, '渋谷', 15, '値上がり', 'ガストのメニューが変わっていた。驚きの値上がりを見せ、唖然とするホノマラーだった。アンケートの項目に「失望」というものがあったのだが、自覚しているのだろうか。');
INSERT INTO public.after VALUES ('tVomUxmm3F', '2011-10-08', '土', '土曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 10, 10, '渋谷', 8, 'カレマッチョ', 'スパマッチョの新作カレマッチョが気になったので、迷いなくスパマッチョへ入るホノマラー。もはや「スパ」の文字は失われており名前からはどんな食べ物か想像するのも難しいが、本当にソースがカレー味のスパゲッティだった。なかなか好評だった。しかし、中野と植竹はその辛さゆえ異常なほど汗をかいていた。');
INSERT INTO public.after VALUES ('iSYQItuu9k', '2011-10-08', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 28, '恋沙汰いろいろ', 'サンマルクが混んでいたので久々にドトールへ。２年生二人が恋の悩みを打ち明けていた。周囲はニヤニヤしながら聞いていた。途中で伊豆旅行から帰ってきたとあるカップルがアフターズに来た。お似合いだった。嫉妬から揶揄する人たちもいた。');
INSERT INTO public.after VALUES ('NRz69tUDeX', '2011-10-08', '土', '土曜練', 1, 'はなまるうどん', '和食', false, false, 400, 400, 0, 0, 0, 11, 11, '渋谷', 11, '話題', 'このアフター録の話題の欄には何を書くのが適切なのかという話。わからなくなってきたので、石河氏が「次に出た話を今日の話題にしよう」と言った。誰もが口をつぐんだ。');
INSERT INTO public.after VALUES ('vkB3KLqKWy', '2011-10-09', '日', '皇居練', 1, 'ターリー屋', 'インド料理', false, false, 850, 850, 0, 0, 0, 4, 4, '神田', 7, 'アタック25', 'アタック25の大学生大会を見てから練習に来た人がいた。最後のボーナス問題が簡単すぎたらしい。そして、関西ローカルと勘違いしている人がいた。');
INSERT INTO public.after VALUES ('HA8AmIUuS7', '2011-10-09', '日', '皇居練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 5, 5, '神田', 5, '干し芋が…', 'さりげなく言った一言がダジャレだと気づいてツッコミが入る前に笑っている人がいた。');
INSERT INTO public.after VALUES ('R3PheBDsrO', '2011-10-12', '水', '水曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 4, 4, '渋谷', 7, 'コンパ', '過去のコンパの話をした。そして今は亡きおひつやに想いを馳せた。');
INSERT INTO public.after VALUES ('Pf5BuD2z4c', '2011-10-12', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 8, 8, '渋谷', 8, 'カレーマッチョ', '米3合を一食でたいらげたことがあるという高田は、初メガマッチョにも動じなかった。また、スパマッチョで帰れま10をやったらどうなるかについて考えた。');
INSERT INTO public.after VALUES ('ZRo6qZaJND', '2011-10-12', '水', '水曜練', 1, 'サンマルクカフェ', 'カフェ', false, false, 350, 350, 0, 0, 0, 5, 5, '渋谷', 12, '忘れた', '１年生の成績について話したり、バイトの話をしたような気がしたが、詳細はあまり思い出すことができない。');
INSERT INTO public.after VALUES ('7HGNurMXOl', '2011-10-16', '日', '甲州フルーツマラソン', 1, '達翁', 'つけ麺', false, false, 700, 700, 0, 0, 0, 5, 5, '新宿', 5, '辛い', '石河さんが辛さに耐えていた。河田さんが600gをたいらげていた。');
INSERT INTO public.after VALUES ('0BSAhksROk', '2011-10-15', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 800, 800, 0, 0, 0, 17, 17, '渋谷', 18, '速報', '小谷さんの出ている神宮外苑の速報ページを情報通の上杉が開いていた。代替わりの話もした。キャプテンはいまだ未定らしい。楽しみである。');
INSERT INTO public.after VALUES ('KXCSB7lYCh', '2011-10-15', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 390, 390, 0, 0, 0, 10, 10, '渋谷', 10, '二分', 'スポーツ選手の話で盛り上がる中、それについていけなかった2人は銀河の話を始めた。');
INSERT INTO public.after VALUES ('rx4DRXXHUK', '2011-10-17', '月', '本郷練', 1, 'サイゼリヤ', 'ファミレス', false, false, 600, 600, 0, 0, 0, 8, 8, '本郷', 8, '代替わり', '代替わりコンパに来てください、と石河が山田を強く誘っていた。最近のホノマラの飲みの雰囲気を山田が気にする一方、細越が補足説明をしていた。');
INSERT INTO public.after VALUES ('l7agp8cHJr', '2011-10-19', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 20, 20, '渋谷', 17, 'お金のゆくえ', '月にいくら使うのか、などを話した。２万で収まっている人もいれば８万まで使う人もいた。レシートを集めて記録するべきだと皆言っていた。');
INSERT INTO public.after VALUES ('z7dgFcSUZ6', '2011-10-19', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 350, 350, 0, 0, 0, 10, 10, '渋谷', 11, 'おごり', 'とある２年生がとある２年生にシュークリームをおごっていた。出世払いと言っていたが、果たして彼が出世できるのだろうか・・・？');
INSERT INTO public.after VALUES ('pz6KIL6Kw3', '2011-10-22', '土', '土曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 5, 5, '渋谷', 5, '昔のホノマラ', '昔、ホノマラには女帝が存在していたらしい。最近のホノマラ内恋愛の話になり、腹黒い人の由来を聞いた。');
INSERT INTO public.after VALUES ('20FyUmm97g', '2011-10-22', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 200, 200, 0, 0, 0, 8, 8, '渋谷', 10, '川村', '一年生の役職人事が気になりつつも、川村の今後について戦略が練られた。希望的観測ばかりの川村に対し、先輩たちが厳しい突っ込みを入れていた。');
INSERT INTO public.after VALUES ('y619ZMt7uT', '2011-10-22', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 390, 390, 0, 0, 0, 4, 4, '渋谷', 6, 'サンキュー', '感謝フェアということで迷わずガストへ。４人しか入れないと言われたが、いざ入ると空席が多かった。');
INSERT INTO public.after VALUES ('uIuKXlSsFT', '2011-10-26', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 200, 200, 0, 0, 0, 4, 4, '渋谷', 11, 'ランキング', '過去のランキングを検索するとどうやら練習回数20位ぐらいまでがホノマラレギュラーとして扱われるらしい。川村が10位からの壁は厚いとぼやいていた。');
INSERT INTO public.after VALUES ('3qOOUdFO8d', '2011-10-26', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 700, 700, 0, 0, 0, 4, 4, '渋谷', 14, '想定外', 'チーズインハンバーグフェアにより勇んで注文するも、売り切れと言われ長谷川が残念がった。そしてチゲうどんも売り切れだった。何故だ。');
INSERT INTO public.after VALUES ('Y69Q3jJAhT', '2011-10-29', '土', '土曜練', 1, '南国亭', '中華', false, false, 2100, 2100, 0, 0, 0, 14, 14, '渋谷', 14, '学年会', '久々に三年会を開いた。食べ放題ということでみんな思い思い食べていた。');
INSERT INTO public.after VALUES ('XB4m5lQnET', '2011-10-29', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 10, 10, '渋谷', 12, 'ブーム', '最近タパタパではワインを頼むのがブームっぽい。ワイン分を門屋氏がおごっていた。');
INSERT INTO public.after VALUES ('m66KnAuh9g', '2011-10-29', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 19, '服', 'ＫがＨ氏の服を見て「この服持ってます」とドヤ顔で言っていた。オシャレナンバー１の彼に言われてＨ氏もさぞご機嫌かと思いきやそうでもなかった。
INSERT INTO public.after VALUES ('024WlW1pJS', '2011-10-29', '土', '土曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 4, 4, '渋谷', 4, 'バイト', 'バイトの種類は様々だが、今日もホノマラは平和だった。');
INSERT INTO public.after VALUES ('IIh2drhn3D', '2011-11-02', '水', '水曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 6, 6, '渋谷', 6, 'バイト', '世界陸上を見ながらできるバイトがあるらしい。');
INSERT INTO public.after VALUES ('RY1pbR6iuU', '2011-11-02', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 350, 350, 0, 0, 0, 10, 10, '渋谷', 13, '若返り', '冬の傾向として、だいぶアフターズのメンバーが若くなっている。駒場祭の前売り券を半ば強引に売りつけている人がいた。西は８枚も買っていたので１年生は見習おう。');
INSERT INTO public.after VALUES ('QZGfLjWEC2', '2011-11-05', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 700, 700, 0, 0, 0, 5, 5, '渋谷', 5, '就活', 'サークル前に企業の説明会に行ってきたという人がいた。');
INSERT INTO public.after VALUES ('vkE8Ltdn7k', '2011-11-08', '火', '本郷練', 1, '美味しい屋', '中華', false, false, 800, 800, 0, 0, 0, 6, 6, '本郷', 6, '洪水', '岩崎の研究テーマからタイの話になり、洪水の話になり、荒川が決壊するとどうなるか、という話になった。赤羽は際どいらしい。美味しい屋の定食のボリュームに皆満足しているようだった。');
INSERT INTO public.after VALUES ('ZLjCON3SEP', '2011-11-09', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 5, 5, '渋谷', 5, 'ロング', '遠山も髪の毛が肩の下まで伸びていた時期があったようだ。みなさん想像してみてください、僕には無理でした。');
INSERT INTO public.after VALUES ('CSumLmL1iw', '2011-11-09', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 350, 350, 0, 0, 0, 10, 10, '渋谷', 12, '圧倒的推測力', '来ていなかったが、今日は石田の誕生日ということで数人が「誕」「生」「日」「おめ」「でと」「う」と手分けしてメールを一斉送信していた。石田にとっては一人を除き誰一人として知らないアドレスからメールが届いたようだが、見事に送り主の名前を当てていた。すごい。');
INSERT INTO public.after VALUES ('603lDUiWgY', '2011-11-09', '水', '水曜練', 1, 'いんでぃら', 'インド料理', false, false, 750, 750, 0, 0, 0, 6, 6, '渋谷', 6, '人生', '河田さんと錦織が人生について語っていた。とりあえず「おいしいものを食べる時が一番」ということで決着していた。');
INSERT INTO public.after VALUES ('8FvnvgAHj3', '2011-11-06', '日', '皇居', 1, 'ターリー屋', 'インド料理', false, false, 750, 750, 0, 0, 0, 9, 9, '神田', 9, 'ナンが熱い', '焼き立てのナンをちぎるのに田儀さんが苦戦していた。そこから荒木さんの猫舌エピソードを聞いた。');
INSERT INTO public.after VALUES ('rP0SKdOaYR', '2011-11-12', '土', '土曜練', 1, 'GOLDRUSH', '洋食', false, false, 1300, 1300, 0, 0, 0, 4, 4, '渋谷', 4, 'Dはつらいよ…', '不安定な立場のPD。安心して研究を続けるためには収入のしっかりした相手と結婚すればよいらしい。来年博士課程に進む方が「ヒモになる」と宣言していた。');
INSERT INTO public.after VALUES ('GtcyLb94qu', '2011-11-09', '水', '水曜練', 1, '吉祥寺どんぶり', 'どんぶり', false, false, 650, 650, 0, 0, 0, 4, 4, '渋谷', 5, '破壊王って', '久しぶりの来訪だが相変わらずのボリュームに満足。デカ盛りは肉5倍、ごはん二倍らしいのでいつかは挑戦してみたい');
INSERT INTO public.after VALUES ('LCEKQt2rsD', '2011-11-16', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 10, 10, '渋谷', 10, '黒マッチョ', '新メニュー黒マッチョを食すべくスパマッチョへ。２名、その得体のしれないメニューに挑んでいたが、両名ともいまいちの評価を下していた。');
INSERT INTO public.after VALUES ('PDLD2ALNTB', '2011-11-16', '水', '水曜練', 2, 'アドアーズ', 'ゲーセン', false, false, 200, 200, 0, 0, 0, 5, 5, '渋谷', 5, 'クイズっておもしろいですね！', 'アフターでゲーセンの話が出たのでそのノリでクイズゲームをしにいった。対戦で迷わず女性キャラを選ぶホノマラーが続出した。ちなみに八甲おろしという応援歌は存在しません。');
INSERT INTO public.after VALUES ('PPyfJFXaly', '2011-11-16', '水', '水曜練', 3, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 4, '一概にわからない人っていっても', '自分の価値観と合わないから理解できない＝マイナスの意味、すごすぎて自分の理解を超えている＝プラスの意味というように、その人の使い方によって意味合いが異なるね〜と納得し合った。');
INSERT INTO public.after VALUES ('XtbAMT3YL2', '2011-11-19', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 600, 600, 0, 0, 0, 11, 11, '渋谷', 11, '代替わり', '一週間後ですね。楽しみですね。明日大会の方は頑張ってください。');
INSERT INTO public.after VALUES ('xaLtu1T3H1', '2011-11-19', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 400, 400, 0, 0, 0, 11, 11, '渋谷', 11, 'クリパ準備', '1年生女子と2年生女子で話し合い。1人1店電話をかけてお店を絞っていた。みなさん12月18日のご予定は決まっていますか？ぜひホノマラクリパに参加してください！');
INSERT INTO public.after VALUES ('RFdW4dxSA9', '2011-11-20', '日', 'I love 古河マラソン', 1, 'cafe 雪華', 'うどん', false, false, 700, 700, 0, 0, 0, 6, 6, '古河駅前', 5, 'マウス', '石河はよく鼠を殺しているらしい。');
