INSERT INTO public.after VALUES ('hQbiK9JmDi', '2013-04-13', '土', 'RUNNET EKIDEN', 1, 'ファミールコート', 'ファミレス', false, false, 680, 680, 0, 0, 0, 18, 18, '赤羽', 18, '文ニート', '週4コマとかうらやましすぎる。
