INSERT INTO public.after VALUES ('vSJYFt3aKp', '2012-05-19', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 24, 24, '渋谷', 24, '異様な盛り上がり', 'サンマルクカフェの一角30席近くを占領し、まるでコンパかのように席移動を繰り返し、異様に盛り上がっている集団がいた。
INSERT INTO public.after VALUES ('KRZMkQAkNR', '2012-05-19', '土', '土曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 18, 18, '渋谷', 17, 'パソコン', '大学からオススメされたPCを18万もはたいて買ってしまったと悔しがる人がいる一方、未だにマイPCを持っていないので、貯金して早く買いたいと意気込んでる人もいた。');
INSERT INTO public.after VALUES ('ZHIMPhTdNz', '2012-05-19', '土', '土曜練', 1, '唐めん', 'ラーメン', false, false, 800, 800, 0, 0, 0, 4, 4, '渋谷', 4, '男子校', '男子校出身者が多かったこともあり、男子校特有の楽しさなどについて語った。やっぱり女の子が居る方が遥かに華やかで楽しく、暑苦しさも相当軽減されるが、俗にモラルと呼ばれるものが一切忘れ去られ、奔放の限りを尽くすことができる男子校のあの雰囲気も時々懐かしくなる。');
INSERT INTO public.after VALUES ('5fXfT5XLC8', '2012-05-19', '土', '土曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 8, 8, '渋谷', 8, '個性豊か', '野宿サークルに入ったという人や、個性豊かな家族の一員である人がおり、たくさん面白い話を聞くことができた。
INSERT INTO public.after VALUES ('6e2ynPoKAr', '2012-05-23', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 700, 700, 0, 0, 0, 22, 22, '渋谷', 25, 'ガストマスター', '自称ガストマスターが、メニューのしまう場所を知らない人達に教えながら、「これが中毒性の違い」と言い放っていた…が、新料理の量は見誤ったため一品では満足できず、周りの人の残りまで手を出す自称ガストマスターであった。');
INSERT INTO public.after VALUES ('2XIeOLRAz2', '2012-05-23', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 12, 12, '渋谷', 15, '家族', '4人兄弟、3人兄弟、一人っ子…家族構成の違いによって、それぞれの悩みや利点というものが存在する。
INSERT INTO public.after VALUES ('w2LvDaIkEf', '2012-05-12', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 300, 300, 0, 0, 0, 300, 300, '渋谷', 32, '夢', '人の夢は自己願望の現れらしい…ということで、いろんな人の夢の話を聞いて盛り上がった。
INSERT INTO public.after VALUES ('1Pjam1DxrQ', '2012-05-26', '土', '土曜練', 1, 'かにチャーハンの店', '中華', false, false, 690, 690, 0, 0, 0, 4, 4, '渋谷', 6, '特別メニュー', '特別メニューの通常サイズと1.5倍大盛サイズを頼んでいる人がいたが、大盛がかなり大きくやや苦しんでいるた。
