INSERT INTO public.after VALUES ('uPwBBVuY5u', '2012-05-25', '金', '山手線一周', 1, '松屋', '牛丼', false, false, 390, 390, 0, 0, 0, 11, 11, '巣鴨', 11, 'お疲れ', '山手線一周を終え、腹を空かせたホノマラーが松屋に入った。
