INSERT INTO public.after VALUES ('KqHemsSuh2', '2012-06-20', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 700, 700, 0, 0, 0, 10, 10, '渋谷', 9, 'ハンバーグ', 'ハンバーグに添えられた野菜がいつも以上に残念な姿であった。いつも予約もなしに大人数をこの店にぶち込んでいる報いであろう。
INSERT INTO public.after VALUES ('R0qs5o3dXa', '2012-06-23', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 630, 630, 0, 0, 0, 20, 20, '渋谷', 19, '語学', '英米文学専攻の人と文学部倫理学科の人がいたので、語学の話で盛り上がった。
INSERT INTO public.after VALUES ('k2umLGpVt3', '2012-06-23', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 18, 18, '渋谷', 27, 'ピンク', '彼が再びやってくれるようである。
INSERT INTO public.after VALUES ('ivR2Z8zXfT', '2012-06-23', '土', '土曜練', 1, '絶品純豆腐', '韓国料理', false, false, 1000, 1000, 0, 0, 0, 6, 6, '渋谷', 6, '趣味', '近況報告や世界規模の話しまで。一眼レフを買うか迷う人がいた。思い出作りしようとか。走る他の趣味に写真という人がちらほら。');
INSERT INTO public.after VALUES ('vyQGLSjtIJ', '2012-06-24', '日', '富里スイカロードレース', 1, '銀ゆば', '和食（食べ放題）', false, false, 1150, 1150, 0, 0, 0, 4, 4, '成田', 4, 'ポケモン', 'ポケモンのゲームにおける地図は、シリーズごとに日本各地を現しているらしい。BWシリーズになってどこかよくわからない地図になってしまったと嘆く人がいた。
INSERT INTO public.after VALUES ('agkU7ej2g6', '2012-06-24', '日', '皇居練', 1, 'サイゼリヤ', 'ファミレス', false, false, 400, 400, 0, 0, 0, 8, 8, '淡路町', 10, 'ピラミッド', '卓上三角柱POPに描いてあったピラミッドの絵の底辺の方にトマトがあり頂点に肉があったが、よく見れば食べるべき頻度が多いもの程下に描かれてあったので、底辺の方がオススメされていた。
INSERT INTO public.after VALUES ('fIXDsDgxWe', '2012-06-23', '土', '土曜練', 1, 'はなまるうどん', 'うどん', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 10, '方言', '地元で標準語で話すと「東京かぶれが」と白い目で見られることがあるとかないとか。
INSERT INTO public.after VALUES ('wBPRwa0SC9', '2012-06-20', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 2, 2, '渋谷', 5, 'テラマッチョ', '2回目のテラマッチョ。今回は20分で完食すると無料とのこと。店に入る前から食べる事になっていた。実物の登場時にはやはり盛り上がった。
INSERT INTO public.after VALUES ('lX72S26Mzs', '2012-06-27', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 650, 650, 0, 0, 0, 11, 11, '渋谷', 15, 'きのこ雑炊', 'きのこ雑炊を注文した錦織がしきりに「量が多くなった」と連発していた。実際に食べてみると、以前よりおいしくなっているものの、やっぱり量が多くなったそうである。
INSERT INTO public.after VALUES ('5Cxlfqld7m', '2013-11-27', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 250, 250, 0, 0, 0, 14, 14, '渋谷', 18, 'クリスマス', 'クリスマスが今年もやってくる(一部を除く)');
INSERT INTO public.after VALUES ('KBSGcrw1ZJ', '2012-06-27', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 6, 6, '渋谷', 19, '他己紹介', '夏合宿のしおりに使う他己紹介を集め始めた。
INSERT INTO public.after VALUES ('VJq9rEOfv4', '2012-06-27', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 4, 4, '渋谷', 7, '田端リベンジフラグ', '川村氏と大城氏がテラマッチョのタイムアタックに挑んだ。大城氏はテラマッチョ初トライにも関わらず、バランスの良いペース配分が功を奏して完食、サークル記録を打ち立てた。川村氏はさすがのベテランである。彼のフォークさばきを見ていると、彼はパスタというよりは何かの塊と呼んだ方が正確であるといえるテラマッチョの切り崩し方を徹頭徹尾把握しているようにすら思えた。
(結果)
INSERT INTO public.after VALUES ('eBcDX5f9JG', '2012-06-30', '土', '土曜練', 1, '一休 ', '飲み', false, false, 3000, 3000, 0, 0, 0, 40, 40, '渋谷', 43, 'コンパ', '特に名目もないコンパということで、事前に連絡のあった参加者は少なめだったようだが、当日のドタ参が10人以上いたこともあって、結局大人数でのコンパとなった。
INSERT INTO public.after VALUES ('WWMSHLoqnH', '2012-06-30', '土', '土曜練', 3, 'ひの屋', '牛丼', false, false, 280, 280, 0, 0, 0, 8, 8, '渋谷', 8, 'カラオケ', 'とある社会人が今月に誕生日企画として、カラオケ100曲採点90点以上を目指すようだ。
INSERT INTO public.after VALUES ('wdvBTvOsfe', '2012-06-30', '土', '土曜練', 2, '金の蔵', '飲み', false, false, 1500, 1500, 0, 0, 0, 40, 40, '渋谷', 23, 'オール', '1次会が3時間と長かった影響でに2次会の開始が遅かったためか、オールをした人が半数以上であった。
INSERT INTO public.after VALUES ('xX63sHakzF', '2012-07-04', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 6, 6, '渋谷', 6, 'メガマッチョとミスマッチョ', '秀嶋さんは初スパマッチョということで無難にミスマッチョを頼んでいた一方で、2回目の塩原さんは自信たっぷりに、メガマッチョに挑戦していた。
INSERT INTO public.after VALUES ('A6w87uJScQ', '2012-07-04', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 12, 12, '渋谷', 16, '合宿', 'あと3日後となった箱根合宿の相談をしたり、残り1ヶ月を切った夏合宿に向け他己紹介を書きあったりした。
INSERT INTO public.after VALUES ('CNN0ziUDX7', '2012-07-04', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 700, 700, 0, 0, 0, 4, 4, '渋谷', 7, 'スマホ', 'スマホデビューしたての人にアプリのダウンロードの仕方を教えてあげていた。
INSERT INTO public.after VALUES ('T21G3D26tR', '2012-07-07', '土', '土曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 4, 4, '渋谷', 12, 'ホノルルマラソン', '12月9日に行われるホノルルマラソンの話をした。色々なツアーがあるが今年はどうなることやら。');
INSERT INTO public.after VALUES ('8bJjVGEALA', '2012-07-07', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 400, 400, 0, 0, 0, 12, 12, '渋谷', 12, '将来のこととか', '各々の将来について話した。身近な進路から夢のような話まで色々と盛り上がった。数学科が増えそう。最近、閉店時間まで残る人が多い気が…。');
INSERT INTO public.after VALUES ('BqsVJx2i3f', '2012-07-11', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 630, 630, 0, 0, 0, 14, 14, '渋谷', 14, '冷麺', '夏も盛りということで、冷麺を注文している人が沢山いた。
INSERT INTO public.after VALUES ('NZgRjvzORO', '2012-07-11', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 14, 14, '渋谷', 15, '結婚は茶番', '各々の給食ネタについて同意を求めあったり首をかしげたり、また、婚約指輪や学生結婚についてしょうもないギャグを交えながら議論したりなど、大いに盛り上がった。
INSERT INTO public.after VALUES ('5tAHjt8lVQ', '2012-06-16', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 800, 800, 0, 0, 0, 4, 4, '渋谷', 11, 'ファミレス', 'そもそもファミリーレストランの定義は何なのかという話をした。ある人はドリンクバーがあることだと主張し、お子様ランチがあることであると主張する人もいた。サイゼリヤはファミレスではないと主張している人もいた。');
INSERT INTO public.after VALUES ('sdmw3YzG8y', '2012-06-16', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 4, 4, '渋谷', 4, 'リクエスト', 'とある人の誕生日企画をやすやすと成功させてはならないと曲を検索している人がいた。
INSERT INTO public.after VALUES ('M4gsQStXu4', '2012-07-14', '土', '土曜練', 1, '絶品純豆腐', '韓国料理', false, false, 900, 900, 0, 0, 0, 4, 4, '渋谷', 4, '回数', '練習回数やアフター回数、その比などについて話した。ここを日常的にチェックする人から存在を知らない人まで様々であった。');
INSERT INTO public.after VALUES ('uecT9M9LjP', '2012-07-14', '土', '土曜練', 1, 'サムラート', 'カレー', false, false, 850, 850, 0, 0, 0, 6, 6, '渋谷', 7, 'カレー', 'カレー屋に入りながらも、辛いものは苦手という人が沢山いた。
INSERT INTO public.after VALUES ('EPfjnAsylh', '2012-07-14', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 16, 16, '渋谷', 18, '夏合宿', '夏合宿が近いのに詳細が決まってないため焦り気味の合宿係が色々と相談をした。
INSERT INTO public.after VALUES ('rK32QR0tK8', '2012-07-14', '土', '土曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 4, 4, '渋谷', 9, '一年生', 'フレッシュな顔ぶれがである一年生。この時期ゆえに進振り相談。優しく答える先輩の姿があった。
INSERT INTO public.after VALUES ('5fF7Pu9zIm', '2012-07-14', '土', '土曜練', 1, '鍋ぞう', '和食', false, false, 2000, 2000, 0, 0, 0, 8, 8, '渋谷', 18, '4年生な会', '何となく久々に感じられる学年会が開かれた。これまでのことやこれからのことを話して盛り上がった。
INSERT INTO public.after VALUES ('WSNMdMpkUk', '2012-07-14', '土', '土曜練', 2, 'カチカチ山', '飲み', false, false, 1000, 1000, 0, 0, 0, 12, 12, '渋谷', 13, '復習', '社会人は一味違います。');
INSERT INTO public.after VALUES ('QWlPKab86L', '2012-07-18', '水', '水曜練', 1, 'はなまるうどん', 'うどん', true, false, 600, 600, 0, 0, 0, 4, 4, '渋谷', 4, '高校野球', '最近暑くて仕方がない。野球の試合中に倒れた高校生が出たようだ。
INSERT INTO public.after VALUES ('Bufl7dyDnH', '2012-07-18', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 650, 650, 0, 0, 0, 8, 8, '渋谷', 9, '夏合宿', '2年生のみで集まって夏合宿の詳細について相談した。
