INSERT INTO public.after VALUES ('WtE8EZELb5', '2012-07-25', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 16, 16, '渋谷', 17, 'お絵かき', '小谷さんの可愛らしい4コマ漫画を火種にして、裏紙やらにお絵かきをする人が大量発生した。
INSERT INTO public.after VALUES ('pJF4kKVWjF', '2012-07-25', '水', '水曜練', 1, 'スパマッチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 6, 6, '渋谷', 6, 'お金の使い方', 'とある2年生はお金が無くて苦しんでいる。どうやら漫画の買いすぎが原因とのこと。他の2年生にもお金の使い方を尋ねていた。');
