INSERT INTO public.after VALUES ('sXKBJnL6Sr', '2012-08-08', '水', '水曜練', 1, 'かにチャーハンの店', '中華', false, false, 690, 690, 0, 0, 0, 8, 8, '渋谷', 8, 'オリンピック', '合宿中もしょっちゅう話題に登ったオリンピック。
