INSERT INTO public.after VALUES ('uLYEVr0Qb6', '2012-10-31', '水', '水曜練', 1, 'カレーショップ C&C', 'カレー', false, false, 600, 600, 0, 0, 0, 4, 4, '渋谷', 4, '青山祭', '青山の学祭への正しい行き方
INSERT INTO public.after VALUES ('86JMW9537p', '2012-11-03', '土', '土曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 5, 5, '渋谷', 8, '乱舞', '代々木公園でイベントがあったせいか、家族連れが多かった。狭い店内を幼児が所狭しと動き回る様が壮観であった。');
INSERT INTO public.after VALUES ('WFcDHAOKvB', '2012-11-24', '土', '土曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 6, 6, '渋谷', 8, 'カーボローディング', '翌日のつくばマラソンのためのカーボローディングをしようする何名かを中心にはなまるへ来店した。
INSERT INTO public.after VALUES ('4XiJ61tK1l', '2012-11-03', '土', '土曜練', 1, '伝説のスタ丼', 'どんぶり', false, false, 600, 600, 0, 0, 0, 3, 3, '渋谷', 9, '学園祭シーズン', '多くの大学で学園祭が開催されている。しかし駒場祭にホノマラの出店はない。他大学へ行く計画をたてて盛り上がった。こんなちも手持ち無沙汰なホノマラーが多いなんて。
INSERT INTO public.after VALUES ('o5prNFg4rj', '2012-11-03', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 26, 26, '渋谷', 17, '写真', '写真を撮られるのを極度に嫌がる某2年が「まだまだアンチ写真活動が足りないな」などと言っていた。これからは積極的に彼の写真を撮っていこう。');
INSERT INTO public.after VALUES ('eG8avNkOZN', '2012-11-03', '土', '土曜練', 2, 'ドトール', 'カフェ', false, false, 200, 200, 0, 0, 0, 16, 16, '渋谷', 21, 'クリスマスパーティ', 'クリパへ向けた会議が行われていた。1年生単独の初イベントだけあって気合が入っているようだった。
INSERT INTO public.after VALUES ('zU7IhtsAw9', '2012-11-07', '水', '水曜練', 1, 'ねぎし', '焼肉', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 3, '日比野さんの憂鬱', '授業延長しすぎワロタw');
