INSERT INTO public.after VALUES ('3PSePh8eCD', '2012-11-07', '水', '水曜練', 1, 'かにチャーハンの店', '中華', false, false, 680, 680, 0, 0, 0, 4, 4, '渋谷', 5, 'お茶大', 'お茶大には面白い(?)先生が沢山いるようだ。
INSERT INTO public.after VALUES ('WrzFxRldV3', '2012-11-07', '水', '水曜練', 1, 'GOLDRUSH', '洋食', false, false, 1200, 1200, 0, 0, 0, 2, 2, '渋谷', 2, '男子校出身', 'GOLDRUSHにもサシで入ってしまうのがディレイド練メンバー。並んで待つアフターの待ち時間もハンバーグを食べながらも男子校トークで盛り上がった。留学している先輩と会いたいという結論で一致した。
INSERT INTO public.after VALUES ('6G5C6Z2XH2', '2012-11-07', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 28, 28, '渋谷', 31, 'フリーペーパー', 'ホノマラとは別のサークル活動で作成したフリーペーパーを持ってきて配っている方々がいた。
INSERT INTO public.after VALUES ('GmmC1mWVrF', '2012-11-03', '土', '本郷練', 1, 'かにチャーハンの店', '中華', false, false, 680, 680, 0, 0, 0, 4, 4, '渋谷', 4, '某大学', '校舎の綺麗さについて話した。
INSERT INTO public.after VALUES ('4qAys3FTsI', '2012-11-07', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 700, 700, 0, 0, 0, 6, 6, '渋谷', 5, 'ガストはできる子', '料理がしっかり先輩から運ばれてきた。
INSERT INTO public.after VALUES ('LrcvgTpUBB', '2012-11-10', '土', '土曜練', 1, 'かにチャーハンの店', '中華', false, false, 800, 800, 0, 0, 0, 4, 4, '渋谷', 4, '田儀語', '割と人気なカニチャーハン。そこで田儀語の解説がなされた。肉離れ、牡蠣による集団食中毒、ぎっくり腰。それに付随して食事と運動によるカロリー消費と食事制限による効果の話もした。忙しい人は走る時間が取れないという結論に至った。
INSERT INTO public.after VALUES ('iS08eQ6x0f', '2012-11-10', '土', '土曜練', 1, 'GOLDRUSH', '洋食', false, false, 1300, 1300, 0, 0, 0, 4, 4, '渋谷', 3, 'ホノマラ', '某氏念願のゴールドラッシュへ来店。
INSERT INTO public.after VALUES ('gqn6KJfvIg', '2012-11-10', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 28, 28, '渋谷', 37, 'くじ引き', 'アフターズでは、アフターの店が同じ人だった同士で席が固まってしまうことが殆どである。
INSERT INTO public.after VALUES ('7p8yLcInU0', '2012-11-07', '水', '水曜練', 1, 'パンチョ', 'パスタ', false, false, 650, 650, 0, 0, 0, 4, 4, '渋谷', 4, 'パスタ', '80年代風のレトロな内装のパスタ屋さん。デートに使えそう。');
INSERT INTO public.after VALUES ('kYPVeqzYxm', '2012-11-10', '土', '土曜練', 1, 'ポムの樹', '洋食', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 3, '一人暮らし', 'いろいろな創作オムライスを楽しみながら、一人暮らしの妄想に耽った。
INSERT INTO public.after VALUES ('0mFolDFUC3', '2012-11-10', '土', '土曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 4, 4, '渋谷', 2, '二人席', '何人ではなまるに行こうとも、この二人は二人席になることが多い。そのためか、サシでも新鮮味がなかった。
INSERT INTO public.after VALUES ('GeNIFD5u98', '2012-10-06', '土', '土曜練', 1, 'はなまるうどん', 'うどん', false, false, 300, 300, 0, 0, 0, 5, 5, '渋谷', 17, '新地開拓？', '一女がはなまるの奥におしゃれなお店を見つけたが、値段がアフター向けでなかったので断念した。
INSERT INTO public.after VALUES ('w08IQp37Ci', '2012-11-14', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 18, 18, '渋谷', 23, '秘境駅', '田舎トークから、お互いの住んでいる市の田舎度合を競う流れになった。
