INSERT INTO public.after VALUES ('xIYkxKgC8A', '2012-11-14', '水', '水曜練', 1, 'すき家', '牛丼', false, false, 500, 500, 0, 0, 0, 1, 1, '渋谷', 1, '交差する想い-最終決戦-', 'すき家「ファミレスって孤独よね、ふふ。」
INSERT INTO public.after VALUES ('13HZF0u0qx', '2012-11-17', '土', '土曜練', 1, 'はなまるうどん', 'うどん', false, false, 400, 400, 0, 0, 0, 4, 4, '渋谷', 4, '合流', '遅れてガストへと合流しに行こうと思ったところ、もう食べ終わると連絡を受けたので単身はなまるへ。
INSERT INTO public.after VALUES ('W2cjdwWVU4', '2012-11-17', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 24, 24, '渋谷', 30, '奥の席', 'サンマルクの奥の10人席は、他のテーブルから隔離されているように感じるため、本来は大声で話すのを憚られるような話題を、大いに盛り上がって話してしまいがちになる。');
INSERT INTO public.after VALUES ('ZoGvmkXQwD', '2012-11-17', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 800, 800, 0, 0, 0, 8, 8, '渋谷', 20, '交差する想い、再び', '「赤ワイン、1つで」
INSERT INTO public.after VALUES ('CT5M7scO5p', '2012-11-21', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 630, 630, 0, 0, 0, 20, 20, '渋谷', 17, 'クレーマー', 'シーザーサラダの出て来るタイミングが早過ぎ、山盛りポテトフライが山状に盛られていない、半額クーポン取得のサイトに繋がらない、キッズプレートの見た目が美味しそうじゃない…等々のクレーム(?)を店員に言いつけてやると荒ぶっている青年がいた。
INSERT INTO public.after VALUES ('EC3lGYhGBb', '2012-11-21', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 26, 26, '渋谷', 28, 'ゲーム', '細井さんが映画で見たという、紙とペンを使ってできる簡単なゲームをやってみた。
INSERT INTO public.after VALUES ('l0h1sJ783L', '2012-11-21', '水', '水曜練', 1, 'タパスタパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 5, 5, '渋谷', 5, '３年生', '各々の進路について語った。');
INSERT INTO public.after VALUES ('PMxs9Fg6TJ', '2012-11-21', '水', '水曜練', 1, 'MILKcafe', 'カフェ', false, true, 2000, 2000, 0, 0, 0, 4, 4, '渋谷', 3, 'ユニークな人', 'とあるホノマラーがチヤホヤされた。また、その人のファンと某氏が似てる事を再確認した。
INSERT INTO public.after VALUES ('LRCHq2rxTu', '2012-11-24', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 28, 28, '渋谷', 30, 'つくばマラソン', '翌日につくばマラソンを控えている人も数多く参加していた。結果次第で罰ゲームをさせようとしている黒い人がいた。');
INSERT INTO public.after VALUES ('Lg8HIiFBYn', '2012-11-24', '土', '土曜練', 1, 'かにチャーハンの店', '中華', false, false, 680, 680, 0, 0, 0, 4, 4, '渋谷', 4, 'ランキング', 'よく練習に来ている人がランキングの順位を気にしていた。
INSERT INTO public.after VALUES ('AJjzNrDpQ5', '2012-11-28', '水', '水曜練', 1, 'すき家', '牛丼', false, false, 550, 550, 0, 0, 0, 2, 2, '渋谷', 3, '無職の美学', '無職にしか見えないこともある');
INSERT INTO public.after VALUES ('gteCuZcvQz', '2012-11-28', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 600, 600, 0, 0, 0, 18, 18, '渋谷', 18, '陸上', '400mや800mなどの中長距離種目でのあるあるを、かつて陸上部だった3人から聞いた。
INSERT INTO public.after VALUES ('eqAGXiTFsS', '2012-11-28', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 20, 20, '渋谷', 26, '留学生', 'とある人が、実はタイからの留学生であるということが発覚した。
INSERT INTO public.after VALUES ('KgayBfGs94', '2012-12-01', '土', '土曜練', 2, '東方見聞録', '飲み', false, false, 2000, 2000, 0, 0, 0, 50, 50, '渋谷', 53, 'オール', '気がついたら、結構な人数がオールをしていた。
INSERT INTO public.after VALUES ('z7EvSourwM', '2012-12-01', '土', '土曜練', 3, '歌広場', 'カラオケ', false, false, 2000, 2000, 0, 0, 0, 3, 3, '渋谷', 4, 'オールカラオケ楽しい', 'オールで飲むくらいならカラオケしたい！って人が集まった。嵐やYUIを中心に歌った。また、PVに出てくる人物や某アイドルＺの容姿に議論が生じた。
INSERT INTO public.after VALUES ('OdnE2dcsp8', '2012-12-01', '土', '土曜練', 4, '歌広場', 'カラオケ', false, false, 2000, 2000, 0, 0, 0, 10, 10, '渋谷', 10, '同期の桜', '貴様と俺とは同期の桜
INSERT INTO public.after VALUES ('XvvWCzERUU', '2012-12-01', '土', '土曜練', 3, 'サイゼリヤ', 'ファミレス', false, false, 400, 400, 0, 0, 0, 20, 20, '渋谷', 22, '朝食', 'オール後は腹が減るということで、サイゼリヤへと朝食を食べに行った。
INSERT INTO public.after VALUES ('WsFfsXydWj', '2012-12-01', '土', '土曜練', 5, 'マクドナルド', 'ファーストフード', false, false, 300, 300, 0, 0, 0, 6, 6, '渋谷', 5, 'jubeat', 'ゲーセンでjubeatをやろうというノリになったのだが、10時にならないとゲーセンが開かないということで、それまでマックで時間を潰した。
INSERT INTO public.after VALUES ('iDb8JVxGJ1', '2012-12-05', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 650, 650, 0, 0, 0, 20, 20, '渋谷', 16, 'フルマラソンの先', 'フルマラソンの先にあるウルトラマラソンの話がホノ完の人を驚かせていた。
INSERT INTO public.after VALUES ('kwVRdm0ZbS', '2012-12-05', '水', '水曜練', 1, 'はなまるうどん', 'うどん', false, false, 400, 400, 0, 0, 0, 4, 4, '渋谷', 2, '大学生', '大学生が出逢う様々な問題について話し合った。最終的にはクリスマスは不要という意見に集約されたようであった。
INSERT INTO public.after VALUES ('aDQFbQHgWU', '2012-12-05', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 25, 25, '渋谷', 24, 'キャプテンの意味', '"今のホノマラにキャプテンというモノは必要なのだろうか？"
INSERT INTO public.after VALUES ('Q701Ao3NIm', '2012-12-05', '水', '水曜練', 1, 'やよい軒', '和食', false, false, 600, 600, 0, 0, 0, 2, 2, '渋谷', 2, '一人暮らし', '某氏が一人暮らしを始めた。そして定期が変わったので書き換えに渋谷駅へ。そのついで？に東口にあるやよい軒へ向かった。近況報告や代替りの感想など濃い話ができた。
INSERT INTO public.after VALUES ('gRrNwFTboC', '2012-12-08', '土', '土曜練', 1, '吉野家', '牛丼', false, false, 400, 400, 0, 0, 0, 3, 3, '渋谷', 3, 'クリパ', 'お店からの電話があり、人数を確認されていたようだ。現在50人ですと答えたがもう少し増えないものか……');
INSERT INTO public.after VALUES ('uZKAzVlnjL', '2012-12-08', '土', '土曜練', 1, '唐めん', 'ラーメン', false, false, 800, 800, 0, 0, 0, 2, 2, '渋谷', 2, '好きな色', '皆さん、何色が好きですか？現在青とオレンジがトップです。
INSERT INTO public.after VALUES ('3WsWYXyzta', '2012-12-08', '土', '土曜練', 1, '蒙古タンメン　中本', 'ラーメン', false, false, 800, 800, 0, 0, 0, 4, 4, '渋谷', 6, 'ティッシュ', 'メニューを間違えて取ってしまった某三男が苦しそうな表情を浮かべる中、隣で冷やし味噌辛さ三倍を楽しく食べる人がいた。
INSERT INTO public.after VALUES ('aWzZVt7mNR', '2012-12-08', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 25, 25, '渋谷', 31, '部ジャー', 'ある高校では部活のジャージの事を「部ジャー」と略すらしい。
INSERT INTO public.after VALUES ('du4llBiydc', '2012-12-08', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 650, 650, 0, 0, 0, 20, 20, '渋谷', 17, '熱き戦い', '某５男VS某１男VS某１女の熱き戦いは駅伝企画に留まらず、フルマラソンでも開催される模様。
INSERT INTO public.after VALUES ('J6oRdpcgvZ', '2012-12-08', '土', '土曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 8, 8, '渋谷', 10, '幸せ', '温かくておいしいうどんとカレーを食べて幸せな気分に浸る。
INSERT INTO public.after VALUES ('ea9Htlhit2', '2012-12-12', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 650, 650, 0, 0, 0, 16, 16, '渋谷', 15, '学生証', '慶応ボーイの中にはは女の子に声をかけるときに学生証を見せる人もいるらしい。');
INSERT INTO public.after VALUES ('TdMCbqV4Jb', '2012-12-12', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 20, 20, '渋谷', 20, 'チャラい話', 'ある早大生とある慶応ボーイがチャラい話をしていた。ホノルルに行ったチャラい東大生がいなくて残念である。');
INSERT INTO public.after VALUES ('e63cj85b42', '2012-12-15', '土', '土曜練', 1, 'はなまるうどん', 'うどん', false, false, 400, 400, 0, 0, 0, 9, 9, '渋谷', 9, '足りないドライバー', '２月にあるおきなわマラソン。参加人数に対して明らかにドライバーが足りない。
INSERT INTO public.after VALUES ('50lzJqWz7A', '2012-12-15', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 650, 650, 0, 0, 0, 4, 4, '渋谷', 4, 'クリパ', '明日のクリパのドレスについて話した。いつもと違う格好のみんなを見るのが待ち遠しくてしょうがない。');
INSERT INTO public.after VALUES ('SceM6w94DF', '2012-12-15', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 250, 250, 0, 0, 0, 15, 15, '渋谷', 19, '駅伝', '昨日発表された駅伝のメンバーを見つつ、高度な情報戦(？)が展開された。
INSERT INTO public.after VALUES ('mT13VJKicr', '2012-12-15', '土', '土曜練', 1, 'タパス・タパス', 'パスタ', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 3, 'メニューが…', '久々にタパタパへ行ったらメニューが減っていた。海Pが無いなんてショックである。
INSERT INTO public.after VALUES ('c7Jq0b8CgY', '2012-12-09', '日', 'ホノマラ', 1, 'マクドナルド', 'ファーストフード', false, false, 800, 800, 0, 0, 0, 4, 4, 'ハワイ', 4, 'ホノルルのマック', '空腹の男４人でホテル横マックへ。
INSERT INTO public.after VALUES ('TR96pENtq9', '2012-12-19', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 650, 650, 0, 0, 0, 10, 10, '渋谷', 10, '夏合宿', '来年の夏合宿を各々想像していた。50キロを希望する人、10キロが合ってそうな人様々である。来年の最長距離グループは果たして何キロになるのであろうか…。');
INSERT INTO public.after VALUES ('MejJtB1J8w', '2012-12-19', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 250, 250, 0, 0, 0, 15, 15, '渋谷', 12, 'いろいろ', '駅伝、沖縄マラソン、チャレンジ富士五湖、地方のあるあるなど話題は多岐に及んでいた。スピードランナーの肺と足を手に入れたいと嘆く人がいた。');
INSERT INTO public.after VALUES ('oXvJ02W4G9', '2012-12-19', '水', '水曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 1, 1, '渋谷', 2, '絶望した', '織田フィールドで出会った2人はここで再開。駅伝のことや近況報告した。そして昨日や今日の飲み会についての話をしたら、そこに絶望する人達が現れた。');
INSERT INTO public.after VALUES ('yYp3EPgsoB', '2012-12-22', '土', '土曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 13, 13, '渋谷', 12, '死ぬ2日前の話', 'どうしてるんだろうね。
INSERT INTO public.after VALUES ('ITRZ6AG8jI', '2012-12-22', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 650, 650, 0, 0, 0, 13, 13, '渋谷', 13, '反省会', '反省会というか、駅伝の結果について話し合った。
INSERT INTO public.after VALUES ('fYq3GHQhbW', '2012-12-22', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 250, 250, 0, 0, 0, 25, 25, '渋谷', 24, 'アトラクション', 'ある一女がお化け屋敷を恐れているのは、かつてお化け屋敷で転んだ時にこんにゃくを顔にぶつけた経験によるものらしい。恐れるべきは、こんにゃくではなかろうか。');
INSERT INTO public.after VALUES ('7QVknztSND', '2012-12-23', '日', '皇居練', 1, 'サイゼリヤ', 'ファミレス', false, false, 400, 400, 0, 0, 0, 14, 14, '神田', 14, '救いの手', '会計を間違えられたようだ。
INSERT INTO public.after VALUES ('qonDCOXKxs', '2012-12-23', '日', '皇居練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 6, 6, '神田', 6, 'イブ', '小谷さん卒業企画や箱根企画など、様々な企画の話で盛り上がった。24日は各地で会合があるらしく、その楽しみ方について試行錯誤し、想像を膨らませていた。');
INSERT INTO public.after VALUES ('Nazjvf6CXW', '2012-12-26', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 650, 650, 0, 0, 0, 11, 11, '渋谷', 11, '話題', '何を話していたんだろうか。他愛もない話が多すぎてここに書くに値する話題が見つからない。でもアフターなんてそんなもんだと思う。');
INSERT INTO public.after VALUES ('vCGfE7w7xx', '2012-12-26', '水', '水曜練', 1, 'はなまるうどん', 'うどん', false, false, 400, 400, 0, 0, 0, 3, 3, '渋谷', 3, 'アフター録', '色んな場所に分かれてのアフター。少しでも周りより楽しそうだと思えるようなアフターをしたい(アフター録を書きたい)。そんな思いから生まれたフィクション。
INSERT INTO public.after VALUES ('qCtGlq9A6L', '2012-12-26', '水', '水曜練', 1, '唐めん', 'ラーメン', false, false, 800, 800, 0, 0, 0, 6, 6, '渋谷', 6, '推しメン', 'さまざまなアニメの中で、それぞれどのキャラが好きかを話し合った。ジブリ好きの一女はなかなか冷静なようである。
INSERT INTO public.after VALUES ('bW7gKQUGHb', '2012-12-26', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 250, 250, 0, 0, 0, 25, 25, '渋谷', 27, '女子会', '手相占いや心理テストなどで盛り上がる非常に女子女子したテーブルがあった。
INSERT INTO public.after VALUES ('lf4Zu3Xril', '2012-12-29', '土', '皇居練', 1, 'サイゼリヤ', 'ファミレス', false, false, 300, 1000, 0, 0, 0, 10, 10, '神田', 10, '大晦日', 'ホノマラーの大晦日の過ごし方は家でグダグダしたり、バイトしたり、走ったり、みんなで集まったりと様々にようである。');
INSERT INTO public.after VALUES ('XyaKaCB0Jt', '2012-12-29', '土', '皇居練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 7, 7, '神田', 7, 'ブログ', 'ホノマラーにはブログをやってる人が多々いる。面白いブログや走りへの意欲がわくブログもあるので、見たことない人は是非目を通していただきたい。');
INSERT INTO public.after VALUES ('g6Wz7FlpoQ', '2013-01-05', '土', '土曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 3, 3, '渋谷', 3, '初詣', '初詣に行った話をした。ある人は昨年大凶を３回連続で引いたそうだ。今年は末吉だとか。そして今日はみんながいるガストに行きそびれていた。運のない人だ。');
INSERT INTO public.after VALUES ('h9y9KfcXy7', '2013-01-05', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 11, 11, '渋谷', 12, 'タイムリミットは追いコン', '本日の名言の数々
INSERT INTO public.after VALUES ('5zelv9dSd9', '2012-12-26', '水', '水曜練', 1, 'やよい軒', 'ファミレス', false, false, 600, 600, 0, 0, 0, 2, 2, '渋谷', 2, 'お誕生日おめでとう', '限界を超えると見えてくるものがある。例えば疲れ知らずで走りまくってる人がバテるとか。いつもテンションの高い人が静まるところとか。
INSERT INTO public.after VALUES ('GNNZcTAgR2', '2013-01-05', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 1000, 1000, 0, 0, 0, 13, 13, '渋谷', 13, '通信教育', 'テーブルに某通信教育の案内が置いてあったので見ていた。「話し方」講座とは如何なるものなのだろうか。');
INSERT INTO public.after VALUES ('sGachZIC6K', '2013-01-09', '水', '水曜練', 1, '日高屋', 'ラーメン', true, false, 60, 60, 0, 0, 0, 4, 4, '渋谷', 4, '玉ねぎ', '玉ねぎは美しい。あのスッとした頭といい、丸みを帯びた曲線、あのつやもいい。すべてが美しい。それがあれになるとは…');
INSERT INTO public.after VALUES ('nGedCKUcys', '2013-01-09', '水', '水曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 3, 3, '渋谷', 3, 'ありのままの自分', 'ホノマラーにとって需要のあるキャラは、どんなキャラなのだろうか。
INSERT INTO public.after VALUES ('IiB9zLhpVF', '2013-01-09', '水', '水曜練', 1, '蒙古タンメン　中本', 'ラーメン', false, false, 770, 770, 0, 0, 0, 4, 4, '渋谷', 4, '辛かった', '2男と1男でラーメンの話で盛り上がり、その話をしたら2女が中本に行きたがったので、勢いで来てしまった。男二人は顔を赤くして必死に食べたにもかかわらず、H氏は平然と北極ラーメンを食べていた。
INSERT INTO public.after VALUES ('aQfjmDEsaa', '2013-01-09', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 17, 17, '渋谷', 17, '好きなタイプ', '久々に考えさせられた。しかし答えはわからなかった。何に惹かれるんでしょうね、本当…');
INSERT INTO public.after VALUES ('lJxfHwvajd', '2013-01-09', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 630, 630, 0, 0, 0, 6, 6, '渋谷', 6, '箱根', '春に行われる予定の箱根合宿は楽しみなイベントではあるが、運転手不足が懸念されているようである。
INSERT INTO public.after VALUES ('H91R4ixaij', '2013-01-11', '金', '合同練', 1, 'ジョナサン', 'ファミレス', false, false, 800, 800, 0, 0, 0, 4, 4, '有楽町', 4, 'マラソンサークル', '合同練習後にアフターに参加させていただいた。同じマラソンサークルとして企画などについて比べあった。
INSERT INTO public.after VALUES ('OATObf3QDI', '2013-01-12', '土', '土曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 2, 2, '渋谷', 2, '待ち時間', '鍋を食べに行こうとしたところ「40分待ちです。」と店員さんから言われた。2人は諦めた訳だが、待ち時間の許容範囲は何分なのだろうか……');
INSERT INTO public.after VALUES ('Z89BbohUq8', '2013-01-12', '土', '土曜練', 1, 'はなまるうどん', 'うどん', false, false, 500, 500, 0, 0, 0, 6, 6, '渋谷', 6, '危機', '某氏が近年のホノマラのガチ化を危惧していた。それにしても過激な発言が多かった。');
INSERT INTO public.after VALUES ('JbduqYrj6G', '2013-01-12', '土', '土曜練', 1, 'かにチャーハンの店', '中華', false, false, 680, 680, 0, 0, 0, 4, 4, '渋谷', 3, '年老いたジャニーズ', '年老いたジャニーズの人達はどう扱われていくのか、そして、熱狂的なファンはいつまで追い続けるのか。
INSERT INTO public.after VALUES ('LOAhuhYA2j', '2013-01-12', '土', '土曜練', 1, 'やよい軒', 'ファミレス', false, false, 700, 700, 0, 0, 0, 2, 2, '渋谷', 2, '成人式', '新成人に向けてアドバイスがなされた。近況報告や1人暮らしの感想等色々と話し合った。いま話題のあの人の話しもしたが、洋服についてももちろん喋った。結果、その後洋服屋に向かった。');
INSERT INTO public.after VALUES ('wfJyrwUtWh', '2013-01-12', '土', '土曜練', 1, 'ガスト', 'ファミレス', false, false, 650, 650, 0, 0, 0, 9, 9, '渋谷', 9, '卒業企画', 'ホノマラのウルトラ化を起こした人は、迫り来る卒業を前に単位取得に追われているらしい。
INSERT INTO public.after VALUES ('RweOrKki4Y', '2013-01-12', '土', '土曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 250, 250, 0, 0, 0, 25, 25, '渋谷', 23, '謎の荒ぶり', 'ある人が様々な話題で荒ぶっていた。環境への不満を言ってはいるが、どんな環境でも結局は不満を言っていそうな気がする。
INSERT INTO public.after VALUES ('HZYzLe8Bfl', '2013-01-16', '水', '水曜練', 1, 'ガスト', 'ファミレス', false, false, 630, 630, 0, 0, 0, 14, 14, '渋谷', 14, 'おきなわマラソン  —海兵隊との仁義なき戦い—', 'おきなわマラソンではユニークな応援やエイドがあるらしい。
