INSERT INTO public.after VALUES ('iTVBIVOBhB', '2013-03-02', '土', '土曜練', 1, '伝説のスタ丼', 'どんぶり', false, false, 650, 650, 0, 0, 0, 4, 4, '渋谷', 4, '卵', '手がかじかんでいた先輩の代わりに卵を割る一年生がいた。人の卵を割ってあげるというコウイはとても緊張するようだ。');
