INSERT INTO public.after VALUES ('nEGbRn5bJ9', '2013-03-06', '水', '水曜練', 1, 'ねぎし', '和食', false, false, 1000, 1000, 0, 0, 0, 4, 4, '渋谷', 3, 'ホノマラとは…', 'ホノマラがまだ3回目という一男がいたので、ホノマラーについていろいろ話した。彼はまだメンバーをあまり知っておらず、僕も覚えられていなかった…。頑張ります(´・Д・)」');
