INSERT INTO public.after VALUES ('4Xf2xo6pTg', '2013-03-17', '日', '鳥取マラソン', 1, 'スタミナ太郎', '食べ放題', false, false, 1680, 1680, 0, 0, 0, 8, 8, '鳥取', 8, '食べ放題', '焼肉に、寿司、デザートととにかく食べまくった。
