INSERT INTO public.after VALUES ('ksTeUwrJFt', '2019-03-13', '水', '水曜練', 2, 'サンマルクカフェ', 'カフェ', false, false, 300, 300, 0, 0, 0, 10, 10, '渋谷', 15, '免許', '卒検を控えた人、取り終わった人、今も車校に通う人。様々な人がいて、ゲームでしか運転をしたことがない私も早く免許がほしくなってきました…(YUIの曲にドライブの歌がよく出てくるので江ノ島とか行きたいです)');
