INSERT INTO public.after VALUES ('i0FkZR7ohd', '2018-10-03', '水', '水曜練', 1, '町田商店', 'ラーメン', false, false, 700, 800, 0, 0, 0, 3, 3, '渋谷', 3, 'おかえり', '一緒には行けなかったけど、僕からも大盛りの「おかえり」を、大盛りの完まくで送ろう。');
