INSERT INTO public.training VALUES ('S70nCkyQ9w', '2002-12-23', '月', '本郷', 'あさの', 'sun-normal.gif', 5, 'メンバー：稲田、大塚、松田、中島、西村<BR><BR>って感じの練習あるよ。<BR>さむかたよ。<BR><BR>こいさんいないからあさのできがえたよ。<BR><BR>浅野にちょとくわしくなたよ。<BR><BR>てつもん１周。<BR>足が重くて思うとおりには走れず。<BR><BR>無念。');
