INSERT INTO public.training VALUES ('n8Bvpg2JHF', '2004-09-29', '水', '代々木公園', '雨', 'rain-happy.gif', 1, '雨の中、海永がフライングして一人で走っていた。');
INSERT INTO public.training VALUES ('Jn3cRzZYGQ', '2004-10-02', '土', '代々木公園', '帰国', 'sun-happy.gif', 17, 'エディンバラから帰国した星野さんが、久し振りに来ました。');
INSERT INTO public.training VALUES ('dDHEZwbmYB', '2004-10-06', '水', '代々木公園', '荷物番', 'sun-happy.gif', 12, '今日もロッカーが空いてなく、荷物番をつけた。');
INSERT INTO public.training VALUES ('UVZ7Jlnhmr', '2004-10-13', '水', '代々木公園', '無題', 'sun-happy.gif', 7, '今日はロッカーが空いていたようだ');
