INSERT INTO public.training VALUES ('dqo5XfjLp3', '2004-10-25', '月', '本郷', '久し振り', 'sun-happy.gif', 2, '久し振りに本郷練が行われた。
INSERT INTO public.training VALUES ('slV0XGrWEh', '2004-10-27', '水', '代々木公園', '大人気', 'cloud-happy.gif', 8, '副キャプテンもキャプテンも大人気なかった。');
INSERT INTO public.training VALUES ('qOBnrsVk8y', '2004-11-03', '水', '代々木公園', 'イベント', 'sun-happy.gif', 12, '水曜にもかかわらず、競技場がイベントで使われていたので、外で着替えるはめとなった。');
INSERT INTO public.training VALUES ('i0YArp43BV', '2006-04-01', '土', '代々木公園', '花見', 'sun-happy.gif', 15, '代々木公園内が花見客だらけだったので、練習は散歩になった。');
INSERT INTO public.training VALUES ('NPcGsDIZ1r', '2005-02-20', '日', '皇居', '皇居練', 'cloud-happy.gif', 6, '雨が降っていて、行うかどうか判断が難しかったが、走り始めたらやんでしまった。
INSERT INTO public.training VALUES ('U3KHtOQXAq', '2005-02-23', '水', '代々木公園', '２月', 'sun-normal.gif', 8, '２月は日数が少ない。日が延びてきたことを記念して４０分弱走った。');
INSERT INTO public.training VALUES ('qoS1tCWVCM', '2005-02-26', '土', '代々木公園', '飲み前', 'sun-happy.gif', 15, '荷物番をつけての40分間。飲み会に向けて走りこんだ。');
INSERT INTO public.training VALUES ('92uiEEsgaU', '2005-03-02', '水', '代々木公園', 'フライング', 'sun-happy.gif', 7, '阿部ちゃんも集合場所に来たのに、追いコンの日程だけ決めて、あっという間に帰ってしまいました。
INSERT INTO public.training VALUES ('rP8beKG74l', '2004-12-22', '水', '代々木公園', '無題', 'sun-happy.gif', 8, '江原さんが練習に初参加！練習は短時間で終わった。');
INSERT INTO public.training VALUES ('PyTpmPrgNt', '2004-12-25', '土', '代々木公園', 'クリスマス', 'sun-happy.gif', 11, 'クリスマスにもかかわらず、結構集まった。
INSERT INTO public.training VALUES ('isLV6oNebJ', '2004-12-27', '月', '本郷', '東京ミレナリオ', 'sun-happy.gif', 7, '今日の練習は東京ミレナリオ見学。不忍通り（の隣の道）を通って外堀通りに入り、ミレナリオのある通りに入った。
INSERT INTO public.training VALUES ('GntyI3aZ4o', '2005-01-08', '土', '代々木公園', '初練習', 'sun-happy.gif', 12, '新年初練習。');
INSERT INTO public.training VALUES ('UnCx4dOfPU', '2005-01-10', '月', '本郷', '谷中七福神、五重塔', 'sun-happy.gif', 4, '清は自転車でアップまで参加。
INSERT INTO public.training VALUES ('xPmluopk46', '2005-01-12', '水', '代々木公園', '無題', 'sun-happy.gif', 8, '非常に寒かった。阿部ちゃんは途中から来た。');
INSERT INTO public.training VALUES ('n3ePETgxdT', '2005-01-19', '水', '代々木公園', '無題', 'sun-happy.gif', 9, '');
