INSERT INTO public.training VALUES ('o3r4lMf9Gj', '2005-06-22', '水', '代々木公園', '無題', 'sun-happy.gif', 14, '午前中雨が降っていたのに、１４人も走りに来ました。すごい。
INSERT INTO public.training VALUES ('Vo9qoSidmt', '2005-06-25', '土', '代々木公園', '無理', 'sun-happy.gif', 27, '27,8分間。
INSERT INTO public.training VALUES ('IgC4QUm8uF', '2005-06-27', '月', '本郷', '無題', 'sun-happy.gif', 5, '35分ほどで鉄門コースを1周。店長は今日も自転車でした。');
INSERT INTO public.training VALUES ('e5er4eQHEd', '2005-06-29', '水', '代々木公園', '無題', 'sun-happy.gif', 10, '的場くんがお金を払いに来ました。練習ではまっつんさんと田川さんが快調に飛ばす。
INSERT INTO public.training VALUES ('ZBSDoTftRY', '2005-07-04', '月', '皇居', '皇居練', 'cloud-happy.gif', 4, '今年度一回目の皇居練です。
INSERT INTO public.training VALUES ('Z3QHqMQOjc', '2005-07-06', '水', '代々木公園', '無題', 'sun-sad.gif', 7, '田川さんはいつもどおりフライングしてました。ビルさんとしほさんと濱崎さんが後からやってきました。
INSERT INTO public.training VALUES ('IjFzVstl3Y', '2006-03-29', '水', '代々木公園', '二人！！', 'sun-happy.gif', 10, '金子さんと土橋さんが初ホノマラ。');
INSERT INTO public.training VALUES ('7RxDGEKefl', '2005-07-09', '土', '代々木公園', '雨', 'rain-sad.gif', 9, '雨が降っていて、走るか走らないかもめました。ホノマラが走る組と走らない組の2つに分裂する危機がせまったところで、キャプテンの近野が現れて、練習なしということで収拾してくれました。');
INSERT INTO public.training VALUES ('fSzXPnqIdX', '2005-07-11', '月', '本郷', 'デビュー', 'sun-happy.gif', 5, '笠松さんがついに本郷練デビューです。練習はいつもどおり鉄門コースを一周。本多が足を痛そうにしていました。');
INSERT INTO public.training VALUES ('ycxicLsn1C', '2005-07-13', '水', '本郷', '浅草', 'sun-happy.gif', 5, '人がいないので、代々木公園での練習は中止となり、代わりに本郷で練習を行った。
INSERT INTO public.training VALUES ('QmPtawmONo', '2005-07-16', '土', '代々木公園', 'どちん', 'sun-happy.gif', 18, 'どちんが久々に姿を現した。宮崎勤めは終わり、しばらくはこちらにいるそうだ。');
INSERT INTO public.training VALUES ('4ubLQOo7DG', '2005-07-17', '日', '皇居', '皇居練', 'sun-happy.gif', 15, '今月2回目の皇居練です。
