INSERT INTO public.training VALUES ('GHAfyU2r4E', '2012-01-25', '水', '代々木公園', '雪', 'snow-happy.gif', 15, '珍しくH氏が不在だったせいか、雪が降った。
INSERT INTO public.training VALUES ('Uy1WxQLNmw', '2012-01-28', '土', '代々木公園', 'バランス', 'sun-happy.gif', 26, '各学年がわりと良いバランスで参加していた。
INSERT INTO public.training VALUES ('LOnIY9yZxX', '2012-01-29', '日', '勝田', '勝田全国マラソン', 'sun-angry.gif', 6, '寒い！！
INSERT INTO public.training VALUES ('aLmLvOc4nZ', '2012-02-01', '水', '代々木公園', '勧誘', 'sun-happy.gif', 17, '年会費も入会費も無料、活動は代々木公園を走るだけ！あなたのお友達の◯◯さんも入っている安心のサークルです！
INSERT INTO public.training VALUES ('0wbxOiHMTV', '2012-02-04', '土', '代々木公園', '久しぶり', 'sun-happy.gif', 17, 'ホノマラを二回休んだ某氏が「久しぶりに」練習に参加し、いつも以上のハイテンションぶりを見せた。
INSERT INTO public.training VALUES ('g7Ehy6yTr9', '2012-02-05', '日', '磯子', '神奈川マラソン', 'cloud-happy.gif', 3, '日清オイリオはNISSINじゃなくてNISSHINにしているのはなぜだろう。大学生集団がけた違いに速かった。');
INSERT INTO public.training VALUES ('LGhbsuLVMx', '2012-02-08', '水', '代々木公園', '頑張る人たち', 'sun-happy.gif', 18, '部活のように懸命に走る一群があった。
INSERT INTO public.training VALUES ('JHAxX9gFL7', '2012-02-11', '土', '代々木公園', '多様なホノマラー', 'cloud-happy.gif', 14, '怪我明けの人・大会前の人・大会後の人・好調な人・不調な人など様々なホノマラーが今日も各々のペースで走っていた。');
INSERT INTO public.training VALUES ('wM9GADj9D2', '2012-02-14', '火', '本郷', '浅草寺コース', 'rain-sad.gif', 10, '冷たい雨の中ストイックに浅草寺まで行って帰って約9km。言問通りでは河田氏が「この辺で歯を落とした」と言っていた。');
INSERT INTO public.training VALUES ('wvebZ7GCUg', '2012-02-12', '日', '荒川沿い', '東京赤羽ハーフマラソン', 'cloud-sad.gif', 3, '風の強い荒川河川敷を走る赤羽ハーフ。今回は後半に向かい風だった。
INSERT INTO public.training VALUES ('yaJ2pF6DsD', '2012-02-15', '水', '代々木公園', 'まったり', 'sun-normal.gif', 21, 'テスト疲れが滲み出る法学部生の姿があった。
INSERT INTO public.training VALUES ('Tnypr5r0I8', '2012-02-18', '土', '代々木公園', '今年一番', 'sun-happy.gif', 10, '寒くて人数が少なかったのではないだろうか。久々に前キャプテンが仕切った。');
INSERT INTO public.training VALUES ('feE1QAExIp', '2012-02-19', '日', '沖縄', 'おきなわマラソン', 'sun-normal.gif', 19, 'フル・10kmの部に分かれ、各々が健闘した。
INSERT INTO public.training VALUES ('bYphGS6MFi', '2012-02-22', '水', '代々木公園', '暖かい', 'cloud-normal.gif', 22, 'わりと暖かかった。
INSERT INTO public.training VALUES ('DH2OuCImxx', '2012-02-25', '土', '代々木公園', '一年生がいない', 'cloud-normal.gif', 18, '一年生がみんなスキーに行ってしまった。
