INSERT INTO public.training VALUES ('MysWxnPLcA', '2012-05-03', '木', '皇居', '皇居練', 'rain-happy.gif', 2, '雨が降っていたような気がするが、ホノマラーにとってそんなことはお構いなし。粛々と皇居練が行われた。');
INSERT INTO public.training VALUES ('O67dL1AOIf', '2012-04-25', '水', '代々木公園', '自己紹介', 'rain-happy.gif', 23, '自己紹介時には、ちゃんと名乗りましょう');
INSERT INTO public.training VALUES ('DzGLT1pCB8', '2012-05-02', '水', '代々木公園', '大雨', 'rain-sad.gif', 15, '最近は、大雨であってもそれなりの人数が集まるのが当たり前となっている。
INSERT INTO public.training VALUES ('YgRLuVbjGP', '2012-05-05', '土', '代々木公園', '好天気', 'sun-happy.gif', 32, 'GW中かつ好天気ということもあって、新歓期が終わったにも関わらず、上級生から1年生まで多くの人が集まった。');
INSERT INTO public.training VALUES ('GaZZ1JmQuS', '2012-05-06', '日', '皇居', '突然の雨', 'rain-happy.gif', 9, '企画者の人望からかたくさんの人が集まった。にわか雨にもめげず各々のペースで走っていた。');
INSERT INTO public.training VALUES ('araxvzJzRd', '2012-05-09', '水', '代々木公園', '降ったり止んだり', 'cloud-sad.gif', 18, '雨が降ったり止んだりの微妙な天気のためか、人数が少ないように感じた。
INSERT INTO public.training VALUES ('xHLlkljAB9', '2012-05-12', '土', '代々木公園', '3女', 'cloud-happy.gif', 48, '3年生女子の方が珍しく揃っていた。
INSERT INTO public.training VALUES ('97QOOEyh5k', '2012-05-16', '水', '代々木公園', '風邪', 'sun-normal.gif', 28, '風邪が流行っているようである。
INSERT INTO public.training VALUES ('TgSBu9knPH', '2012-05-19', '土', '代々木公園', '五月祭', 'sun-happy.gif', 37, '本郷キャンパスで行われている五月祭のため、東大の一年生がいなかった。しかし他大の一年生がたくさん来たので、フレッシュな雰囲気の中で練習を行うことができた。
INSERT INTO public.training VALUES ('m9X4XkE7NZ', '2012-05-23', '水', '代々木公園', '安定', 'sun-happy.gif', 25, '新歓期が終わってから暫くすると、安定して集まるメンバーというのが分かってくるものである。
INSERT INTO public.training VALUES ('sOrACzJL1C', '2012-05-26', '土', '代々木公園', '執念', 'sun-happy.gif', 24, '山手線一周後にも関わらず、眠気や筋肉痛に耐えながら執念で練習にまでやってくる人が10名近くいた。
INSERT INTO public.training VALUES ('ckAIGOCOYr', '2012-05-30', '水', '代々木公園', '高速化', 'sun-normal.gif', 29, '最近のホノマラではトップ集団の高速化が進んでいる。
INSERT INTO public.training VALUES ('zaODEZRGjv', '2012-05-27', '日', '代々木公園', '日曜の朝', 'sun-happy.gif', 10, 'たまには、朝練というのも良いものである。
INSERT INTO public.training VALUES ('L1GTT5Bv7L', '2012-06-02', '土', '代々木公園', '学年会', 'sun-normal.gif', 54, '一年会、二年会、三年会があるということで、久しぶりに練習に来た人や、新しく来た人まで多くのホノマラーが集まった。
INSERT INTO public.training VALUES ('1g5IW76n8u', '2012-06-06', '水', '代々木公園', '今日も平和だった', 'cloud-normal.gif', 24, '書くネタが思いつかないので、ついにこの話題にしてしまった。
INSERT INTO public.training VALUES ('Ym9zsrD9ou', '2012-06-09', '土', '代々木公園', '男のみの練習', 'rain-sad.gif', 9, '天候不良のため、久々に少数での練習となった。
INSERT INTO public.training VALUES ('9uCmW5uHGw', '2012-06-09', '土', '代々木公園', '逆走', 'rain-normal.gif', 2, 'この時期のホノマラ土曜練は17:00に集合して始めます。何周もみんなを探して雨の中を逆走するホノマラーがいた。
INSERT INTO public.training VALUES ('ztW9NDVLN3', '2012-06-13', '水', '代々木公園', '雨雲', 'cloud-normal.gif', 24, '田儀さんは雨雲にも人気である、今週末の雨雲をどこかへ連れ去ってくれないだろうか。');
INSERT INTO public.training VALUES ('CILKATydlq', '2012-06-17', '日', '夢の島', 'ゆめのしま12時間リレー', 'rain-sad.gif', 41, 'ホノマラOB(OBチーム)・ホノマラＡ(ガチチーム)・ホノマラＢ(2年チーム)・チームTBT(2年チーム)の4チームが出場した。
INSERT INTO public.training VALUES ('adSUEwtVxf', '2012-06-20', '水', '代々木公園', '蒸し暑い', 'sun-normal.gif', 24, '非常に蒸し暑かった。
INSERT INTO public.training VALUES ('wgEf6GPWYt', '2012-06-24', '日', '富里', '富里スイカロードレース', 'sun-normal.gif', 4, '蒸し暑い天候であったが、みな給スイカ所目指して頑張っていた。
INSERT INTO public.training VALUES ('fFZKigad5r', '2012-06-24', '日', '皇居', '皇居練', 'sun-happy.gif', 12, '4時間半走を行った。
INSERT INTO public.training VALUES ('3B0bm8Rzpg', '2012-06-23', '土', '代々木公園', '1年生', 'cloud-happy.gif', 43, '1年生、特に女子がたくさん来た。
INSERT INTO public.training VALUES ('mY7IUXjJ1x', '2012-06-27', '水', '代々木公園', '5年生', 'sun-normal.gif', 30, '水曜日であるにも関わらず、たくさんの人が練習に参加した。
INSERT INTO public.training VALUES ('INERPCocHn', '2012-06-30', '土', '代々木公園', '踊り', 'sun-normal.gif', 46, '代々木公園の集合場所で踊りの練習をしている30人程の集団がいたが、その中の一人について、かつてホノマラにきたことがあったなかったで、論争をしている人達がいた。
INSERT INTO public.training VALUES ('5SleSk2Xvt', '2012-07-04', '水', '代々木公園', 'フライング', 'sun-happy.gif', 22, 'ここ最近、秋田100キロに向けてフライング練習をする人が増えている。
INSERT INTO public.training VALUES ('0cWNsb9wpT', '2012-07-07', '土', '代々木公園', '仕切る', 'rain-sad.gif', 10, 'みんなの不安な視線をよそに仕切ってる人がいた。みなさん雨の中お疲れ様でした！！！');
INSERT INTO public.training VALUES ('XJpGUwTMMO', '2012-07-04', '水', '代々木公園', 'ディレード練', 'sun-normal.gif', 1, '通常練が終わった頃合いを見計らい一人で代々木公園を走った。近頃フライングが流行っているらしいがこれからはディレード練の時代だ。');
INSERT INTO public.training VALUES ('kZ1Hjn4SzP', '2012-07-07', '土', '代々木公園', '箱根企画', 'cloud-normal.gif', 11, '蒸し暑い気候の中、3月の企画のリベンジとして箱根5区山登り企画が実施された。
INSERT INTO public.training VALUES ('xYAWrxBHCg', '2012-07-08', '日', '皇居', '皇居練', 'sun-happy.gif', 10, '箱根からの直行組も含めて、各々が暑さや疲労や眠気と闘いながらも自らのペースで走っていた。
INSERT INTO public.training VALUES ('zJAyF7topn', '2012-07-11', '水', '代々木公園', '戻りましょう', 'sun-normal.gif', 22, 'キャプテンがやってくると同時に練習の終わりを告げた。');
INSERT INTO public.training VALUES ('m8RPX7iMHY', '2012-06-16', '土', '代々木公園', 'ホノマラですか？', 'rain-happy.gif', 11, 'リレーで特に2年生が大量に不在でも、たとえ雨が降っていようとも練習はある。
INSERT INTO public.training VALUES ('wLKUa2rWJI', '2012-07-14', '土', '代々木公園', '懐かしい面々', 'sun-happy.gif', 36, '名古屋の研修から帰ってきたあの人がいたり、学年会のために4年生が沢山いたり、久々の3年生が来たりなど、まるで去年の練習かのようなメンバーが揃っていた。');
INSERT INTO public.training VALUES ('DaDzzyU0Hf', '2012-07-18', '水', '代々木公園', '三重の女', 'sun-happy.gif', 14, '三重の女は好かないが、岐阜の女は良いと主張する人がいた。
INSERT INTO public.training VALUES ('NPDYwmtiLv', '2012-07-21', '土', '代々木公園', '涼しい', 'cloud-happy.gif', 22, 'この時期としてはとても涼しく、走りやすかった。
INSERT INTO public.training VALUES ('ffetRT7sKN', '2012-07-25', '水', '代々木公園', '不在', 'sun-angry.gif', 18, 'いつもいるはずのある人が、茨城の方にいらっしゃるとかで不在であった。
INSERT INTO public.training VALUES ('fGqZVcjPKB', '2012-07-28', '土', '代々木公園', '隅田川花火大会', 'sun-happy.gif', 30, 'そんなものが開催されていたようだが、上から下まで多くの健全なホノマラーが代々木公園に集まった。
INSERT INTO public.training VALUES ('ajvkFtTyVL', '2012-08-01', '水', '代々木公園', '合宿前', 'sun-normal.gif', 31, '合宿前最後の練習ということで、久々に練習に来た人も多かった。
INSERT INTO public.training VALUES ('ypsXcNf0lr', '2012-08-08', '水', '代々木公園', 'ディレード練', 'sun-happy.gif', 3, '居残り練習をしていたところ、バイト上がりの二人と合流した。
INSERT INTO public.training VALUES ('edrMjLF9zC', '2012-08-08', '水', '代々木公園', '合宿後', 'sun-happy.gif', 22, '合宿後最初の練習であったが、1,2年生を中心にそれなりの人数が集まった。
INSERT INTO public.training VALUES ('mlivK2kStZ', '2012-08-11', '土', '代々木公園', 'ディレード練', 'cloud-normal.gif', 1, '練習に物足りなかった中野さんと田端がバイト帰りの人と練習をした。雨が降るか否かの微妙な天気も関係なくモチベーションを高く持ち走った。');
INSERT INTO public.training VALUES ('V91MMhDMip', '2012-08-11', '土', '代々木公園', 'おばけ', 'cloud-normal.gif', 19, 'お盆まっただ中であったが、意欲あるホノマラーが多数集まった。
INSERT INTO public.training VALUES ('TxroJbBu1X', '2012-08-12', '日', '皇居', '午前中の皇居', 'cloud-sad.gif', 4, 'それぞれのペースで皇居を走った。曇り空で蒸し暑かった。ビールを飲みたがる人がいた。');
INSERT INTO public.training VALUES ('jrf87f3WA7', '2012-08-12', '日', '皇居', '午後の皇居', 'sun-sad.gif', 7, '日差しが強く暑かったがそれぞれ自分なりに走っていた。練習後のビールの味を思い浮かべながら走ると頑張れた。');
INSERT INTO public.training VALUES ('VzcDNx3NIJ', '2012-08-15', '水', '代々木公園', 'お土産', 'sun-normal.gif', 21, '帰省している人も多いので練習にあまり人は集まらないだろうと予想して、お土産を少なめに持って行ったところ、想定以上に人が来たので足りなくなってしまった。
INSERT INTO public.training VALUES ('ieKBOAD1Qm', '2012-08-18', '土', '代々木公園', 'じめじめ', 'sun-sad.gif', 21, '雨が降ったあとの練習で涼しいかと思いきや、じめじめしていて暑かった。
INSERT INTO public.training VALUES ('orqryYknQN', '2012-08-22', '水', '代々木公園', 'シューズ', 'sun-normal.gif', 24, 'シューズを新調した田端が、小谷さんと同じシューズであることに興奮していた。');
INSERT INTO public.training VALUES ('LQxeHKIVmW', '2012-08-25', '土', '代々木公園', 'いない', 'sun-happy.gif', 19, '北海道マラソンと秩父旅行で多くのホノマラーが不在のため、年齢層の高い練習だった。
INSERT INTO public.training VALUES ('ED4WL6jEZ1', '2012-12-05', '水', '代々木公園', '合同練習', 'sun-normal.gif', 24, '代替わり後初の練習は早大ホノマラ完走会との合同練習。三人の二年生が来てくれた。
INSERT INTO public.training VALUES ('X3DMu3RDv4', '2012-08-29', '水', '代々木公園', 'ストイック', 'sun-happy.gif', 24, '先の北海道マラソンでの結果に対する罰ゲームとして、3日連続フルマラソンを行うストイックな人が練習前から走っていた。
INSERT INTO public.training VALUES ('OZgzfMXFXZ', '2012-09-01', '土', '代々木公園', '雨上がり', 'cloud-normal.gif', 18, '浦佐マラソン&雨上がり&1年理系は試験前という三重苦だったが、それなりに人が集まった。すごく久々に仕切れるのを喜んでいる男がいた。');
INSERT INTO public.training VALUES ('5lnHIfeXIS', '2012-09-02', '日', '浦佐', '浦佐山岳耐久マラソン', 'sun-happy.gif', 26, '暑さや激しいアップダウンにも負けずに、参加したホノマラー全員が完走した。
INSERT INTO public.training VALUES ('FnrCzFkscs', '2012-09-05', '水', '代々木公園', 'ナシ', 'cloud-happy.gif', 33, '家がナシ農家S氏が練習後にナシを配っていた。
INSERT INTO public.training VALUES ('ajI1vWS2O2', '2012-09-08', '土', '代々木公園', '白い恋人', 'sun-normal.gif', 28, '札幌土産として、かつて世間を賑わせた"白い恋人"を配っている人がいた。
INSERT INTO public.training VALUES ('SocMw1kI8o', '2012-09-08', '土', '高尾山〜陣馬山', '高尾練', 'sun-happy.gif', 12, '1年生3人を含む多くのメンバーが参加した。
INSERT INTO public.training VALUES ('lW6Wqcj6Eu', '2012-09-13', '木', '皇居', '午後の皇居', 'sun-normal.gif', 12, 'コンパの前の皇居練。井上真央や皇太子閣下を見かけた。暑かった。');
INSERT INTO public.training VALUES ('lI3eW03Hmo', '2012-09-12', '水', '代々木公園', '暗い', 'sun-normal.gif', 32, '段々と日が暮れる時間が早まってきている。');
INSERT INTO public.training VALUES ('ooAgwUQpav', '2012-09-15', '土', '代々木公園', '秋田100キロ', 'sun-normal.gif', 27, '秋田100キロ大会参加も、「サークルで秋田旅行に行くぜ〜」と言うとチャラく聞こえたりする。');
INSERT INTO public.training VALUES ('nfuDMJ3ME5', '2012-09-19', '水', '代々木公園', 'もわっ', 'cloud-sad.gif', 29, '湿気が高くもわっとしていたため、気温以上に暑く感じられた。
INSERT INTO public.training VALUES ('N2uPQNJaJV', '2012-09-22', '土', '代々木公園', '100km', 'sun-normal.gif', 30, 'みんなで秋田100kmの大会に出る人々に思いを馳せた。その一方で、朝の6時半から代々木公園で黙々と100kmを走っていた人がいた。
INSERT INTO public.training VALUES ('4Cdxwjfega', '2012-09-23', '日', '秋田', '秋田100キロマラソン', 'cloud-normal.gif', 12, '終始曇りの恵まれた天候の中、秋田100キロに
INSERT INTO public.training VALUES ('mbTWrZM4ZA', '2012-09-26', '水', '代々木公園', '秋田帰り', 'cloud-happy.gif', 29, '秋田100キロ直後で、まともに走れない人がいる一方で、フライングしたり先頭で6周したりと元気な人達もいた。
INSERT INTO public.training VALUES ('YHxgfYztnb', '2012-09-29', '土', '代々木公園', '1年会', 'sun-normal.gif', 46, '1年会があるとのことで、多くの1年生が練習に来ていた。
INSERT INTO public.training VALUES ('Jn8JOslZoc', '2012-10-03', '水', '代々木公園', '雨', 'rain-happy.gif', 16, '15時過ぎには非常に強い雨が降っていたにも関わらず、練習に人が集まった。
INSERT INTO public.training VALUES ('ZnLggN1RFb', '2012-10-06', '土', '代々木公園', '久々', 'cloud-normal.gif', 46, '練習に勢揃いは新歓のとき以来である。
INSERT INTO public.training VALUES ('7Qh59A5tbu', '2012-10-10', '水', '代々木公園', 'クラス', 'cloud-happy.gif', 34, '同級生の所属クラスを完璧に把握している1年生がいた。
INSERT INTO public.training VALUES ('dK4Fp38YEE', '2012-10-13', '土', '代々木公園', '自己ベスト', 'sun-happy.gif', 55, '代々木公園6周の自己ベストを大きく更新して喜びに浸る人が2名いた。
INSERT INTO public.training VALUES ('VvPubDVQOp', '2012-10-17', '水', '代々木公園', '雨', 'rain-sad.gif', 20, '雨によりはげるのを気にする人達がいた。');
INSERT INTO public.training VALUES ('qiQRFu30Yi', '2012-10-21', '日', '教育の森公園', '文の京12時間リレー', 'sun-normal.gif', 38, '1年生を中心に12時間リレーへと参加した。
INSERT INTO public.training VALUES ('NsQdtz3faW', '2012-10-20', '土', '代々木公園', '弟', 'sun-normal.gif', 29, '沖縄から(？)はるばる弟がやってきた。メガネを掛けても取っても姉そっくり。合宿以来の参加のホノマラーをキュンキュンさせていた。
INSERT INTO public.training VALUES ('v8nkxsVdxg', '2012-10-21', '日', '山梨', '第３回甲州フルーツマラソン', 'sun-happy.gif', 3, '熱い日差しの葡萄畑の中ホノマラーが激走した。レンタカーでの移動だった。みな無事に完走してぶどうやジュースを堪能した。ストイックに観光やSAは封印した。');
INSERT INTO public.training VALUES ('QVnnDDXpKX', '2012-10-24', '水', '代々木公園', '懐かしい', 'sun-normal.gif', 29, '山手線1周ぶりの人や、夢の島リレーぶりの人など、久しぶりの2年生が沢山来た。
INSERT INTO public.training VALUES ('imRW2tDBQd', '2012-10-27', '土', '代々木公園', '超高速時代', 'cloud-happy.gif', 49, '先頭集団の高速化はとどまることを知らない。
INSERT INTO public.training VALUES ('WnqVuVuHeG', '2012-10-28', '日', '皇居', '皇居練', 'rain-happy.gif', 14, '今にも振り出しそうな雨を気にしつつも皇居練が開催された。
INSERT INTO public.training VALUES ('69eAd8AwgF', '2012-11-03', '土', '代々木公園', 'イベント目白押し', 'sun-happy.gif', 25, '総長杯、湘南国際マラソン、明大祭と大きなイベントが重なっていた。
INSERT INTO public.training VALUES ('XbELcF1zPR', '2012-10-31', '水', '代々木公園', 'ぶっち', 'sun-normal.gif', 25, 'ついに、授業をぶっちしてまで練習にやってきた1年生がいた。
INSERT INTO public.training VALUES ('im9Eo73d2i', '2012-11-07', '水', '代々木公園', 'メンバー', 'cloud-happy.gif', 28, '下半期の水曜日に来るメンバーというのも固まってきた気がする。
INSERT INTO public.training VALUES ('AJmLtISeaT', '2012-11-10', '土', '代々木公園', '徽音祭', 'sun-normal.gif', 41, 'お茶大の文化祭のためキャプテンが不在であった。
INSERT INTO public.training VALUES ('6faolG8QxK', '2012-11-14', '水', '代々木公園', '少ない', 'cloud-normal.gif', 18, '寒くなってきた影響か練習参加者が少なくなってきた。
INSERT INTO public.training VALUES ('tZeEfTglN6', '2012-11-17', '土', '代々木公園', '遅刻魔集結', 'rain-sad.gif', 24, '大雨の影響で人が少なかった。1500mTTしてる人がいた。走ってない人も多くいて平和だった。');
INSERT INTO public.training VALUES ('oRFc4u2wns', '2012-11-21', '水', '代々木公園', '落ち葉', 'sun-normal.gif', 24, 'かき集めた落ち葉の山に寝転んで埋まっている人がいた。
INSERT INTO public.training VALUES ('8odx27QzfD', '2012-11-24', '土', '代々木公園', '駒場祭', 'sun-normal.gif', 35, '駒場祭のピアノの会の発表に出演していたある1年生の演奏を聞いていたため、練習に遅れてやって来る集団がいた。
INSERT INTO public.training VALUES ('6CNXU1BSIM', '2012-11-28', '水', '代々木公園', '関係', 'sun-normal.gif', 28, '新しくホノマラにやってきた子と某1男の関係が不思議なことになっていた。
INSERT INTO public.training VALUES ('d5tkk2OKbA', '2012-05-25', '金', '山手線周り', '山手線一周', 'sun-normal.gif', 46, 'ホノマラ毎年恒例の山手線一周企画が今年も実施された。
INSERT INTO public.training VALUES ('otJx9ihKkA', '2012-12-01', '土', '代々木公園', 'ウィンブレ', 'sun-happy.gif', 52, '代替わり前の練習ということで多くの人が集まった。
INSERT INTO public.training VALUES ('oUINKVTpPM', '2012-12-08', '土', '代々木公園', 'ホノルルマラソン', 'sun-happy.gif', 43, 'これからホノルルへと飛び立つ人が何人か来ていた。
INSERT INTO public.training VALUES ('9rWwHsEwg9', '2012-12-12', '水', '代々木公園', '寒い', 'sun-normal.gif', 20, '最近は天気は良いけどとても寒い。手が凍りそうである。
INSERT INTO public.training VALUES ('wyz1Ufiw3Y', '2012-12-15', '土', '代々木公園', 'ホラー映画', 'cloud-sad.gif', 19, '昼前から降っていた雨が運良く上がった。
INSERT INTO public.training VALUES ('afS2chUQiI', '2012-12-09', '日', 'ハワイ', '第40回ホノルルマラソン', 'sun-happy.gif', 8, 'ほのまらーの最大の目標であるホノルルマラソンが開催された。今年は２年生を中心にしてメンバーを集め８人で参加した。とある４年生からしたらこの４年間の集大成である。ホノルルマラソン参加で練習回数+50くらいにならないかなど冗談もあった。
INSERT INTO public.training VALUES ('KjuGyzOlgK', '2012-12-19', '水', '代々木公園', 'この冬一番', 'sun-sad.gif', 20, '今日はこの冬一番の冷え込みのようであった。皆防寒万全と思いきや、半ズボンの人を何人か見かけて改めてホノマラーの強さを知った。');
INSERT INTO public.training VALUES ('V09b0fXjeg', '2012-12-22', '土', '駒沢公園', '駅伝', 'cloud-happy.gif', 41, 'チームが直前で変わるなどのハプニングもあったが、駅伝企画が予定通り行われた。
INSERT INTO public.training VALUES ('mSNoIjEgH7', '2012-12-23', '日', '皇居', '天皇誕生日', 'cloud-normal.gif', 15, '天皇誕生日に皇居の周りを走る意識が高い集団──ホノマラ。
INSERT INTO public.training VALUES ('tVyPFZn3DS', '2012-12-26', '水', '代々木公園', '極寒', 'sun-normal.gif', 21, '「寝るな！寝たら死ぬぞ！」──そんな感じだった。');
INSERT INTO public.training VALUES ('z8RcFkShtl', '2012-12-29', '土', '皇居', '走り納め', 'sun-normal.gif', 11, '今日は通常練が中止なので代わりに皇居練を実施した。天気予報では雨だったが、予想が外れて晴れていた。この中に晴れ男or晴れ女がいるのかもしれない。');
INSERT INTO public.training VALUES ('Vt5zRm9ACp', '2013-01-05', '土', '代々木公園', '初練習', 'sun-normal.gif', 15, '今年もよろしくお願いします。
INSERT INTO public.training VALUES ('dFc43hIsqq', '2013-01-09', '水', '代々木公園', '骨折', 'sun-normal.gif', 19, 'スキーで骨を三カ所折ってきた彼の姿があった。骨折しやすいという彼のイメージに違わない折りっぷりだと思う。みなさん骨は大切に！');
INSERT INTO public.training VALUES ('zCSpwuCCBN', '2013-07-27', '土', '代々木公園', '隅田川', 'cloud-normal.gif', 25, '健全なホノマラーには花火大会なんて関係ない。でも、何故か雨は降ってくれない。
(このあと、大雨により花火大会は中止になった模様。)');
INSERT INTO public.training VALUES ('SJQHUN7gWe', '2013-01-11', '金', '皇居', '合同練習〜派遣編〜', 'sun-normal.gif', 6, '昨年、ホノマラの練習に来ていただいたホノルルマラソン完走会の練習に参加させていただいた。聞いていたよりも人数が多くて少し驚いた。
INSERT INTO public.training VALUES ('WSbIJ3zVXt', '2013-01-12', '土', '代々木公園', 'OB会', 'sun-happy.gif', 29, 'OB会が開かれるということで練習開始時には多くのOBの方々が集まった。
INSERT INTO public.training VALUES ('PUdgKKD8YI', '2013-01-16', '水', '代々木公園', '雪', 'happy.gif', 15, '珍しく東京でも雪が積もった。雪合戦をする人や池の氷の上を歩こうとしてやめる人たちがいた。
INSERT INTO public.training VALUES ('9PVeofXQWr', '2013-01-19', '土', '代々木公園', '調整', 'sun-normal.gif', 29, '織田フィールドは相変わらず使えなかったが、公園内の雪は水曜に比べると格段に減った。
INSERT INTO public.training VALUES ('1dT2sjHcTn', '2013-01-20', '日', '海浜幕張', '千葉マリンハーフマラソン', 'cloud-sad.gif', 17, '強風に阻まれ思うような走りができなかったホノマラーが多かった一方で、初ハーフを好記録で完走したホノマラーもいた。
INSERT INTO public.training VALUES ('F7Tei3nTiE', '2013-01-23', '水', '代々木公園', '試験期間？', 'sun-normal.gif', 15, '試験期間中なのだろうか、人がいつもより少ない気がする。珍しいことに下半期練習参加回数TOP3が誰も参加してなかった。');
INSERT INTO public.training VALUES ('cXiY2eB95L', '2013-01-26', '土', '代々木公園', 'どれが好き？', 'sun-normal.gif', 28, '「6と12と24、どれが好き？」
INSERT INTO public.training VALUES ('ljCJXc8Ubv', '2013-01-30', '水', '代々木公園', '二年会', 'sun-happy.gif', 27, '今日は二年会が開かれるとのことで二年生が最大勢力になっていた。
INSERT INTO public.training VALUES ('GxpK2qHMlF', '2013-02-02', '土', '代々木公園', '春ですね', 'sun-normal.gif', 31, '暦の上ではもうすぐ春ですもんね。
INSERT INTO public.training VALUES ('5pYn6jn9Lo', '2013-02-03', '日', '皇居', '疲れた。', 'cloud-happy.gif', 15, '峠走の翌日なのに皇居に来る元気な人が何人かいた。
INSERT INTO public.training VALUES ('PQttdW19eE', '2013-02-02', '土', 'ヤビツ峠', '峠走', 'sun-normal.gif', 7, '鬼神の走りを見せる人やガードレールにぶつかりながらも生還した人がいた');
INSERT INTO public.training VALUES ('uDmPtSJzdN', '2013-02-06', '水', '代々木公園', '極寒', 'rain-sad.gif', 10, '冷たい雨が降る中、元気なホノマラーたちが代々木公園に集まった。トラックを使って良いペースで練習出来ていたようである。');
INSERT INTO public.training VALUES ('z4MASwSMwP', '2013-02-09', '土', '代々木公園', 'テスト明け', 'sun-happy.gif', 33, 'そろそろテストが終わった人が増えてきた。
INSERT INTO public.training VALUES ('kpu6da4vhh', '2013-02-13', '水', '代々木公園', '明日はバレンタインだから……', 'sun-normal.gif', 18, '「チョコレート作ってきたよ」とドヤ顔で市販のチョコレートを配っている男の子がいた。
INSERT INTO public.training VALUES ('1JVGpdJE1c', '2013-02-16', '土', '代々木公園', '((+_+))ガクブル', 'cloud-happy.gif', 7, '明日、沖縄、熊本と大会があるため人がとても少なかった(´・ω・`)ワインを呑んでから走りにくるというすごい方がいた(゜ロ゜;
INSERT INTO public.training VALUES ('BUHQP0rg0K', '2013-02-17', '日', '沖縄', 'おきなわマラソン', 'sun-angry.gif', 26, '高低差のあるコースと暑さで、初フルの人が多い割りには辛い大会になってしまった。
INSERT INTO public.training VALUES ('AYeVQ2FQds', '2013-02-17', '日', '熊本県', '熊本で大会', 'sun-normal.gif', 7, '熊本城マラソンが開催された。その裏で人吉市でも大会が開催された。好記録続出だった。');
INSERT INTO public.training VALUES ('KDxZ22bbSV', '2013-02-20', '水', '代々木公園', '大会後', 'sun-sad.gif', 20, '初フルマラソン後なのに練習してる人が思ったより多かった。');
INSERT INTO public.training VALUES ('6t8tp09QzG', '2013-02-23', '土', '代々木公園', 'ちょっと早めの新歓期？', 'sun-normal.gif', 33, '新歓期を前に新しい人が二人も来た。
INSERT INTO public.training VALUES ('mDz2OS4HET', '2013-02-27', '水', '代々木公園', '明るい', 'sun-normal.gif', 28, '練習が終わっても、はっきりと皆の顔が見えるくらい明るくなった。もう少し暖かくなると嬉しい。
INSERT INTO public.training VALUES ('eMRTkp0PQ9', '2013-03-02', '土', '代々木公園', '北風', 'sun-happy.gif', 34, '北風が強かった。あまりの強さに前の人を風避けに使う集団がいた。
INSERT INTO public.training VALUES ('ktOZzsdCBF', '2013-03-03', '日', '昭和記念公園', '立川シティハーフマラソン', 'sun-normal.gif', 6, '箱根の予選会にもなるコースで行われるハーフマラソンを走った。学連がたくさん出場しており、速い人が多かった。その分、良い結果が出た人もいた。
INSERT INTO public.training VALUES ('rJHcj9Cm1e', '2013-10-30', '水', '代々木公園', '暗い', 'sun-normal.gif', 18, 'アップのあとのストレッチで誰が誰だか分からない季節になってきました。');
INSERT INTO public.training VALUES ('qjiDtVzcP4', '2013-03-06', '水', '代々木公園', '暑い', 'sun-happy.gif', 18, '普段よりも暑かった。
INSERT INTO public.training VALUES ('EkJPwwfa4I', '2013-03-10', '日', '皇居', '朝練', 'sun-happy.gif', 3, '３人しか集まらず元キャプテンの人望のなさが改めて浮き彫りになった。7時半から集まって練習を行ったが、結局90分しかいなかったのでもっと遅くに集まっても良かったんではないかという結論に至った。それでも朝早くから走るのは気持ちいい。');
INSERT INTO public.training VALUES ('JHkvOqqapW', '2013-03-09', '土', '代々木公園', '暖かいというか暑い', 'sun-happy.gif', 26, '3月とは思えないほど暖かかった。
INSERT INTO public.training VALUES ('i33jd36lRW', '2013-03-13', '水', '代々木公園', '風', 'sun-sad.gif', 25, '「毎年３月ってこのなに風が強かったけ？」と首をかしげたくなるほど最近は風が強い。
INSERT INTO public.training VALUES ('JZqjoX2iW0', '2013-03-16', '土', '高尾山口〜陣馬山', '高尾練', 'sun-normal.gif', 6, '前回とは打って変わり、少人数でワイワイと走る練習になった。これで最後の高尾山かも…と感慨にふける人がいた。
INSERT INTO public.training VALUES ('V3RykmZIB2', '2013-03-16', '土', '代々木公園', 'CM撮影', 'sun-happy.gif', 16, '練習前に集合していたら謎の人からCM撮影の勧誘を受けた。報酬は少なくとも一人あたり５万円とのことだったが…果たしてそんなうまい話があるのだろうか…');
INSERT INTO public.training VALUES ('NheMofmIsM', '2013-03-17', '日', '鳥取', '鳥取マラソン', 'sun-happy.gif', 12, '学年旅行の一環として、鳥取マラソンに出場した。
INSERT INTO public.training VALUES ('D3XpocA86g', '2013-03-20', '水', '代々木公園', '花見', 'sun-normal.gif', 15, '桜が咲き始めた祝日ということでお花見の人が多かった。
INSERT INTO public.training VALUES ('H4NLddDZ5f', '2013-03-27', '水', '代々木公園', '肌寒い', 'cloud-normal.gif', 12, 'しばらく暖かい日が続いていたのに、今日は午前は雨で肌寒い天気であった。
INSERT INTO public.training VALUES ('6QLdAlKXvx', '2013-03-30', '土', '代々木公園', '寒かった', 'cloud-normal.gif', 20, 'みなさん知ってますか??
INSERT INTO public.training VALUES ('9N0cL3h9o7', '2013-03-23', '土', '代々木公園', '花見&#9313;', 'sun-happy.gif', 23, 'ついに満開となった桜。公園のいたる所で宴会が開かれている中、ホノマラの集合場所はいつも通りであった。御神木の力恐るべし…');
INSERT INTO public.training VALUES ('lYBKfuPdep', '2013-04-03', '水', '代々木公園', '今日から新年度', 'sun-normal.gif', 15, '学生長距離界で超有名なあの人らしき人物が代々木公園を走っていた。 
INSERT INTO public.training VALUES ('GrISKzo25t', '2013-08-14', '水', '代々木公園', '蚊', 'sun-angry.gif', 19, '非リアは蚊にも好かれない。');
INSERT INTO public.training VALUES ('WwzHSFmKVt', '2013-04-06', '土', '代々木公園', '爆弾低気圧', 'rain-sad.gif', 22, '明日の行田ハーフが中止となるほどの爆弾低気圧が来ているなか、ホノマラーは相変わらず走っていた。ここまで人のいない織田フィールドも久しぶりだった。
INSERT INTO public.training VALUES ('aSwYZ4YGCn', '2013-04-10', '水', '代々木公園', '新歓期', 'cloud-happy.gif', 36, 'サークルオリも終わった最初の練習ということで、ついにまとまって新入生が来てくれた。
INSERT INTO public.training VALUES ('xdQC5cRBX0', '2013-04-13', '土', '赤羽', 'RUNNET EKIDEN', 'sun-happy.gif', 22, 'ホノマラからは恒例の仮装をしつつ、５チームが参加した。
INSERT INTO public.training VALUES ('R2yTJJt0RG', '2013-04-20', '土', '代々木公園', '雨', 'rain-sad.gif', 24, '新歓期なのにあいにくの雨…
INSERT INTO public.training VALUES ('u9IfJIrnH8', '2013-04-17', '水', '代々木公園', 'MARCH', 'sun-normal.gif', 36, '法政から一人新入生が来たので「ついにMARCHが揃った！」と喜ぶ人がいたが、よくよく考えると「C」がいなかった・・・');
INSERT INTO public.training VALUES ('JWa5Y1Itai', '2013-04-27', '土', '代々木公園', '新歓期ラスト', 'cloud-happy.gif', 42, '今日が本年度の新歓期最後の練習である。ご協力いただいた上級生の方々、二年生のみなさん新歓活動お疲れ様でした。
INSERT INTO public.training VALUES ('a17Mxu6JAd', '2013-05-01', '水', '代々木公園', '終わらない！', 'cloud-sad.gif', 27, '5月になったがホノマラの新歓期はまだまだ終わらない！
INSERT INTO public.training VALUES ('k9aya9hksp', '2013-05-04', '土', '代々木公園', '合宿下見', 'sun-normal.gif', 25, '二年生が少なく、上級生の方が多く来られたため新鮮な練習であった。
INSERT INTO public.training VALUES ('Dsn4vbhiAp', '2013-05-08', '水', '代々木公園', 'そろそろ', 'sun-normal.gif', 31, '日常生活では「暖かい」で済むが。走ると「暑い」季節に突入した…
INSERT INTO public.training VALUES ('CfTssT2JMb', '2013-05-11', '土', '代々木公園', 'トラック', 'rain-sad.gif', 27, '雨が降っており、空いていたので織田フィールドで走った。
INSERT INTO public.training VALUES ('K6JFXY1dbN', '2013-04-13', '土', '代々木公園', '多すぎる', 'sun-happy.gif', 68, '新歓期はここまで集まるのか！と叫びたくなるくらい人が集まった。
INSERT INTO public.training VALUES ('NCfeQUXNZs', '2013-05-12', '日', '皇居', '皇居練', 'sun-happy.gif', 22, '今年度初の皇居練が開かれた。
INSERT INTO public.training VALUES ('Ir0UjULLcT', '2013-05-15', '水', '代々木公園', 'まだまだ終わらない', 'sun-normal.gif', 35, 'もう五月も半ば。それでも、今日は5人の新入生が来た。');
INSERT INTO public.training VALUES ('sHN4vvATEU', '2013-05-18', '土', '代々木公園', '先導', 'sun-happy.gif', 15, '今日の先導はチビッ子二人組であった。
INSERT INTO public.training VALUES ('YNl1BzCPZD', '2013-04-24', '水', '代々木公園', 'またしても…', 'rain-happy.gif', 22, '新歓コンパだというのにまたしても雨…
INSERT INTO public.training VALUES ('p1YXPrsm8W', '2013-05-22', '水', '代々木公園', '最大勢力', 'sun-normal.gif', 39, 'ついに最大勢力が一年生となった…
INSERT INTO public.training VALUES ('0ylJklpbEd', '2013-05-25', '土', '代々木公園', '眠かった', 'cloud-happy.gif', 16, '山手線1周企画のあとにもかかわらず、練習に参加する人が若干名いた。
INSERT INTO public.training VALUES ('VBU9uGi7js', '2013-05-29', '水', '代々木公園', ' エントリー戦略', 'rain-normal.gif', 26, 'そろそろ10月、11月の大会のエントリー時期である。
INSERT INTO public.training VALUES ('OhxTQg7ORy', '2013-05-24', '金', '駒込', '山手線', 'sun-happy.gif', 47, '毎年恒例の山手線企画。
INSERT INTO public.training VALUES ('pklYq7B2NP', '2013-06-05', '水', '代々木公園', 'ランニング集団', 'sun-normal.gif', 32, '最近、代々木公園にランニング集団が多い。
INSERT INTO public.training VALUES ('Djtr6GGOjm', '2013-06-01', '土', '代々木公園', '結婚式', 'sun-normal.gif', 38, 'ホノマラの方の結婚式があったために、練習前に多くの上級生が正装で現れた。おめでとうございます。');
INSERT INTO public.training VALUES ('PgUprbdxPr', '2013-06-12', '水', '代々木公園', ' 湿度', 'cloud-sad.gif', 21, '雨が降りそうで降らなかった。
INSERT INTO public.training VALUES ('5UVpbokpDw', '2013-06-09', '日', 'ヤビツ峠', '峠走', 'sun-angry.gif', 5, 'かなり高速なメンバーで峠走が行われた。
INSERT INTO public.training VALUES ('MiIaNUyiwg', '2014-06-04', '水', '代々木公園', 'トレイル', 'sun-happy.gif', 25, '忍野トレイルの後で筋肉痛に苦しんでいる人とさほどでもない人がいた。');
INSERT INTO public.training VALUES ('mM1FAHl9BE', '2013-06-15', '土', '代々木公園', '応援', 'cloud-sad.gif', 23, '待ちに待った某氏が帰ってきた。そんなに驚かなかったのは僕だけだろうか…三重からの彼にはもう誰も驚かない。');
INSERT INTO public.training VALUES ('SVjfjowRcp', '2013-06-16', '日', '夢の島陸上競技場', '夢の島12時間リレー', 'rain-sad.gif', 47, '今年も12時間リレーマラソンに参加した。
INSERT INTO public.training VALUES ('Khf7MjjUT2', '2013-06-19', '水', '代々木公園', '降ら…なーい！', 'cloud-sad.gif', 29, '降りそうで、降らない。降ってなかったのに、降る。
INSERT INTO public.training VALUES ('5OqBytIu32', '2013-06-22', '土', '代々木公園', '平和な日', 'sun-normal.gif', 35, 'めぼしい記憶がない…きっと平和だったのだろう。');
INSERT INTO public.training VALUES ('58clXME9p8', '2013-06-26', '水', '代々木公園', 'ただの雨', 'cloud-sad.gif', 8, '雨。以上。');
INSERT INTO public.training VALUES ('ePxqxw8AwU', '2013-06-29', '土', '代々木公園', '17時', 'sun-happy.gif', 36, '皆知っているのか微妙なところであるが、練習の集合時間は本来は17時であり、なし崩し的に17時15分に集まってロッカーに移動しているのが現状である。
INSERT INTO public.training VALUES ('FZPYH6Wnt6', '2013-07-03', '水', '代々木公園', 'むしむし', 'cloud-normal.gif', 23, '湿度は髪の敵！');
INSERT INTO public.training VALUES ('eXBCBBJmki', '2013-07-07', '日', '皇居', 'もう無理', 'sun-angry.gif', 20, '暑い、暑すぎる。
INSERT INTO public.training VALUES ('JyVQ9KHjip', '2013-07-06', '土', '代々木公園', 'あづい', 'sun-angry.gif', 30, 'ついに30℃を越えてきた気温。
INSERT INTO public.training VALUES ('03SOYEyGjj', '2013-07-10', '水', '代々木公園', '久々に', 'sun-normal.gif', 28, '久々にホ完の方々が来て下さった。
INSERT INTO public.training VALUES ('1CCCvuYu1r', '2013-07-17', '水', '代々木公園', '少なめ', 'rain-sad.gif', 14, 'テストと雨のせいかいつもより人が少なかった。
INSERT INTO public.training VALUES ('qilY5IpItk', '2013-07-13', '土', '代々木公園', '行列', 'cloud-sad.gif', 34, 'いつものコースを横断するほどの行列が出来ていた。
INSERT INTO public.training VALUES ('6q5zhtHl2L', '2013-07-24', '水', '代々木公園', '紅一点', 'rain-normal.gif', 6, 'テスト＋雨のせいか久々の人の少なさを誇った。
INSERT INTO public.training VALUES ('d45jRibVW2', '2013-07-20', '土', '代々木公園', 'けしからん', 'sun-normal.gif', 27, '神聖なる練習に出ないで花火大会に行く輩と練習に顔だけ出して花火大会にいくけしからん輩がいた。
INSERT INTO public.training VALUES ('vnzqfyqKzT', '2013-07-31', '水', '代々木公園', '久しぶり', 'sun-normal.gif', 33, 'テストが終わり始め久々に練習に来る人が結構いた。');
INSERT INTO public.training VALUES ('5mbXuOvTAU', '2013-08-03', '土', '代々木公園', '愛の力', 'sun-happy.gif', 30, '久しぶりに見る一年生が結構いた。
INSERT INTO public.training VALUES ('1bN7RmyEqJ', '2013-12-29', '日', '皇居', '走り納め', 'sun-happy.gif', 24, '走り納めにたくさんのホノマラーが皇居をぐるぐるした。
INSERT INTO public.training VALUES ('12nMuUMM6v', '2013-08-07', '水', '代々木公園', '合宿前', 'sun-normal.gif', 25, '合宿前日という事で盛り上がったのか、気温もとても上がっていた。
INSERT INTO public.training VALUES ('iwSC9Lrzcv', '2013-08-21', '水', '代々木公園', 'ゲリラ豪雨', 'cloud-sad.gif', 18, '代々木に来るまではゲリラ豪雨に見舞われた人も多かったようだが、ついてみれば雨には降られなかった。
INSERT INTO public.training VALUES ('K8Nweiw1hK', '2013-08-24', '土', '代々木公園', 'やっぱり少なめ', 'cloud-normal.gif', 18, 'マラソン大会や帰省ということもあり、20人弱の回が続いている。
INSERT INTO public.training VALUES ('w80iVttxfz', '2013-08-28', '水', '代々木公園', 'たふ', 'sun-normal.gif', 29, '午前に北海道から帰ってきた人とか帰省前に来る人とか下界に降り立ったスカイランナーとかタフな人が多かった。');
INSERT INTO public.training VALUES ('TIe6OyicLT', '2013-08-31', '土', '代々木公園', '浦佐', 'sun-happy.gif', 18, '浦佐の裏の練習で人が少ないと予想していたが、意外に集まった模様。数日前まで涼しかったのに気候が夏に逆戻りしていた。');
INSERT INTO public.training VALUES ('7PeyMqSMhm', '2013-09-04', '水', '代々木公園', '学年会×2', 'cloud-sad.gif', 35, '一年生理系の期末が終わり、二つの学年会が被った事でにぎやかであった。');
INSERT INTO public.training VALUES ('4PygDSVfwj', '2013-09-07', '土', '代々木公園', '長距離練', 'sun-happy.gif', 36, '50km超の長距離練を終えてから練習に合流する猛者がたくさんいた。
INSERT INTO public.training VALUES ('XjZ1N6W3CN', '2013-08-17', '土', '代々木公園', '少なめ？', 'sun-normal.gif', 17, '帰省する人も出てきて、少し少なめ？
INSERT INTO public.training VALUES ('BvrLUQBkq0', '2013-09-14', '土', '代々木公園', '秋の予感', 'cloud-normal.gif', 36, 'そろそろ日も短くなり、それほど暑さも感じなくなり始めた………ような気がした。
INSERT INTO public.training VALUES ('uYBNAxuVwY', '2013-09-11', '水', '代々木公園', '準備', 'sun-normal.gif', 27, '10月から始まる大会シーズンに備えて、フライングや通常練で思い思いにしっかり練習する人が増えてきた。
INSERT INTO public.training VALUES ('d6tGc8KYyl', '2013-09-18', '水', '代々木公園', '改修', 'sun-normal.gif', 17, '織田が改修のためロッカーが使えなかった。
INSERT INTO public.training VALUES ('Z0QNZ3001S', '2013-09-21', '土', '代々木公園', '最大勢力', 'sun-happy.gif', 27, '最大勢力が一年生であった。これはあくまで二年生が秋田に大勢参加する気合いのある学年であるという証明にすぎない。
INSERT INTO public.training VALUES ('b5NCWLTvHf', '2013-09-25', '水', '代々木公園', '雨ニモ負ケズ', 'rain-sad.gif', 12, '朝から降ったり止んだりの微妙な天気だったがホノマラーは集まった。
INSERT INTO public.training VALUES ('clnU0FU4c1', '2013-09-28', '土', '代々木公園', '波', 'cloud-normal.gif', 44, '来週からは一時間早まるため、土曜日のこの時間では最後の練習となった。
INSERT INTO public.training VALUES ('OTtQFGdIbU', '2013-10-02', '水', '代々木公園', 'ロッカー', 'cloud-normal.gif', 25, 'ロッカーが使えず、大変不便な思いをした練習であった。そしてこの時期にも関わらず蚊がいた。
INSERT INTO public.training VALUES ('zcI5W3z3kl', '2013-10-05', '土', '代々木公園', '意外と', 'rain-sad.gif', 26, '雨が比較的に強いにも関わらず人が集まった。
INSERT INTO public.training VALUES ('HSBOfui6HF', '2013-10-09', '水', '代々木公園', '新学期', 'sun-normal.gif', 27, '新学期の始まりと秋を感じる練習だった。
INSERT INTO public.training VALUES ('kbqwwTtyXF', '2013-10-12', '土', '代々木公園', 'だいがわり', 'sun-happy.gif', 36, '早くも代がかわりました
INSERT INTO public.training VALUES ('Ukt94JMpxV', '2013-10-14', '月', '皇居', '体育の日', 'sun-happy.gif', 18, '体育の日だからという訳ではないが、午前からランナーで賑わう皇居へ。
INSERT INTO public.training VALUES ('no2tWKXosH', '2013-10-16', '水', '代々木公園', '台風', 'sun-normal.gif', 19, '台風が過ぎ去った。落ち葉がたくさん落ちていて昨日の風の強さを物語っていた。');
INSERT INTO public.training VALUES ('0zoGTM1of6', '2013-10-19', '土', '代々木公園', '文の京', 'cloud-normal.gif', 22, '一年生が少なくて寂しかった');
INSERT INTO public.training VALUES ('1A1YIAGxSO', '2013-10-20', '日', '文京区', '「文の京」12時間リレーマラソン', 'rain-sad.gif', 10, '1年生チームで「文の京」12時間リレーマラソンに出場した。
