INSERT INTO public.training VALUES ('Vg95pQysSF', '2016-03-05', '月', '代々木公園', '増加', 'sun-normal.gif', 16, 'だんだんと日が長くなるにつれ、代々木公園を訪れる人が増えて来て走りづらさを感じた練習でした。');
